import React, { useState, useEffect, useCallback } from 'react';
import { collection, addDoc, getDocs, updateDoc, deleteDoc, doc, query, where, setDoc, serverTimestamp } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from '../firebase';
import { useAuth } from '../contexts/AuthContext';
import Header from './Header';
import { addAllExercises } from '../services/exerciseService';
import { useNavigate } from 'react-router-dom';
import RoutinesNew from './RoutinesNew';
import { createUserWithEmailAndPassword, signOut, signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase';
import { FaUserPlus, FaDumbbell, FaShoppingBag, FaAppleAlt, FaClipboardList, FaCrown } from 'react-icons/fa';
import { GiWeightLiftingUp } from 'react-icons/gi';
import Particles from 'react-tsparticles';
import { loadSlim } from 'tsparticles-slim';
import ParticlesBackground from './ParticlesBackground';
import '../styles/global.css';

export default function AdminPage() {
  const [section, setSection] = useState('main');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [personalTrainers, setPersonalTrainers] = useState([]);
  const { register, currentUser } = useAuth();

  // Estados para nutrición
  const [nutritionForm, setNutritionForm] = useState({
    name: '',
    description: ''
  });

  // Estados para shop
  const [shopForm, setShopForm] = useState({
    name: '',
    description: '',
    price: '',
    stock: '',
    imageUrl: ''
  });

  // Estados para ejercicios
  const [exerciseForm, setExerciseForm] = useState({
    name: '',
    categoryId: '',
    description: '',
    difficulty: 'Principiante',
    imageUrl: ''
  });
  const [categories, setCategories] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [editExerciseId, setEditExerciseId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [exercisePage, setExercisePage] = useState(1);
  const exercisesPerPage = 10;

  const [users, setUsers] = useState([]);
  const [routines, setRoutines] = useState([]);
  const [products, setProducts] = useState([]);
  const [showRoutineModal, setShowRoutineModal] = useState(false);
  const [userRoutine, setUserRoutine] = useState(null);
  const [selectedUser, setSelectedUser] = useState(null);

  const loadPersonalTrainers = useCallback(async () => {
    try {
      const q = query(collection(db, 'users'), where('role', '==', 2));
      const querySnapshot = await getDocs(q);
      const trainers = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setPersonalTrainers(trainers);
    } catch (error) {
      console.error('Error al cargar personal trainers:', error);
    }
  }, []);

  useEffect(() => {
    if (section === 'pt') {
      loadPersonalTrainers();
    }
  }, [section, loadPersonalTrainers]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      setLoading(true);
      await register(email, password, name, 2);
      setSuccess('Personal Trainer creado exitosamente');
      setEmail('');
      setPassword('');
      setName('');
      loadPersonalTrainers();
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleNutritionChange = (e) => {
    setNutritionForm({
      ...nutritionForm,
      [e.target.name]: e.target.value
    });
  };

  const handleAddNutrition = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'nutrition'), {
        ...nutritionForm,
        createdAt: new Date().toISOString()
      });
      setNutritionForm({ name: '', description: '' });
      alert('Plan nutricional agregado correctamente');
    } catch (error) {
      console.error('Error al agregar plan nutricional:', error);
      alert('Error al agregar plan nutricional');
    }
  };

  const handleShopChange = (e) => {
    setShopForm({
      ...shopForm,
      [e.target.name]: e.target.value
    });
  };

  const handleAddShop = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'shop'), {
        ...shopForm,
        createdAt: new Date().toISOString()
      });
      setShopForm({ name: '', description: '', price: '', stock: '', imageUrl: '' });
      alert('Producto agregado correctamente');
    } catch (error) {
      console.error('Error al agregar producto:', error);
      alert('Error al agregar producto');
    }
  };

  const handleExerciseChange = (e) => {
    setExerciseForm({
      ...exerciseForm,
      [e.target.name]: e.target.value
    });
  };

  const loadCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'categories'));
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categorías:', error);
    }
  };

  const loadExercises = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'exercises'));
      const exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setExercises(exercisesList);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
    }
  };

  useEffect(() => {
    if (section === 'exercises') {
      loadCategories();
      loadExercises();
    }
  }, [section]);

  const handleAddExercise = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      if (editExerciseId) {
        await updateDoc(doc(db, 'exercises', editExerciseId), {
          ...exerciseForm,
          updatedAt: new Date().toISOString()
        });
        alert('Ejercicio actualizado correctamente');
        setEditExerciseId(null);
      } else {
        await addDoc(collection(db, 'exercises'), {
          ...exerciseForm,
          createdAt: new Date().toISOString()
        });
        alert('Ejercicio agregado correctamente');
      }
      setExerciseForm({
        name: '',
        categoryId: '',
        description: '',
        difficulty: 'Principiante',
        imageUrl: ''
      });
      loadExercises();
    } catch (error) {
      console.error('Error al guardar ejercicio:', error);
      alert('Error al guardar ejercicio');
    } finally {
      setIsLoading(false);
    }
  };

  const totalExercisePages = Math.ceil(exercises.length / exercisesPerPage);
  const paginatedExercises = exercises.slice(
    (exercisePage - 1) * exercisesPerPage,
    exercisePage * exercisesPerPage
  );

  const particlesInit = useCallback(async (engine) => {
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback((container) => {
    // Solo loguear en desarrollo
    if (process.env.NODE_ENV === 'development') {
      console.log('Particles container loaded:', container);
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch users
        const usersRef = collection(db, 'users');
        const usersSnapshot = await getDocs(usersRef);
        const usersList = usersSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setUsers(usersList);

        // Fetch routines
        const routinesRef = collection(db, 'routines');
        const routinesSnapshot = await getDocs(routinesRef);
        const routinesList = routinesSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setRoutines(routinesList);

        // Fetch products
        const productsRef = collection(db, 'products');
        const productsSnapshot = await getDocs(productsRef);
        const productsList = productsSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setProducts(productsList);

        // Fetch user routine if currentUser exists
        if (currentUser) {
          const userRoutineQuery = query(routinesRef, where('userId', '==', currentUser.uid));
          const userRoutineSnapshot = await getDocs(userRoutineQuery);
          if (!userRoutineSnapshot.empty) {
            setUserRoutine(userRoutineSnapshot.docs[0].data());
          }
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [currentUser]);

  return (
    <div className="min-h-screen text-white p-4 pb-24 neon-luxury">
      <div className="neon-glow-luxury"></div>
      <ParticlesBackground />
      <Header />
      <div className="max-w-6xl mx-auto p-6">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold flex items-center">
            <GiWeightLiftingUp className="mr-2 text-yellow-400" />
            <span className="text-gradient-gold">Panel de Administración</span>
          </h1>
          <div className="badge-premium flex items-center">
            <FaCrown className="text-yellow-400 mr-1" />
            <span className="text-xs text-yellow-300">ADMIN</span>
          </div>
        </div>

        {section === 'main' && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <button 
              onClick={() => setSection('pt')} 
              className="neon-panel-luxury p-6 rounded-lg hover-scale"
            >
              <FaUserPlus className="text-2xl text-yellow-400 mb-2" />
              <h2 className="text-xl font-bold text-gradient-gold mb-2">Personal Trainer</h2>
              <p className="text-gray-400">Gestionar personal trainers y sus cuentas</p>
            </button>

            <button 
              onClick={() => setSection('nutrition')} 
              className="neon-panel-luxury p-6 rounded-lg hover-scale"
            >
              <FaAppleAlt className="text-2xl text-yellow-400 mb-2" />
              <h2 className="text-xl font-bold text-gradient-gold mb-2">Nutrición</h2>
              <p className="text-gray-400">Gestionar planes nutricionales</p>
            </button>

            <button 
              onClick={() => setSection('routines')} 
              className="neon-panel-luxury p-6 rounded-lg hover-scale"
            >
              <FaClipboardList className="text-2xl text-yellow-400 mb-2" />
              <h2 className="text-xl font-bold text-gradient-gold mb-2">Rutinas</h2>
              <p className="text-gray-400">Gestionar rutinas de ejercicios</p>
            </button>

            <button 
              onClick={() => setSection('shop')} 
              className="neon-panel-luxury p-6 rounded-lg hover-scale"
            >
              <FaShoppingBag className="text-2xl text-yellow-400 mb-2" />
              <h2 className="text-xl font-bold text-gradient-gold mb-2">Shop</h2>
              <p className="text-gray-400">Gestionar productos y servicios</p>
            </button>

            <button 
              onClick={() => setSection('exercises')} 
              className="neon-panel-luxury p-6 rounded-lg hover-scale"
            >
              <FaDumbbell className="text-2xl text-yellow-400 mb-2" />
              <h2 className="text-xl font-bold text-gradient-gold mb-2">Ejercicios</h2>
              <p className="text-gray-400">Gestionar ejercicios y categorías</p>
            </button>
          </div>
        )}

        {section === 'pt' && (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="neon-panel-luxury p-6 rounded-lg">
              <h2 className="text-2xl font-bold text-gradient-gold mb-6 flex items-center">
                <FaUserPlus className="mr-2" />
                Crear Personal Trainer
              </h2>
              {error && <div className="bg-red-500/20 text-red-300 p-3 rounded mb-4 border border-red-500/30">{error}</div>}
              {success && <div className="bg-green-500/20 text-green-300 p-3 rounded mb-4 border border-green-500/30">{success}</div>}
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-yellow-400 mb-2">Nombre</label>
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="neon-input-luxury w-full p-2 rounded"
                    required
                  />
                </div>
                <div>
                  <label className="block text-yellow-400 mb-2">Email</label>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="neon-input-luxury w-full p-2 rounded"
                    required
                  />
                </div>
                <div>
                  <label className="block text-yellow-400 mb-2">Contraseña</label>
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="neon-input-luxury w-full p-2 rounded"
                    required
                  />
                </div>
                <button
                  type="submit"
                  disabled={loading}
                  className="neon-btn-add-luxury w-full py-2 px-4 rounded font-bold disabled:opacity-50"
                >
                  {loading ? 'Creando...' : 'Crear Personal Trainer'}
                </button>
              </form>
            </div>

            <div className="neon-panel-luxury p-6 rounded-lg">
              <h2 className="text-2xl font-bold text-gradient-gold mb-6 flex items-center">
                <FaUserPlus className="mr-2" />
                Personal Trainers Registrados
              </h2>
              {personalTrainers.length === 0 ? (
                <p className="text-gray-400">No hay personal trainers registrados</p>
              ) : (
                <div className="space-y-4">
                  {personalTrainers.map((trainer) => (
                    <div key={trainer.id} className="neon-exercise-card-luxury p-4 rounded-lg">
                      <h3 className="text-lg font-semibold text-gradient-gold">{trainer.name}</h3>
                      <p className="text-gray-300">{trainer.email}</p>
                      <p className="text-sm text-gray-400">
                        Registrado: {trainer.createdAt?.toDate().toLocaleDateString()}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </div>
            <button 
              onClick={() => setSection('main')} 
              className="col-span-2 mt-4 neon-btn-add-luxury py-2 px-4 rounded font-bold"
            >
              Volver al menú principal
            </button>
          </div>
        )}

        {section === 'nutrition' && (
          <div className="neon-panel-luxury p-6 rounded-lg">
            <h2 className="text-2xl font-bold text-gradient-gold mb-6 flex items-center">
              <FaAppleAlt className="mr-2" />
              Gestión de Nutrición
            </h2>
            <form onSubmit={handleAddNutrition} className="space-y-4">
              <div>
                <label className="block text-yellow-400 mb-2">Nombre del Plan</label>
                <input
                  type="text"
                  name="name"
                  value={nutritionForm.name}
                  onChange={handleNutritionChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-yellow-400 mb-2">Descripción</label>
                <textarea
                  name="description"
                  value={nutritionForm.description}
                  onChange={handleNutritionChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <button
                type="submit"
                className="neon-btn-add-luxury w-full py-2 px-4 rounded font-bold"
              >
                Agregar Plan
              </button>
            </form>
            <button 
              onClick={() => setSection('main')} 
              className="mt-4 neon-btn-add-luxury py-2 px-4 rounded font-bold"
            >
              Volver al menú principal
            </button>
          </div>
        )}

        {section === 'routines' && (
          <div className="neon-panel-luxury p-6 rounded-lg">
            <h2 className="text-2xl font-bold text-gradient-gold mb-6 flex items-center">
              <FaClipboardList className="mr-2" />
              Gestión de Rutinas
            </h2>
            <RoutinesNew />
            <button 
              onClick={() => setSection('main')} 
              className="mt-4 neon-btn-add-luxury py-2 px-4 rounded font-bold"
            >
              Volver al menú principal
            </button>
          </div>
        )}

        {section === 'shop' && (
          <div className="neon-panel-luxury p-6 rounded-lg">
            <h2 className="text-2xl font-bold text-gradient-gold mb-6 flex items-center">
              <FaShoppingBag className="mr-2" />
              Gestión de Shop
            </h2>
            <form onSubmit={handleAddShop} className="space-y-4">
              <div>
                <label className="block text-yellow-400 mb-2">Nombre del Producto</label>
                <input
                  type="text"
                  name="name"
                  value={shopForm.name}
                  onChange={handleShopChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-yellow-400 mb-2">Descripción</label>
                <textarea
                  name="description"
                  value={shopForm.description}
                  onChange={handleShopChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-yellow-400 mb-2">Precio</label>
                <input
                  type="number"
                  name="price"
                  value={shopForm.price}
                  onChange={handleShopChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-yellow-400 mb-2">Stock</label>
                <input
                  type="number"
                  name="stock"
                  value={shopForm.stock}
                  onChange={handleShopChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-yellow-400 mb-2">URL de Imagen (opcional)</label>
                <input
                  type="text"
                  name="imageUrl"
                  value={shopForm.imageUrl}
                  onChange={handleShopChange}
                  className="neon-input-luxury w-full p-2 rounded"
                />
              </div>
              <button
                type="submit"
                className="neon-btn-add-luxury w-full py-2 px-4 rounded font-bold"
              >
                Agregar Producto
              </button>
            </form>
            <button 
              onClick={() => setSection('main')} 
              className="mt-4 neon-btn-add-luxury py-2 px-4 rounded font-bold"
            >
              Volver al menú principal
            </button>
          </div>
        )}

        {section === 'exercises' && (
          <div className="neon-panel-luxury p-6 rounded-lg">
            <h2 className="text-2xl font-bold text-gradient-gold mb-6 flex items-center">
              <FaDumbbell className="mr-2" />
              Gestión de Ejercicios
            </h2>
            <form onSubmit={handleAddExercise} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-yellow-400 mb-2">Nombre del Ejercicio</label>
                  <input
                    type="text"
                    name="name"
                    value={exerciseForm.name}
                    onChange={handleExerciseChange}
                    className="neon-input-luxury w-full p-2 rounded"
                    required
                  />
                </div>
                <div>
                  <label className="block text-yellow-400 mb-2">Categoría</label>
                  <select
                    name="categoryId"
                    value={exerciseForm.categoryId}
                    onChange={handleExerciseChange}
                    className="neon-input-luxury w-full p-2 rounded"
                    required
                  >
                    <option value="">Seleccionar categoría</option>
                    {categories.map(cat => (
                      <option key={cat.id} value={cat.id}>{cat.name}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-yellow-400 mb-2">Dificultad</label>
                  <select
                    name="difficulty"
                    value={exerciseForm.difficulty}
                    onChange={handleExerciseChange}
                    className="neon-input-luxury w-full p-2 rounded"
                    required
                  >
                    <option value="Principiante">Principiante</option>
                    <option value="Intermedio">Intermedio</option>
                    <option value="Avanzado">Avanzado</option>
                  </select>
                </div>
                <div>
                  <label className="block text-yellow-400 mb-2">URL de Imagen (opcional)</label>
                  <input
                    type="text"
                    name="imageUrl"
                    value={exerciseForm.imageUrl}
                    onChange={handleExerciseChange}
                    className="neon-input-luxury w-full p-2 rounded"
                  />
                </div>
              </div>
              <div>
                <label className="block text-yellow-400 mb-2">Descripción</label>
                <textarea
                  name="description"
                  value={exerciseForm.description}
                  onChange={handleExerciseChange}
                  className="neon-input-luxury w-full p-2 rounded"
                  required
                />
              </div>
              <button
                type="submit"
                disabled={isLoading}
                className="neon-btn-add-luxury w-full py-2 px-4 rounded font-bold disabled:opacity-50"
              >
                {editExerciseId ? 'Guardar Cambios' : isLoading ? 'Agregando...' : 'Agregar Ejercicio'}
              </button>
            </form>

            <div className="mt-8">
              <h3 className="text-xl font-bold text-gradient-gold mb-4">Ejercicios Existentes</h3>
              <div className="space-y-4">
                {paginatedExercises.map(ex => (
                  <div key={ex.id} className="neon-exercise-card-luxury p-4 rounded-lg">
                    <div className="flex justify-between items-start">
                      <div>
                        <h4 className="text-lg font-semibold text-gradient-gold">{ex.name}</h4>
                        <p className="text-sm text-gray-300">
                          Categoría: {categories.find(c => c.id === ex.categoryId)?.name || 'Sin categoría'}
                        </p>
                        <p className="text-sm text-gray-300">Dificultad: {ex.difficulty}</p>
                        <p className="text-sm text-gray-400 mt-2">{ex.description}</p>
                      </div>
                      <button
                        onClick={() => {
                          setEditExerciseId(ex.id);
                          setExerciseForm({
                            name: ex.name,
                            categoryId: ex.categoryId,
                            difficulty: ex.difficulty,
                            description: ex.description,
                            imageUrl: ex.imageUrl || ''
                          });
                        }}
                        className="neon-btn-luxury px-3 py-1 rounded text-sm font-bold hover:bg-yellow-500 hover:text-black transition-colors"
                      >
                        Editar
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              <div className="flex justify-center items-center gap-4 mt-4">
                <button
                  disabled={exercisePage === 1}
                  onClick={() => setExercisePage(p => Math.max(1, p - 1))}
                  className="neon-btn-luxury px-4 py-2 rounded font-bold disabled:opacity-50"
                >
                  Anterior
                </button>
                <span className="text-yellow-400">
                  Página {exercisePage} de {totalExercisePages}
                </span>
                <button
                  disabled={exercisePage === totalExercisePages}
                  onClick={() => setExercisePage(p => Math.min(totalExercisePages, p + 1))}
                  className="neon-btn-luxury px-4 py-2 rounded font-bold disabled:opacity-50"
                >
                  Siguiente
                </button>
              </div>
            </div>
            <button 
              onClick={() => setSection('main')} 
              className="mt-4 neon-btn-add-luxury py-2 px-4 rounded font-bold"
            >
              Volver al menú principal
            </button>
          </div>
        )}

        {/* Mi Rutina */}
        <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm hover:shadow-neon transition-all duration-300">
          <div className="flex items-center gap-3 mb-4">
            <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
              <GiWeightLiftingUp className="text-2xl text-black" />
            </div>
            <h2 className="text-xl font-bold bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
              Mi Rutina
            </h2>
          </div>
          <div className="space-y-4">
            {userRoutine ? (
              <div className="space-y-4">
                <div className="bg-black/30 rounded-lg p-4 border border-neon-gold/20 shadow-neon-sm">
                  <h3 className="text-lg font-semibold text-neon-gold mb-2">{userRoutine.name}</h3>
                  <p className="text-gray-300 mb-3">{userRoutine.description}</p>
                  <div className="flex flex-wrap gap-2">
                    {userRoutine.tags?.map((tag, index) => (
                      <span key={index} className="px-2 py-1 bg-neon-gold/10 text-neon-gold rounded-full text-sm border border-neon-gold/20">
                        {tag}
                      </span>
                    ))}
                  </div>
                </div>
                <div className="bg-black/30 rounded-lg p-4 border border-neon-gold/20 shadow-neon-sm">
                  <h4 className="text-neon-gold font-semibold mb-3">Ejercicios</h4>
                  <div className="space-y-3">
                    {userRoutine.exercises?.map((exercise, index) => (
                      <div key={index} className="flex items-start gap-3 p-3 bg-black/20 rounded-lg border border-neon-gold/10">
                        <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center bg-neon-gold/10 rounded-full">
                          <span className="text-neon-gold font-bold">{index + 1}</span>
                        </div>
                        <div className="flex-grow">
                          <h5 className="text-white font-medium">{exercise.name}</h5>
                          <p className="text-sm text-gray-400">
                            {exercise.sets} series x {exercise.reps} repeticiones
                          </p>
                          {exercise.notes && (
                            <p className="text-sm text-neon-gold/80 mt-1">{exercise.notes}</p>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                <button
                  onClick={() => setShowRoutineModal(true)}
                  className="w-full py-2 px-4 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300 flex items-center justify-center gap-2"
                >
                  <FaClipboardList className="text-lg" />
                  Editar Rutina
                </button>
              </div>
            ) : (
              <div className="text-center py-8">
                <p className="text-gray-400 mb-4">No tienes una rutina asignada</p>
                <button
                  onClick={() => setShowRoutineModal(true)}
                  className="py-2 px-6 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300 flex items-center justify-center gap-2 mx-auto"
                >
                  <FaClipboardList className="text-lg" />
                  Crear Rutina
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {showRoutineModal && (
        <RoutinesNew
          onClose={() => setShowRoutineModal(false)}
          onSave={(routine) => {
            setUserRoutine(routine);
            setShowRoutineModal(false);
          }}
          initialRoutine={userRoutine}
        />
      )}
    </div>
  );
}
