import React, { useState, useEffect, useRef } from 'react';
import { FaTrash, FaPlay, FaPause, FaFire, FaDumbbell, FaCrown, FaBolt } from 'react-icons/fa';
import { GiWeightLiftingUp, GiDiamondTrophy } from 'react-icons/gi';

// Efectos de partículas para destellos (opcional)
import Particles from 'react-particles-js';

const DAYS = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

// --- Estilos actualizados con efectos premium ---
const styles = `
  .neon-luxury {
    position: relative;
    overflow-x: hidden;
    background: radial-gradient(ellipse at center, #0a0a0a 0%, #000000 100%);
  }

  .neon-glow-luxury {
    position: fixed;
    top: -10px;
    left: -10px;
    right: -10px;
    bottom: -10px;
    border: 2px solid rgba(255, 215, 0, 0.3);
    border-radius: 20px;
    animation: neon-pulse 3s infinite alternate, neon-flicker 5s infinite;
    pointer-events: none;
    z-index: 0;
  }

  .neon-btn-luxury {
    background: linear-gradient(to right, #1a1a1a, #2a2a2a);
    border: 1px solid rgba(255, 215, 0, 0.3);
    box-shadow: 0 0 8px rgba(255, 215, 0, 0.2);
    transition: all 0.4s;
  }

  .neon-btn-luxury:hover {
    box-shadow: 0 0 15px rgba(255, 215, 0, 0.6);
    transform: translateY(-2px);
  }

  .neon-btn-active-luxury {
    background: linear-gradient(to right, #ffd700, #ffcc00);
    color: #000 !important;
    font-weight: bold;
    animation: gold-pulse 1.5s infinite alternate, gold-sparkle 4s infinite;
    box-shadow: 0 0 20px rgba(255, 215, 0, 0.7);
  }

  .neon-panel-luxury {
    background: rgba(20, 20, 20, 0.8);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 215, 0, 0.4);
    box-shadow: 0 0 30px rgba(255, 215, 0, 0.2);
  }

  .neon-exercise-card-luxury {
    background: linear-gradient(to bottom, #1e1e1e, #171717);
    border: 1px solid rgba(255, 215, 0, 0.2);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.5);
    transition: all 0.4s;
  }

  .neon-exercise-card-luxury:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(255, 215, 0, 0.3);
    border: 1px solid rgba(255, 215, 0, 0.5);
  }

  .neon-set-card-luxury {
    background: rgba(30, 30, 30, 0.7);
    border: 1px solid rgba(255, 215, 0, 0.1);
  }

  .neon-btn-add-luxury {
    background: linear-gradient(to right, #ffd700, #ffcc00);
    color: #000;
    font-weight: bold;
    box-shadow: 0 0 20px rgba(255, 215, 0, 0.7);
    animation: gold-pulse 2s infinite;
  }

  .timer-active-luxury {
    animation: neon-blink 1s infinite, gold-sparkle 3s infinite;
  }

  /* Animaciones premium */
  @keyframes neon-pulse {
    0% { box-shadow: 0 0 10px rgba(255, 215, 0, 0.3); }
    100% { box-shadow: 0 0 30px rgba(255, 215, 0, 0.7); }
  }

  @keyframes neon-flicker {
    0%, 19%, 21%, 23%, 25%, 54%, 56%, 100% { opacity: 1; }
    20%, 22%, 24%, 55% { opacity: 0.7; }
  }

  @keyframes gold-pulse {
    0% { box-shadow: 0 0 15px rgba(255, 215, 0, 0.7); }
    100% { box-shadow: 0 0 25px rgba(255, 215, 0, 0.9); }
  }

  @keyframes gold-sparkle {
    0% { filter: brightness(100%); }
    50% { filter: brightness(130%); }
    100% { filter: brightness(100%); }
  }

  @keyframes neon-blink {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
  }
`;

function TrainingPage() {
  // ... (mantén el mismo estado y lógica del código anterior)

  return (
    <div className="min-h-screen text-white p-4 pb-24 max-w-md mx-auto neon-luxury">
      {/* Efecto de borde neón premium */}
      <div className="neon-glow-luxury"></div>

      {/* Efecto de partículas (opcional) */}
      <Particles
        params={{
          particles: {
            number: { value: 20 },
            size: { value: 2, random: true },
            color: { value: "#ffd700" },
            line_linked: { enable: false },
            move: {
              enable: true,
              speed: 1,
              direction: "none",
              random: true,
              straight: false,
              out_mode: "out"
            }
          }
        }}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
          opacity: 0.3
        }}
      />

      {/* Header con toque lujoso */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-yellow-400 flex items-center">
          <GiDiamondTrophy className="mr-2 text-yellow-300" />
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-200">
            POWER TRAINING
          </span>
        </h1>
        <div className="flex items-center bg-black/50 px-3 py-1 rounded-full border border-yellow-400/30">
          <FaCrown className="text-yellow-400 mr-1" />
          <span className="text-xs text-yellow-300">PREMIUM</span>
        </div>
      </div>

      {/* Selector de días con oro */}
      <div className="flex justify-between mb-6 overflow-x-auto py-2 scrollbar-hide">
        {DAYS.map((d, i) => (
          <button
            key={d}
            onClick={() => handleDayChange(i)}
            className={`flex-1 min-w-[40px] mx-1 py-2 rounded-lg text-sm font-medium transition-all duration-300 flex flex-col items-center ${
              today === i 
                ? 'neon-btn-active-luxury'
                : 'neon-btn-luxury text-gray-300'
            }`}
          >
            <span>{d.slice(0,3)}</span>
            {today === i && <FaBolt className="text-xs mt-1 text-black animate-bounce" />}
          </button>
        ))}
      </div>

      {/* Panel de agregar ejercicio */}
      <div className="neon-panel-luxury rounded-xl p-4 mb-6">
        <h2 className="text-xl font-bold mb-4 flex items-center">
          <GiWeightLiftingUp className="mr-2 text-yellow-400" />
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-200">
            NUEVO EJERCICIO
          </span>
        </h2>

        {/* ... (mantén los mismos inputs del código anterior) */}

        <button
          onClick={handleAddExercise}
          disabled={!selectedExercise}
          className="neon-btn-add-luxury mt-4 px-4 py-3 rounded-lg font-bold transition-all transform hover:scale-105 active:scale-95 flex items-center justify-center w-full"
        >
          <FaFire className="mr-2 animate-pulse" />
          AGREGAR SET
        </button>
      </div>

      {/* Lista de ejercicios */}
      <div className="space-y-4">
        {(routine[DAYS[today]] || []).map((ex, exIdx) => (
          <div key={exIdx} className="neon-exercise-card-luxury p-4 rounded-xl">
            <div className="flex justify-between items-center mb-3">
              <h3 className="font-bold text-lg flex items-center">
                <FaDumbbell className="mr-2 text-yellow-400" />
                <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-200">
                  {ex.name}
                </span>
              </h3>
              <button 
                onClick={() => handleRemoveExercise(exIdx)}
                className="text-red-400 hover:text-red-300 p-2 rounded-full hover:bg-red-900/30 transition-colors"
              >
                <FaTrash />
              </button>
            </div>

            <div className="space-y-3">
              {ex.series.map((set, setIdx) => {
                const key = `${exIdx}-${setIdx}`;
                const timerState = timers[key] || { running: false, timeLeft: set.timer };

                return (
                  <div key={setIdx} className="neon-set-card-luxury p-3 rounded-lg">
                    {/* ... (mantén los mismos inputs del código anterior) */}

                    {/* Timer con efecto de destello */}
                    <button
                      type="button"
                      className={`p-2 rounded-full ml-2 ${
                        timerState.running 
                          ? 'timer-active-luxury bg-yellow-400 text-black'
                          : 'bg-gray-700 text-yellow-400'
                      }`}
                      onClick={() => timerState.running
                        ? pauseTimer(exIdx, setIdx)
                        : startTimer(exIdx, setIdx, Number(set.timer) || 0)}
                    >
                      {timerState.running ? <FaPause /> : <FaPlay />}
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>

      {/* Estilos CSS */}
      <style>{styles}</style>
    </div>
  );
}
