import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { db } from '../firebase';
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';

const RoutineForm = () => {
    const { currentUser } = useAuth();
    const [routineName, setRoutineName] = useState('');
    const [exercises, setExercises] = useState([{ name: '', sets: '', reps: '' }]);
    const [loading, setLoading] = useState(false);

    const addExercise = () => {
        setExercises([...exercises, { name: '', sets: '', reps: '' }]);
    };

    const updateExercise = (index, field, value) => {
        const newExercises = [...exercises];
        newExercises[index][field] = value;
        setExercises(newExercises);
    };

    const removeExercise = (index) => {
        const newExercises = exercises.filter((_, i) => i !== index);
        setExercises(newExercises);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!currentUser) return;

        try {
            setLoading(true);
            const routineData = {
                userId: currentUser.uid,
                name: routineName,
                exercises: exercises,
                createdAt: serverTimestamp(),
                isActive: true
            };

            await addDoc(collection(db, 'routines'), routineData);
            setRoutineName('');
            setExercises([{ name: '', sets: '', reps: '' }]);
            alert('Rutina creada exitosamente');
        } catch (error) {
            console.error('Error al crear la rutina:', error);
            alert('Error al crear la rutina');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-4">
            <h2 className="text-2xl font-bold text-yellow-400 mb-4">Crear Nueva Rutina</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-white mb-2">Nombre de la Rutina</label>
                    <input
                        type="text"
                        value={routineName}
                        onChange={(e) => setRoutineName(e.target.value)}
                        className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700"
                        required
                    />
                </div>

                <div className="space-y-4">
                    <label className="block text-white mb-2">Ejercicios</label>
                    {exercises.map((exercise, index) => (
                        <div key={index} className="flex gap-2 items-center">
                            <input
                                type="text"
                                placeholder="Nombre del ejercicio"
                                value={exercise.name}
                                onChange={(e) => updateExercise(index, 'name', e.target.value)}
                                className="flex-1 p-2 rounded bg-gray-800 text-white border border-gray-700"
                                required
                            />
                            <input
                                type="number"
                                placeholder="Series"
                                value={exercise.sets}
                                onChange={(e) => updateExercise(index, 'sets', e.target.value)}
                                className="w-20 p-2 rounded bg-gray-800 text-white border border-gray-700"
                                required
                            />
                            <input
                                type="number"
                                placeholder="Reps"
                                value={exercise.reps}
                                onChange={(e) => updateExercise(index, 'reps', e.target.value)}
                                className="w-20 p-2 rounded bg-gray-800 text-white border border-gray-700"
                                required
                            />
                            <button
                                type="button"
                                onClick={() => removeExercise(index)}
                                className="p-2 text-red-500 hover:text-red-700"
                            >
                                Eliminar
                            </button>
                        </div>
                    ))}
                    <button
                        type="button"
                        onClick={addExercise}
                        className="text-yellow-400 hover:text-yellow-500"
                    >
                        + Agregar Ejercicio
                    </button>
                </div>

                <button
                    type="submit"
                    disabled={loading}
                    className="w-full bg-yellow-500 text-black font-bold py-2 px-4 rounded hover:bg-yellow-600 disabled:opacity-50"
                >
                    {loading ? 'Creando...' : 'Crear Rutina'}
                </button>
            </form>
        </div>
    );
};

export default RoutineForm;
