// --- Componentes de la Interfaz ---

const HomePage = () => {
    const [quote, setQuote] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Seleccionar una frase aleatoria al cargar
        const randomQuote = QUOTES[Math.floor(Math.random() * QUOTES.length)];
        setQuote(randomQuote);

        // Función para reproducir el audio
        const speakQuote = () => {
            if ('speechSynthesis' in window) {
                const voices = window.speechSynthesis.getVoices();
                // Buscar una voz femenina en español
                const femaleSpanishVoice = voices.find(voice => 
                    (voice.lang.includes('es') || voice.name.includes('Spanish') || voice.name.includes('Español')) &&
                    voice.name.toLowerCase().includes('female')
                ) || voices.find(voice => 
                    voice.lang.includes('es') || 
                    voice.name.includes('Spanish') || 
                    voice.name.includes('Español')
                );

                const utterance = new SpeechSynthesisUtterance(randomQuote);
                utterance.lang = 'es-ES';
                if (femaleSpanishVoice) utterance.voice = femaleSpanishVoice;
                
                // Configuración para una voz más sensual
                utterance.rate = 0.7; // Velocidad más lenta para un tono más sensual
                utterance.pitch = 1.2; // Tono ligeramente más alto para sonar más femenino
                utterance.volume = 0.9; // Volumen ligeramente más bajo para un tono más suave

                // Agregar pausas naturales para un ritmo más sensual
                utterance.text = randomQuote.replace(/\./g, '. ').replace(/,/g, ', ');

                utterance.onend = () => {
                    const currentUser = auth.currentUser;
                    if (currentUser) {
                        navigate('/training');
                    } else {
                        navigate('/login');
                    }
                };

                window.speechSynthesis.speak(utterance);
            }
        };

        // Reproducir inmediatamente si las voces están disponibles
        if (window.speechSynthesis.getVoices().length > 0) {
            speakQuote();
        } else {
            window.speechSynthesis.onvoiceschanged = speakQuote;
        }

        return () => {
            window.speechSynthesis.cancel();
        };
    }, [navigate]);

    return (
        <div className="min-h-screen bg-black flex flex-col items-center justify-center p-4">
            <div className="text-center">
                <img 
                    src="/logo.png" 
                    alt="SementalFit Logo" 
                    className="w-48 h-48 mx-auto mb-8"
                />
                <div className="max-w-2xl mx-auto">
                    <p className="text-2xl text-yellow-400 mb-4 font-semibold">{quote}</p>
                </div>
            </div>
        </div>
    );
};

// Función para obtener el día actual en español
const getCurrentDay = () => {
    const days = ['Domingo', 'Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado'];
    return days[new Date().getDay()];
};

const TrainingPage = () => {
    const [selectedDay, setSelectedDay] = useState(getCurrentDay());
    const [selectedCategory, setSelectedCategory] = useState('todos');
    const [exercises, setExercises] = useState([]);
    const [userRoutine, setUserRoutine] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [user, setUser] = useState(null);
    const [profile, setProfile] = useState(null);
    const [reps, setReps] = useState(12);
    const [weight, setWeight] = useState(0);
    const [series, setSeries] = useState(3);
    const [selectedExercise, setSelectedExercise] = useState(null);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            setUser(user);
            if (user) {
                loadExercises();
                loadUserRoutine(user.uid);
                // Establecer el día actual al cargar la página
                setSelectedDay(getCurrentDay());
            }
        });

        return () => unsubscribe();
    }, []);

    const loadExercises = async () => {
        try {
            const exercisesData = await getAllExercises();
            setExercises(exercisesData);
        } catch (error) {
            console.error('Error al cargar ejercicios:', error);
            setError('Error al cargar ejercicios');
        }
    };

    const loadUserRoutine = async (userId) => {
        try {
            const routine = await getUserRoutine(userId);
            setUserRoutine(routine || {});
        } catch (error) {
            console.error('Error al cargar rutina:', error);
            setError('Error al cargar rutina');
        } finally {
            setLoading(false);
        }
    };

    const addExerciseToRoutine = async (exercise) => {
        try {
            if (!selectedDay) {
                setError('Por favor, selecciona un día primero');
                return;
            }

            if (!user) {
                setError('Debes iniciar sesión para agregar ejercicios');
                return;
            }

            const newRoutine = { ...userRoutine };
            if (!newRoutine[selectedDay]) {
                newRoutine[selectedDay] = [];
            }

            // Verificar si el ejercicio ya existe en el día seleccionado
            const exerciseExists = newRoutine[selectedDay].some(
                ex => ex.id === exercise.id
            );

            if (exerciseExists) {
                setError('Este ejercicio ya está en tu rutina para este día');
                return;
            }

            // Crear el array de series basado en el número seleccionado
            const seriesArray = Array(series).fill().map(() => ({
                reps: reps,
                weight: weight,
                completed: false
            }));

            // Agregar el ejercicio con las series configuradas
            newRoutine[selectedDay].push({
                ...exercise,
                series: seriesArray
            });

            setUserRoutine(newRoutine);
            await saveUserRoutine(user.uid, newRoutine);
            setSelectedExercise(null);
            setError(null);
        } catch (error) {
            console.error('Error al agregar ejercicio:', error);
            setError('Error al agregar ejercicio a la rutina');
        }
    };

    const removeExerciseFromRoutine = async (exerciseId, day) => {
        try {
            if (!user) {
                setError('Debes iniciar sesión para eliminar ejercicios');
                return;
            }

            const newRoutine = { ...userRoutine };
            newRoutine[day] = newRoutine[day].filter(
                ex => ex.id !== exerciseId
            );
            setUserRoutine(newRoutine);
            await saveUserRoutine(user.uid, newRoutine);
        } catch (error) {
            console.error('Error al eliminar ejercicio:', error);
            setError('Error al eliminar ejercicio de la rutina');
        }
    };

    // Función para actualizar el progreso cuando cambia el día
    const handleDayChange = async (day) => {
        setSelectedDay(day);
        if (user) {
            try {
                await updateUserProgress(user.uid, 'day_change');
                // Actualizar las estadísticas
                const userProfile = await getUserProfile(user.uid);
                setProfile(userProfile);
            } catch (error) {
                console.error('Error al actualizar progreso:', error);
            }
        }
    };

    const handleDeleteExercise = async (exerciseId, day) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar este ejercicio?')) {
            try {
                const newRoutine = { ...userRoutine };
                newRoutine[day] = newRoutine[day].filter(ex => ex.id !== exerciseId);
                
                // Solo actualizar la rutina, sin afectar el progreso
                await saveUserRoutine(user.uid, newRoutine);
                setUserRoutine(newRoutine);
            } catch (error) {
                console.error('Error al eliminar ejercicio:', error);
                setError('Error al eliminar ejercicio');
            }
        }
    };

    const updateExerciseInRoutine = async (exerciseId, updatedExercise, day) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            const exerciseIndex = updatedRoutine[day].findIndex(ex => ex.id === exerciseId);
            
            if (exerciseIndex !== -1) {
                updatedRoutine[day][exerciseIndex] = updatedExercise;
                await saveUserRoutine(user.uid, updatedRoutine);
                setUserRoutine(updatedRoutine);

                // Actualizar estadísticas directamente
                const userRef = doc(db, 'users', user.uid);
                const userDoc = await getDoc(userRef);
                
                if (userDoc.exists()) {
                    const userData = userDoc.data();
                    const currentStats = userData.stats || {
                        totalExercises: 0,
                        totalWeightLifted: 0,
                        totalSets: 0,
                        totalReps: 0
                    };

                    const totalWeight = updatedExercise.series.reduce((sum, serie) => 
                        sum + (Number(serie.weight) * Number(serie.reps)), 0);
                    const totalSets = updatedExercise.series.length;
                    const totalReps = updatedExercise.series.reduce((sum, serie) => 
                        sum + Number(serie.reps), 0);

                    await updateDoc(userRef, {
                        stats: {
                            totalExercises: currentStats.totalExercises + 1,
                            totalWeightLifted: currentStats.totalWeightLifted + totalWeight,
                            totalSets: currentStats.totalSets + totalSets,
                            totalReps: currentStats.totalReps + totalReps,
                            lastUpdated: new Date()
                        }
                    });

                    // Actualizar el perfil local
                    const updatedProfile = await getUserProfile(user.uid);
                    setProfile(updatedProfile);
                }
            }
        } catch (error) {
            console.error('Error al actualizar ejercicio:', error);
            setError('Error al actualizar ejercicio en la rutina');
        }
    };

    const handleAddSet = async () => {
        if (!selectedExercise) return;
        
        try {
            const newSeries = [...selectedExercise.series, { reps: '', weight: '', completed: false }];
            const updatedExercise = {
                ...selectedExercise,
                series: newSeries
            };
            
            // Actualizar en Firestore
            const userRoutineRef = doc(db, 'userRoutines', user.uid);
            const routineDoc = await getDoc(userRoutineRef);
            
            if (routineDoc.exists()) {
                const routineData = routineDoc.data();
                const updatedRoutine = { ...routineData };
                const exerciseIndex = updatedRoutine[selectedDay].findIndex(ex => ex.id === selectedExercise.id);
                
                if (exerciseIndex !== -1) {
                    updatedRoutine[selectedDay][exerciseIndex] = updatedExercise;
                    await updateDoc(userRoutineRef, updatedRoutine);
                    setUserRoutine(updatedRoutine);
                    setSelectedExercise(updatedExercise);
                }
            }
        } catch (error) {
            console.error('Error al agregar set:', error);
        }
    };

    const handleUpdateExercise = async () => {
        if (!selectedExercise) return;
        
        try {
            const updatedSeries = selectedExercise.series.map(serie => ({
                ...serie,
                reps: Number(serie.reps),
                weight: Number(serie.weight)
            }));

            const updatedExercise = {
                ...selectedExercise,
                series: updatedSeries
            };

            await updateExerciseInRoutine(selectedExercise.id, updatedExercise, selectedDay);
            setSelectedExercise(null);

            // Actualizar estadísticas
            const userRef = doc(db, 'users', user.uid);
            const userDoc = await getDoc(userRef);
            
            if (userDoc.exists()) {
                const userData = userDoc.data();
                const currentStats = userData.stats || {
                    totalExercises: 0,
                    totalWeightLifted: 0,
                    totalSets: 0,
                    totalReps: 0
                };

                const totalWeight = updatedSeries.reduce((sum, serie) => 
                    sum + (Number(serie.weight) * Number(serie.reps)), 0);
                const totalSets = updatedSeries.length;
                const totalReps = updatedSeries.reduce((sum, serie) => 
                    sum + Number(serie.reps), 0);

                await updateDoc(userRef, {
                    stats: {
                        totalExercises: currentStats.totalExercises + 1,
                        totalWeightLifted: currentStats.totalWeightLifted + totalWeight,
                        totalSets: currentStats.totalSets + totalSets,
                        totalReps: currentStats.totalReps + totalReps,
                        lastUpdated: new Date()
                    }
                });

                // Actualizar el perfil local
                const updatedProfile = await getUserProfile(user.uid);
                setProfile(updatedProfile);
            }
        } catch (error) {
            console.error('Error al actualizar ejercicio:', error);
            setError('Error al actualizar ejercicio');
        }
    };

    const filteredExercises = exercises.filter(ex => ex.category === selectedCategory);

    if (loading) {
        return (
            <div className="min-h-screen bg-black text-white p-4">
                <div className="max-w-4xl mx-auto">
                    <div className="bg-gray-900 rounded-lg shadow-md p-6">
                        <div className="flex justify-center items-center h-64">
                            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
                            <p className="ml-3 text-gray-300">Cargando ejercicios...</p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-black text-white p-4">
                <div className="max-w-4xl mx-auto">
                    <div className="bg-gray-900 rounded-lg shadow-md p-6">
                        <div className="text-center">
                            <p className="text-lg font-semibold text-red-400 mb-4">{error}</p>
                            <button 
                                onClick={() => window.location.reload()}
                                className="px-4 py-2 bg-yellow-500 text-black rounded hover:bg-yellow-600 transition-colors"
                            >
                                Reintentar
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-900 text-white p-8">
            <div className="max-w-4xl mx-auto">
                <div className="mb-8">
                    <h2 className="text-xl font-bold mb-4">Selecciona el día</h2>
                    <div className="grid grid-cols-7 gap-2">
                        {['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'].map((day) => (
                            <button
                                key={day}
                                onClick={() => handleDayChange(day)}
                                className={`p-2 rounded-lg ${
                                    selectedDay === day
                                        ? 'bg-yellow-500 text-black'
                                        : 'bg-gray-800 text-gray-300 hover:bg-gray-700'
                                }`}
                            >
                                {day.slice(0, 3)}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Espacio de publicidad */}
                <div className="mb-8 bg-gray-800 rounded-lg p-4 border border-gray-700">
                    <div className="flex items-center justify-between">
                        <div className="flex-1">
                            <h3 className="text-lg font-semibold text-yellow-500 mb-2">¡Mejora tu entrenamiento!</h3>
                            <p className="text-gray-300 mb-3">Descubre nuestros planes premium y accede a rutinas personalizadas, seguimiento de progreso y más.</p>
                            <button className="bg-yellow-500 text-black px-4 py-2 rounded-lg hover:bg-yellow-400 transition-colors">
                                Conoce más
                            </button>
                        </div>
                        <div className="ml-4">
                            <div className="w-24 h-24 bg-gray-700 rounded-lg flex items-center justify-center">
                                <span className="text-gray-400">Logo</span>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Selector de categoría */}
                <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-300 mb-2">Categoría</label>
                    <select
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                        className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                    >
                        {['Pecho', 'Espalda', 'Hombros', 'Piernas', 'Gemelos', 'Bíceps', 'Tríceps', 'Abdominales'].map(category => (
                            <option key={category} value={category}>{category}</option>
                        ))}
                    </select>
                </div>

                {/* Lista de ejercicios */}
                <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-300 mb-2">Ejercicios disponibles</label>
                    <select
                        value={selectedExercise?.id || ''}
                        onChange={(e) => {
                            const exercise = filteredExercises.find(ex => ex.id === e.target.value);
                            setSelectedExercise(exercise);
                        }}
                        className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                    >
                        <option value="">Selecciona un ejercicio</option>
                        {filteredExercises.map(exercise => (
                            <option key={exercise.id} value={exercise.id}>
                                {exercise.name}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Configuración de series, repeticiones y peso */}
                {selectedExercise && (
                    <div className="mb-6 grid grid-cols-3 gap-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">Series</label>
                            <input
                                type="number"
                                value={series}
                                onChange={(e) => setSeries(parseInt(e.target.value) || 0)}
                                min="1"
                                className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">Repeticiones</label>
                            <input
                                type="number"
                                value={reps}
                                onChange={(e) => setReps(parseInt(e.target.value) || 0)}
                                min="1"
                                className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">Peso (kg)</label>
                            <input
                                type="number"
                                value={weight}
                                onChange={(e) => setWeight(parseInt(e.target.value) || 0)}
                                min="0"
                                className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                            />
                        </div>
                    </div>
                )}

                {/* Botón para agregar ejercicio */}
                <button
                    onClick={() => addExerciseToRoutine(selectedExercise)}
                    disabled={!selectedExercise}
                    className="w-full bg-yellow-500 text-black py-2 px-4 rounded hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400"
                >
                    Agregar ejercicio
                </button>

                {/* Lista de ejercicios del día */}
                <div className="mt-6">
                    <h3 className="text-lg font-semibold mb-3 text-yellow-500">Ejercicios del día</h3>
                    {userRoutine[selectedDay]?.map((exercise, index) => (
                        <div key={index} className="bg-gray-800 rounded mb-4 p-4">
                            <div className="flex justify-between items-center mb-2">
                                <span className="font-medium">{exercise.name}</span>
                                <div className="flex space-x-2">
                                    <button
                                        onClick={() => removeExerciseFromRoutine(exercise.id, selectedDay)}
                                        className="text-red-400 hover:text-red-300"
                                    >
                                        Eliminar
                                    </button>
                                    <button
                                        onClick={() => {
                                            const updatedExercise = { ...exercise, series: [{ reps: series, weight: weight }] };
                                            updateExerciseInRoutine(exercise.id, updatedExercise, selectedDay);
                                        }}
                                        className="text-yellow-400 hover:text-yellow-300"
                                    >
                                        Actualizar
                                    </button>
                                </div>
                            </div>
                            <div className="space-y-2">
                                {exercise.series.map((serie, serieIndex) => (
                                    <div key={serieIndex} className="flex items-center space-x-4 bg-gray-700 p-2 rounded">
                                        <span className="text-sm text-gray-400">Serie {serieIndex + 1}</span>
                                        <input
                                            type="number"
                                            value={serie.weight || ''}
                                            onChange={(e) => updateExerciseInRoutine(exercise.id, {
                                                ...exercise,
                                                series: exercise.series.map((s, i) => 
                                                    i === serieIndex ? { ...s, weight: e.target.value } : s
                                                )
                                            }, selectedDay)}
                                            className="w-20 px-2 py-1 bg-gray-600 rounded text-white"
                                            placeholder="Peso"
                                        />
                                        <input
                                            type="number"
                                            value={serie.reps || ''}
                                            onChange={(e) => updateExerciseInRoutine(exercise.id, {
                                                ...exercise,
                                                series: exercise.series.map((s, i) => 
                                                    i === serieIndex ? { ...s, reps: e.target.value } : s
                                                )
                                            }, selectedDay)}
                                            className="w-20 px-2 py-1 bg-gray-600 rounded text-white"
                                            placeholder="Reps"
                                        />
                                        <input
                                            type="checkbox"
                                            checked={serie.completed || false}
                                            onChange={(e) => updateExerciseInRoutine(exercise.id, {
                                                ...exercise,
                                                series: exercise.series.map((s, i) => 
                                                    i === serieIndex ? { ...s, completed: e.target.checked } : s
                                                )
                                            }, selectedDay)}
                                            className="w-4 h-4 text-yellow-500"
                                        />
                                        <RestTimer 
                                            onComplete={() => {
                                                // Opcional: marcar la serie como completada cuando termina el descanso
                                                updateExerciseInRoutine(exercise.id, {
                                                    ...exercise,
                                                    series: exercise.series.map((s, i) => 
                                                        i === serieIndex ? { ...s, completed: true } : s
                                                    )
                                                }, selectedDay);
                                            }}
                                        />
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

const NutritionPage = () => {
