const ShopPage = ({ user }) => {
    const formatPrice = (price) => {
        return new Intl.NumberFormat('es-AR', { style: 'currency', currency: 'ARS' }).format(price);
    };

    const getDiscountedPrice = (price) => {
        return price * (1 - user.discount);
    };
    
    const handleWhatsAppBuy = (product) => {
        const message = encodeURIComponent(`¡Hola! Estoy interesado en el producto "${product.name}" de la tienda SE...MENTAL.`);
        window.open(`https://wa.me/?text=${message}`, '_blank');
    }

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-2">Tienda</h1>
            <p className="text-yellow-400 mb-6">
                Tu nivel <span className="font-bold">{user.levelName}</span> te da un <span className="font-bold">{(user.discount * 100).toFixed(0)}% de descuento</span>.
            </p>

            <div className="grid grid-cols-2 gap-4">
                {PRODUCTS.map(product => {
                    const discountedPrice = getDiscountedPrice(product.price);
                    return (
                        <div key={product.id} className="bg-gray-800 rounded-lg overflow-hidden">
                            <img src={product.img} alt={product.name} className="w-full h-32 object-cover"/>
                            <div className="p-3">
                                <h3 className="font-bold">{product.name}</h3>
                                {user.discount > 0 && (
                                     <p className="text-sm text-gray-500 line-through">{formatPrice(product.price)}</p>
                                )}
                                <p className="text-lg font-bold text-yellow-400">{formatPrice(discountedPrice)}</p>
                                <button onClick={() => handleWhatsAppBuy(product)} className="w-full bg-green-500 text-white font-bold py-1 mt-2 text-sm rounded-lg hover:bg-green-400 transition-colors">
                                    Comprar
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

const ProfilePage = () => {
    const [user, setUser] = useState(null);
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState('info');
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        photoURL: '',
        preferences: {
            theme: 'dark',
            notifications: true,
            language: 'es'
        }
    });

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (user) => {
            setUser(user);
            if (user) {
                try {
                    const userProfile = await getUserProfile(user.uid);
                    setProfile(userProfile);
                    setFormData({
                        name: userProfile.name || '',
                        email: user.email,
                        photoURL: userProfile.photoURL || '',
                        preferences: userProfile.preferences || {
                            theme: 'dark',
                            notifications: true,
                            language: 'es'
                        }
                    });
                } catch (error) {
                    console.error('Error al cargar perfil:', error);
                    setError('Error al cargar el perfil');
                }
            }
            setLoading(false);
        });

        return () => unsubscribe();
    }, []);

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (name.startsWith('preferences.')) {
            const prefName = name.split('.')[1];
            setFormData(prev => ({
                ...prev,
                preferences: {
                    ...prev.preferences,
                    [prefName]: type === 'checkbox' ? checked : value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSaveProfile = async () => {
        try {
            // Actualizar perfil en Firestore
            const userRef = doc(db, 'users', user.uid);
            await updateDoc(userRef, {
                name: formData.name,
                photoURL: formData.photoURL,
                preferences: formData.preferences,
                lastUpdated: new Date().toISOString()
            });

            // Actualizar estado local
            setProfile(prev => ({
                ...prev,
                ...formData
            }));
            setEditMode(false);
        } catch (error) {
            console.error('Error al guardar perfil:', error);
            setError('Error al guardar los cambios');
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-900 text-white p-8">
                <div className="max-w-4xl mx-auto">
                    <div className="animate-pulse">
                        <div className="h-32 bg-gray-800 rounded-lg mb-8"></div>
                        <div className="space-y-4">
                            <div className="h-4 bg-gray-800 rounded w-3/4"></div>
                            <div className="h-4 bg-gray-800 rounded w-1/2"></div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-900 text-white p-8">
            <div className="max-w-4xl mx-auto">
                {/* Barra de progreso */}
                <div className="bg-gray-800 rounded-lg p-6 mb-8">
                    <h2 className="text-xl font-bold mb-4">Progreso General</h2>
                    <div className="w-full bg-gray-700 rounded-full h-4 mb-2">
                        <div 
                            className="bg-yellow-500 h-4 rounded-full transition-all duration-500"
                            style={{ width: `${profile?.progress || 0}%` }}
                        ></div>
                    </div>
                    <p className="text-sm text-gray-400">
                        {profile?.progress?.toFixed(1)}% completado
                    </p>
                </div>

                {/* Tabs de navegación */}
                <div className="flex space-x-4 mb-8">
                    <button
                        onClick={() => setActiveTab('info')}
                        className={`px-4 py-2 rounded-lg ${
                            activeTab === 'info' 
                                ? 'bg-yellow-500 text-black' 
                                : 'bg-gray-800 text-gray-300'
                        }`}
                    >
                        Información Personal
                    </button>
                    <button
                        onClick={() => setActiveTab('stats')}
                        className={`px-4 py-2 rounded-lg ${
                            activeTab === 'stats' 
                                ? 'bg-yellow-500 text-black' 
                                : 'bg-gray-800 text-gray-300'
                        }`}
                    >
                        Estadísticas
                    </button>
                    <button
                        onClick={() => setActiveTab('history')}
                        className={`px-4 py-2 rounded-lg ${
                            activeTab === 'history' 
                                ? 'bg-yellow-500 text-black' 
                                : 'bg-gray-800 text-gray-300'
                        }`}
                    >
                        Historial
                    </button>
                    <button
                        onClick={() => setActiveTab('settings')}
                        className={`px-4 py-2 rounded-lg ${
                            activeTab === 'settings' 
                                ? 'bg-yellow-500 text-black' 
                                : 'bg-gray-800 text-gray-300'
                        }`}
                    >
                        Configuración
                    </button>
                </div>

                {/* Contenido de las tabs */}
                <div className="bg-gray-800 rounded-lg p-6">
                    {activeTab === 'info' && (
                        <div>
                            <div className="flex justify-between items-center mb-6">
                                <h3 className="text-xl font-bold">Información Personal</h3>
                                <button
                                    onClick={() => setEditMode(!editMode)}
                                    className="px-4 py-2 bg-yellow-500 text-black rounded-lg hover:bg-yellow-600"
                                >
                                    {editMode ? 'Cancelar' : 'Editar'}
                                </button>
                            </div>
                            
                            {editMode ? (
                                <div className="space-y-4">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-400 mb-1">
                                            Nombre
                                        </label>
                                        <input
                                            type="text"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleInputChange}
                                            className="w-full p-2 bg-gray-700 rounded text-white"
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-400 mb-1">
                                            Email
                                        </label>
                                        <input
                                            type="email"
                                            value={formData.email}
                                            disabled
                                            className="w-full p-2 bg-gray-700 rounded text-gray-400"
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-400 mb-1">
                                            URL de Foto
                                        </label>
                                        <input
                                            type="text"
                                            name="photoURL"
                                            value={formData.photoURL}
                                            onChange={handleInputChange}
                                            className="w-full p-2 bg-gray-700 rounded text-white"
                                        />
                                    </div>
                                    <button
                                        onClick={handleSaveProfile}
                                        className="w-full px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                                    >
                                        Guardar Cambios
                                    </button>
                                </div>
                            ) : (
                                <div className="space-y-4">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-400 mb-1">
                                            Nombre
                                        </label>
                                        <p className="text-white">{profile?.name || 'No especificado'}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-400 mb-1">
                                            Email
                                        </label>
                                        <p className="text-white">{profile?.email}</p>
                                    </div>
                                    {profile?.photoURL && (
                                        <div>
                                            <label className="block text-sm font-medium text-gray-400 mb-1">
                                                Foto de Perfil
                                            </label>
                                            <img 
                                                src={profile.photoURL} 
                                                alt="Foto de perfil" 
                                                className="w-32 h-32 rounded-full object-cover"
                                            />
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    )}

                    {activeTab === 'stats' && (
                        <div>
                            <h3 className="text-xl font-bold mb-6">Estadísticas de Entrenamiento</h3>
                            <div className="grid grid-cols-2 gap-4">
                                <div className="bg-gray-700 p-4 rounded-lg">
                                    <h4 className="text-sm font-medium text-gray-400 mb-1">
                                        Total de Entrenamientos
                                    </h4>
                                    <p className="text-2xl font-bold text-yellow-500">
                                        {profile?.stats?.totalWorkouts || 0}
                                    </p>
                                </div>
                                <div className="bg-gray-700 p-4 rounded-lg">
                                    <h4 className="text-sm font-medium text-gray-400 mb-1">
                                        Total de Ejercicios
                                    </h4>
                                    <p className="text-2xl font-bold text-yellow-500">
                                        {profile?.stats?.totalExercises || 0}
                                    </p>
                                </div>
                                <div className="bg-gray-700 p-4 rounded-lg">
                                    <h4 className="text-sm font-medium text-gray-400 mb-1">
                                        Peso Total Levantado
                                    </h4>
                                    <p className="text-2xl font-bold text-yellow-500">
                                        {profile?.stats?.totalWeightLifted || 0} kg
                                    </p>
                                </div>
                                <div className="bg-gray-700 p-4 rounded-lg">
                                    <h4 className="text-sm font-medium text-gray-400 mb-1">
                                        Último Entrenamiento
                                    </h4>
                                    <p className="text-lg font-bold text-yellow-500">
                                        {profile?.stats?.lastWorkout 
                                            ? new Date(profile.stats.lastWorkout).toLocaleDateString()
                                            : 'Nunca'}
                                    </p>
                                </div>
                            </div>
                        </div>
                    )}

                    {activeTab === 'history' && (
                        <div>
                            <h3 className="text-xl font-bold mb-6">Historial de Entrenamientos</h3>
                            {/* Aquí irá el historial de entrenamientos */}
                            <p className="text-gray-400">Próximamente: Historial detallado de entrenamientos</p>
                        </div>
                    )}

                    {activeTab === 'settings' && (
                        <div>
                            <h3 className="text-xl font-bold mb-6">Configuración</h3>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-400 mb-1">
                                        Tema
                                    </label>
                                    <select
                                        name="preferences.theme"
                                        value={formData.preferences.theme}
                                        onChange={handleInputChange}
                                        className="w-full p-2 bg-gray-700 rounded text-white"
                                    >
                                        <option value="dark">Oscuro</option>
                                        <option value="light">Claro</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="flex items-center space-x-2">
                                        <input
                                            type="checkbox"
                                            name="preferences.notifications"
                                            checked={formData.preferences.notifications}
                                            onChange={handleInputChange}
                                            className="form-checkbox h-5 w-5 text-yellow-500"
                                        />
                                        <span className="text-gray-400">Activar notificaciones</span>
                                    </label>
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-400 mb-1">
                                        Idioma
                                    </label>
                                    <select
                                        name="preferences.language"
                                        value={formData.preferences.language}
                                        onChange={handleInputChange}
                                        className="w-full p-2 bg-gray-700 rounded text-white"
                                    >
                                        <option value="es">Español</option>
                                        <option value="en">English</option>
                                    </select>
                                </div>
                                <button
                                    onClick={handleSaveProfile}
                                    className="w-full px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                                >
                                    Guardar Configuración
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Verificar si el usuario ya está autenticado
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                navigate('/training');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Verificar si el usuario es administrador
            const userDoc = await getDoc(doc(db, 'users', user.uid));
            if (userDoc.exists() && userDoc.data().role === 'admin') {
                navigate('/admin');
            } else {
                navigate('/training');
            }
        } catch (error) {
            console.error('Error en login:', error);
            if (error.code === 'auth/user-not-found') {
                setError('Usuario no encontrado');
            } else if (error.code === 'auth/wrong-password') {
                setError('Contraseña incorrecta');
            } else {
                setError('Error al iniciar sesión');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white flex items-center justify-center p-4">
            <div className="w-full max-w-md">
                <div className="text-center mb-8">
                    <h1 className="text-3xl font-bold text-yellow-500 mb-2">SementalFit</h1>
                    <p className="text-gray-400">Inicia sesión para continuar</p>
                </div>

                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="tu@email.com"
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="••••••••"
                        />
                    </div>

                    {error && (
                        <div className="text-red-500 text-sm text-center">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black py-2 px-4 rounded-lg hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400 transition-colors"
                    >
                        {loading ? (
                            <div className="flex items-center justify-center">
                                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-black"></div>
                            </div>
                        ) : (
                            'Iniciar sesión'
                        )}
                    </button>

                    <div className="text-center">
                        <p className="text-gray-400">
                            ¿No tienes una cuenta?{' '}
                            <button
                                type="button"
                                onClick={() => navigate('/register')}
                                className="text-yellow-500 hover:text-yellow-400"
                            >
                                Regístrate
                            </button>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

const RegisterPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Verificar si el usuario ya está autenticado
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                navigate('/training');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleRegister = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            // Crear usuario en Firebase Auth
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Crear documento de usuario en Firestore
            await setDoc(doc(db, 'users', user.uid), {
                name,
                email,
                role: 'user',
                createdAt: new Date().toISOString(),
                level: 'Principiante',
                levelName: 'Nivel 1',
                experience: 0,
                nextLevelExp: 1000,
                progress: 0,
                stats: {
                    totalExercises: 0,
                    totalWeightLifted: 0,
                    totalSets: 0,
                    totalReps: 0,
                    lastUpdated: new Date()
                }
            });

            // Verificar que el usuario se creó correctamente
            const userDoc = await getDoc(doc(db, 'users', user.uid));
            console.log('Usuario creado:', userDoc.data());

            // Redirigir a la página de entrenamiento
            navigate('/training');
        } catch (error) {
            console.error('Error en registro:', error);
            if (error.code === 'auth/email-already-in-use') {
                setError('El email ya está registrado');
            } else if (error.code === 'auth/invalid-email') {
                setError('Email inválido');
            } else if (error.code === 'auth/weak-password') {
                setError('La contraseña debe tener al menos 6 caracteres');
            } else {
                setError('Error al crear la cuenta');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white flex items-center justify-center p-4">
            <div className="w-full max-w-md">
                <div className="text-center mb-8">
                    <h1 className="text-3xl font-bold text-yellow-500 mb-2">SementalFit</h1>
                    <p className="text-gray-400">Crea tu cuenta</p>
                </div>

                <form onSubmit={handleRegister} className="space-y-6">
                    <div>
                        <label htmlFor="name" className="block text-sm font-medium text-gray-300 mb-2">
                            Nombre
                        </label>
                        <input
                            type="text"
                            id="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="Tu nombre"
                        />
                    </div>

                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="tu@email.com"
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="••••••••"
                        />
                    </div>

                    {error && (
                        <div className="text-red-500 text-sm text-center">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black py-2 px-4 rounded-lg hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400 transition-colors"
                    >
                        {loading ? (
                            <div className="flex items-center justify-center">
                                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-black"></div>
                            </div>
                        ) : (
                            'Registrarse'
                        )}
                    </button>

                    <div className="text-center">
                        <p className="text-gray-400">
                            ¿Ya tienes una cuenta?{' '}
                            <button
                                type="button"
                                onClick={() => navigate('/login')}
                                className="text-yellow-500 hover:text-yellow-400"
                            >
                                Inicia sesión
                            </button>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

const Navigation = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            setUser(user);
            setLoading(false);
            if (!user) {
                navigate('/login');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogout = async () => {
        try {
            await signOut(auth);
            navigate('/login');
        } catch (error) {
            console.error('Error al cerrar sesión:', error);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-black text-white flex items-center justify-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
            </div>
        );
    }

    if (!user) {
        return <Navigate to="/login" />;
    }

    return (
        <div className="fixed bottom-0 left-0 right-0 bg-gray-900 border-t border-gray-800">
            <div className="max-w-4xl mx-auto px-4">
                <div className="flex justify-around py-2">
                    <NavLink to="/" className={({ isActive }) =>
                        `flex flex-col items-center ${isActive ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'}`
