// Componente para proteger rutas de administrador
const AdminRoute = ({ children }) => {
    const [isAdmin, setIsAdmin] = useState(false);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const checkAdminStatus = async () => {
            const user = auth.currentUser;
            console.log('Usuario actual:', user?.email);
            
            if (!user) {
                console.log('No hay usuario autenticado');
                navigate('/login');
                return;
            }

            try {
                const userDoc = await getDoc(doc(db, 'users', user.uid));
                console.log('Datos del usuario:', userDoc.data());
                
                // Solo permitir acceso si tiene rol admin
                if (userDoc.exists() && userDoc.data().role === 'admin') {
                    console.log('Acceso permitido como administrador');
                    setIsAdmin(true);
                } else {
                    console.log('Acceso denegado - No es administrador');
                    navigate('/training');
                }
            } catch (error) {
                console.error('Error checking admin status:', error);
                navigate('/training');
            }
            setLoading(false);
        };

        checkAdminStatus();
    }, [navigate]);

    if (loading) {
        return (
            <div className="min-h-screen bg-black flex items-center justify-center">
                <div className="text-yellow-500 text-xl">Cargando...</div>
            </div>
        );
    }

    return isAdmin ? children : null;
};

// Página de administrador
const AdminPage = () => {
    const [activeTab, setActiveTab] = useState('exercises');
    const [exercises, setExercises] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [user, setUser] = useState(null);
    const [userRoutine, setUserRoutine] = useState({});
    const [selectedDay, setSelectedDay] = useState(getCurrentDay());
    const [newExercise, setNewExercise] = useState({
        name: '',
        category: '',
        muscleGroup: '',
        difficulty: 'Principiante',
        description: '',
        instructions: '',
        imageUrl: '',
        videoUrl: ''
    });

    useEffect(() => {
        const loadExercises = async () => {
            if (activeTab === 'exercises') {
                try {
                    const exercisesSnapshot = await getDocs(collection(db, 'exercises'));
                    const exercisesList = exercisesSnapshot.docs.map(doc => ({
                        id: doc.id,
                        ...doc.data()
                    }));
                    setExercises(exercisesList);
                } catch (error) {
                    console.error('Error al cargar ejercicios:', error);
                }
            }
        };

        loadExercises();
    }, [activeTab]);

    const handleExerciseSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const docRef = await addDoc(collection(db, 'exercises'), {
                ...newExercise,
                createdAt: new Date()
            });

            setExercises([...exercises, { id: docRef.id, ...newExercise }]);
            setNewExercise({
                name: '',
                description: '',
                category: '',
                muscleGroup: '',
                difficulty: 'Principiante',
                imageUrl: '',
                videoUrl: '',
                instructions: ''
            });

            alert('Ejercicio creado exitosamente');
        } catch (error) {
            console.error('Error al crear ejercicio:', error);
            alert('Error al crear el ejercicio');
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteExercise = async (exerciseId, day) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar este ejercicio?')) {
            try {
                const newRoutine = { ...userRoutine };
                newRoutine[day] = newRoutine[day].filter(ex => ex.id !== exerciseId);
                
                // Solo actualizar la rutina, sin afectar el progreso
                await saveUserRoutine(user.uid, newRoutine);
                setUserRoutine(newRoutine);
            } catch (error) {
                console.error('Error al eliminar ejercicio:', error);
                setError('Error al eliminar ejercicio');
            }
        }
    };

    return (
        <div className="min-h-screen bg-black text-white p-8">
            <div className="max-w-7xl mx-auto">
                <h1 className="text-4xl font-bold mb-8 text-yellow-500">Panel de Administración</h1>
                
                {/* Tabs */}
                <div className="flex space-x-4 mb-8">
                    <button
                        onClick={() => setActiveTab('exercises')}
                        className={`px-4 py-2 rounded ${
                            activeTab === 'exercises' ? 'bg-yellow-500 text-black' : 'bg-gray-800'
                        }`}
                    >
                        Ejercicios
                    </button>
                    <button
                        onClick={() => setActiveTab('quotes')}
                        className={`px-4 py-2 rounded ${
                            activeTab === 'quotes' ? 'bg-yellow-500 text-black' : 'bg-gray-800'
                        }`}
                    >
                        Frases
                    </button>
                    <button
                        onClick={() => setActiveTab('products')}
                        className={`px-4 py-2 rounded ${
                            activeTab === 'products' ? 'bg-yellow-500 text-black' : 'bg-gray-800'
                        }`}
                    >
                        Productos
                    </button>
                    <button
                        onClick={() => setActiveTab('nutrition')}
                        className={`px-4 py-2 rounded ${
                            activeTab === 'nutrition' ? 'bg-yellow-500 text-black' : 'bg-gray-800'
                        }`}
                    >
                        Nutrición
                    </button>
                    <button
                        onClick={() => setActiveTab('users')}
                        className={`px-4 py-2 rounded ${
                            activeTab === 'users' ? 'bg-yellow-500 text-black' : 'bg-gray-800'
                        }`}
                    >
                        Usuarios
                    </button>
                </div>

                {/* Contenido de las tabs */}
                <div className="bg-gray-900 p-6 rounded-lg">
                    {activeTab === 'exercises' && (
                        <div>
                            <h2 className="text-2xl font-bold mb-4 text-yellow-500">Gestión de Ejercicios</h2>
                            
                            {/* Formulario para nuevo ejercicio */}
                            <form onSubmit={handleExerciseSubmit} className="mb-8 space-y-4">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div>
                                        <label className="block text-sm font-medium mb-1">Nombre</label>
                                        <input
                                            type="text"
                                            value={newExercise.name}
                                            onChange={(e) => setNewExercise({...newExercise, name: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium mb-1">Categoría</label>
                                        <input
                                            type="text"
                                            value={newExercise.category}
                                            onChange={(e) => setNewExercise({...newExercise, category: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium mb-1">Grupo Muscular</label>
                                        <input
                                            type="text"
                                            value={newExercise.muscleGroup}
                                            onChange={(e) => setNewExercise({...newExercise, muscleGroup: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium mb-1">Dificultad</label>
                                        <select
                                            value={newExercise.difficulty}
                                            onChange={(e) => setNewExercise({...newExercise, difficulty: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                            required
                                        >
                                            <option value="Principiante">Principiante</option>
                                            <option value="Intermedio">Intermedio</option>
                                            <option value="Avanzado">Avanzado</option>
                                        </select>
                                    </div>
                                    <div className="md:col-span-2">
                                        <label className="block text-sm font-medium mb-1">Descripción</label>
                                        <textarea
                                            value={newExercise.description}
                                            onChange={(e) => setNewExercise({...newExercise, description: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                            rows="3"
                                            required
                                        />
                                    </div>
                                    <div className="md:col-span-2">
                                        <label className="block text-sm font-medium mb-1">Instrucciones</label>
                                        <textarea
                                            value={newExercise.instructions}
                                            onChange={(e) => setNewExercise({...newExercise, instructions: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                            rows="3"
                                            required
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium mb-1">URL de Imagen</label>
                                        <input
                                            type="url"
                                            value={newExercise.imageUrl}
                                            onChange={(e) => setNewExercise({...newExercise, imageUrl: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                        />
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium mb-1">URL de Video</label>
                                        <input
                                            type="url"
                                            value={newExercise.videoUrl}
                                            onChange={(e) => setNewExercise({...newExercise, videoUrl: e.target.value})}
                                            className="w-full px-3 py-2 bg-gray-800 rounded text-white"
                                        />
                                    </div>
                                </div>
                                <button
                                    type="submit"
                                    disabled={loading}
                                    className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded"
                                >
                                    {loading ? 'Creando...' : 'Crear Ejercicio'}
                                </button>
                            </form>

                            {/* Lista de ejercicios */}
                            <div className="space-y-4">
                                <h3 className="text-xl font-bold text-yellow-500">Ejercicios Existentes</h3>
                                {exercises.map((exercise) => (
                                    <div key={exercise.id} className="bg-gray-800 p-4 rounded-lg">
                                        <div className="flex justify-between items-start">
                                            <div>
                                                <h4 className="text-lg font-bold">{exercise.name}</h4>
                                                <p className="text-gray-400">{exercise.category} - {exercise.muscleGroup}</p>
                                                <p className="text-sm text-gray-500">Dificultad: {exercise.difficulty}</p>
                                            </div>
                                            <button
                                                onClick={() => handleDeleteExercise(exercise.id, selectedDay)}
                                                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                                            >
                                                Eliminar
                                            </button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                    {activeTab === 'quotes' && (
                        <div>
                            <h2 className="text-2xl font-bold mb-4 text-yellow-500">Gestión de Frases</h2>
                            {/* Contenido de frases */}
                        </div>
                    )}
                    {activeTab === 'products' && (
                        <div>
                            <h2 className="text-2xl font-bold mb-4 text-yellow-500">Gestión de Productos</h2>
                            {/* Contenido de productos */}
                        </div>
                    )}
                    {activeTab === 'nutrition' && (
                        <div>
                            <h2 className="text-2xl font-bold mb-4 text-yellow-500">Gestión de Nutrición</h2>
                            {/* Contenido de nutrición */}
                        </div>
                    )}
                    {activeTab === 'users' && (
                        <div>
                            <h2 className="text-2xl font-bold mb-4 text-yellow-500">Gestión de Usuarios</h2>
                            {/* Contenido de usuarios */}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

// Componente para el temporizador de descanso
const RestTimer = ({ onComplete }) => {
    const [timeLeft, setTimeLeft] = useState(60);
    const [isRunning, setIsRunning] = useState(false);
    const [isComplete, setIsComplete] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [minutes, setMinutes] = useState(1);
    const [seconds, setSeconds] = useState(0);

    useEffect(() => {
        let timer;
        if (isRunning && timeLeft > 0) {
            timer = setInterval(() => {
                setTimeLeft(prev => prev - 1);
            }, 1000);
        } else if (timeLeft === 0) {
            setIsRunning(false);
            setIsComplete(true);
            onComplete?.();
        }
        return () => clearInterval(timer);
    }, [isRunning, timeLeft, onComplete]);

    const toggleTimer = () => {
        if (isEditing) {
            const totalSeconds = (parseInt(minutes) * 60) + parseInt(seconds);
            setTimeLeft(totalSeconds);
            setIsEditing(false);
        }
        setIsRunning(!isRunning);
        if (isComplete) {
            setTimeLeft((parseInt(minutes) * 60) + parseInt(seconds));
            setIsComplete(false);
        }
    };

    const formatTime = (totalSeconds) => {
        const mins = Math.floor(totalSeconds / 60);
        const secs = totalSeconds % 60;
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    const handleTimeChange = (value, type) => {
        const numValue = parseInt(value) || 0;
        if (type === 'minutes') {
            setMinutes(Math.min(99, Math.max(0, numValue)));
        } else {
            setSeconds(Math.min(59, Math.max(0, numValue)));
        }
    };

    return (
        <div className="flex flex-col space-y-1">
            <span className="text-sm font-bold text-gray-400">Descanso</span>
            <div className="flex items-center space-x-2">
                {isEditing ? (
                    <div className="flex items-center space-x-1">
                        <input
                            type="number"
                            value={minutes}
                            onChange={(e) => handleTimeChange(e.target.value, 'minutes')}
                            className="w-12 px-1 py-0.5 bg-gray-600 rounded text-white text-sm"
                            min="0"
                            max="99"
                            placeholder="Min"
                        />
                        <span className="text-sm text-gray-400">:</span>
                        <input
                            type="number"
                            value={seconds}
                            onChange={(e) => handleTimeChange(e.target.value, 'seconds')}
                            className="w-12 px-1 py-0.5 bg-gray-600 rounded text-white text-sm"
                            min="0"
                            max="59"
                            placeholder="Seg"
                        />
                    </div>
                ) : (
                    <span className={`text-sm font-bold ${isComplete ? 'text-green-500' : 'text-yellow-500'}`}>
                        {formatTime(timeLeft)}
                    </span>
                )}
                <button
                    onClick={toggleTimer}
                    className={`px-2 py-1 rounded text-xs font-bold ${
                        isRunning 
                            ? 'bg-red-600 hover:bg-red-700 text-white' 
                            : isEditing
                                ? 'bg-green-500 hover:bg-green-600 text-white'
                                : 'bg-green-500 hover:bg-green-600 text-white'
                    }`}
                >
                    {isRunning ? 'Stop' : isComplete ? 'Reset' : isEditing ? 'Guardar' : 'Start'}
                </button>
                {!isRunning && !isComplete && !isEditing && (
                    <button
                        onClick={() => setIsEditing(true)}
                        className="px-2 py-1 rounded text-xs font-bold bg-gray-600 hover:bg-gray-700 text-white"
                    >
                        Editar
                    </button>
                )}
            </div>
        </div>
    );
};

function App() {
    useEffect(() => {
        // Crear usuario administrador al iniciar la aplicación
        createAdminUser();
    }, []);

    return (
        <div className="min-h-screen bg-black">
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/training" element={
                    <Layout>
                        <TrainingPage />
                    </Layout>
                } />
                <Route path="/nutrition" element={
                    <Layout>
                        <NutritionPage />
                    </Layout>
                } />
                <Route path="/profile" element={
                    <Layout>
                        <ProfilePage />
                    </Layout>
                } />
                <Route path="/admin" element={
                    <AdminRoute>
                        <AdminPage />
                    </AdminRoute>
                } />
            </Routes>
        </div>
    );
}

export default App;
