// Importar Firebase usando la versión compat
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';

const firebaseConfig = {
    apiKey: "AIzaSyBXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",
    authDomain: "sementalfit.firebaseapp.com",
    projectId: "sementalfit",
    storageBucket: "sementalfit.appspot.com",
    messagingSenderId: "XXXXXXXXXXXX",
    appId: "1:XXXXXXXXXXXX:web:XXXXXXXXXXXXXXXX"
};

// Inicializar Firebase
firebase.initializeApp(firebaseConfig);

// Obtener instancias de Auth y Firestore
const auth = firebase.auth();
const db = firebase.firestore();

// Conectar a los emuladores en desarrollo
if (process.env.NODE_ENV === 'development') {
    auth.useDeviceLanguage();
    auth.connectAuthEmulator('http://localhost:9099');
    db.connectFirestoreEmulator('localhost', 8080);
}

export const getUser = async (userId) => {
    try {
        const userDoc = await db.collection('users').doc(userId).get();
        return userDoc.exists ? userDoc.data() : null;
    } catch (error) {
        console.error('Error getting user:', error);
        return null;
    }
};

export const getUserRoutine = async (userId) => {
    try {
        const routineDoc = await db.collection('routines').doc(userId).get();
        return routineDoc.exists ? routineDoc.data() : {};
    } catch (error) {
        console.error('Error getting user routine:', error);
        return {};
    }
};

export const saveUserRoutine = async (userId, routine) => {
    try {
        await db.collection('routines').doc(userId).set(routine);
        return true;
    } catch (error) {
        console.error('Error saving user routine:', error);
        return false;
    }
};

export const getAllExercises = async () => {
    try {
        const exercisesSnapshot = await db.collection('exercises').get();
        return exercisesSnapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data()
        }));
    } catch (error) {
        console.error('Error getting exercises:', error);
        return [];
    }
};

export { auth, db };
