import React, { useState, useEffect } from 'react';
import { getAuth } from 'firebase/auth';
import { getUserRoutine, saveUserRoutine, getAllExercises } from '../services/firebase';

const getCurrentDay = () => {
    const days = ['domingo', 'lunes', 'martes', 'miércoles', 'jueves', 'viernes', 'sábado'];
    return days[new Date().getDay()];
};

const TrainingPage = () => {
    const [selectedDay, setSelectedDay] = useState(getCurrentDay());
    const [selectedCategory, setSelectedCategory] = useState('todos');
    const [exercises, setExercises] = useState([]);
    const [userRoutine, setUserRoutine] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [user, setUser] = useState(null);
    const [reps, setReps] = useState(12);
    const [weight, setWeight] = useState(0);
    const [series, setSeries] = useState(3);
    const [isAddingExercise, setIsAddingExercise] = useState(false);
    const [selectedExercise, setSelectedExercise] = useState(null);

    useEffect(() => {
        const loadUserData = async () => {
            const auth = getAuth();
            const currentUser = auth.currentUser;
            if (currentUser) {
                setUser(currentUser);
                try {
                    const routine = await getUserRoutine(currentUser.uid);
                    setUserRoutine(routine);
                    const allExercises = await getAllExercises();
                    setExercises(allExercises);
                } catch (error) {
                    console.error('Error loading user data:', error);
                    setError('Error al cargar los datos del usuario');
                }
            }
            setLoading(false);
        };
        loadUserData();
    }, []);

    const handleUpdateExercise = async (exerciseId, updatedExercise) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            const exerciseIndex = updatedRoutine[selectedDay].findIndex(ex => ex.id === exerciseId);
            
            if (exerciseIndex !== -1) {
                updatedRoutine[selectedDay][exerciseIndex] = updatedExercise;
                await saveUserRoutine(user.uid, updatedRoutine);
                setUserRoutine(updatedRoutine);
            }
        } catch (error) {
            console.error('Error al actualizar ejercicio:', error);
            setError('Error al actualizar ejercicio');
        }
    };

    const handleAddExercise = async (exercise) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            if (!updatedRoutine[selectedDay]) {
                updatedRoutine[selectedDay] = [];
            }

            const newExercise = {
                ...exercise,
                id: Date.now().toString(),
                series: [{ reps: 12, weight: 0 }]
            };

            updatedRoutine[selectedDay].push(newExercise);
            await saveUserRoutine(user.uid, updatedRoutine);
            setUserRoutine(updatedRoutine);
            setIsAddingExercise(false);
        } catch (error) {
            console.error('Error al agregar ejercicio:', error);
            setError('Error al agregar ejercicio');
        }
    };

    const handleDeleteExercise = async (exerciseId) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            updatedRoutine[selectedDay] = updatedRoutine[selectedDay].filter(ex => ex.id !== exerciseId);
            await saveUserRoutine(user.uid, updatedRoutine);
            setUserRoutine(updatedRoutine);
        } catch (error) {
            console.error('Error al eliminar ejercicio:', error);
            setError('Error al eliminar ejercicio');
        }
    };

    if (loading) {
        return <div className="min-h-screen bg-black text-white flex items-center justify-center">Cargando...</div>;
    }

    return (
        <div className="min-h-screen bg-black text-white p-4">
            <div className="max-w-4xl mx-auto">
                <h2 className="text-3xl font-bold mb-6 text-yellow-500">Entrenamiento</h2>
                {error && <div className="bg-red-900/50 p-4 rounded-lg mb-4 text-red-400">{error}</div>}

                <div className="flex space-x-2 mb-6 overflow-x-auto pb-2">
                    {['lunes', 'martes', 'miércoles', 'jueves', 'viernes', 'sábado', 'domingo'].map(day => (
                        <button
                            key={day}
                            onClick={() => setSelectedDay(day)}
                            className={`px-4 py-2 rounded-lg ${
                                selectedDay === day 
                                    ? 'bg-yellow-500 text-black font-bold' 
                                    : 'bg-gray-800 hover:bg-gray-700'
                            }`}
                        >
                            {day.charAt(0).toUpperCase() + day.slice(1)}
                        </button>
                    ))}
                </div>

                <div className="space-y-4">
                    {userRoutine && userRoutine[selectedDay] && userRoutine[selectedDay].map(exercise => (
                        <div key={exercise.id} className="bg-gray-800 p-4 rounded-lg">
                            <div className="flex justify-between items-start mb-4">
                                <h3 className="text-xl font-bold">{exercise.name}</h3>
                                <button
                                    onClick={() => handleDeleteExercise(exercise.id)}
                                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                                >
                                    Eliminar
                                </button>
                            </div>
                            <div className="space-y-2">
                                {exercise.series.map((serie, index) => (
                                    <div key={index} className="flex items-center space-x-4">
                                        <span className="text-gray-400">Serie {index + 1}:</span>
                                        <input
                                            type="number"
                                            value={serie.reps}
                                            onChange={(e) => {
                                                const updatedExercise = { ...exercise };
                                                updatedExercise.series[index].reps = Number(e.target.value);
                                                handleUpdateExercise(exercise.id, updatedExercise);
                                            }}
                                            min="0"
                                            className="w-20 px-2 py-1 bg-gray-700 rounded text-white"
                                        />
                                        <span className="text-gray-400">reps</span>
                                        <input
                                            type="number"
                                            value={serie.weight}
                                            onChange={(e) => {
                                                const updatedExercise = { ...exercise };
                                                updatedExercise.series[index].weight = Number(e.target.value);
                                                handleUpdateExercise(exercise.id, updatedExercise);
                                            }}
                                            min="0"
                                            className="w-20 px-2 py-1 bg-gray-700 rounded text-white"
                                        />
                                        <span className="text-gray-400">kg</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>

                <button
                    onClick={() => setIsAddingExercise(true)}
                    className="mt-6 bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded-lg w-full"
                >
                    Agregar Ejercicio
                </button>

                {isAddingExercise && (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
                        <div className="bg-gray-800 rounded-lg p-6 max-w-2xl w-full max-h-[80vh] overflow-y-auto">
                            <div className="flex justify-between items-center mb-4">
                                <h3 className="text-xl font-bold text-yellow-500">Agregar Ejercicio</h3>
                                <button
                                    onClick={() => setIsAddingExercise(false)}
                                    className="text-gray-400 hover:text-white"
                                >
                                    ✕
                                </button>
                            </div>
                            <div className="space-y-4">
                                {exercises.map(exercise => (
                                    <div
                                        key={exercise.id}
                                        className="bg-gray-700 p-4 rounded-lg cursor-pointer hover:bg-gray-600"
                                        onClick={() => {
                                            handleAddExercise(exercise);
                                            setIsAddingExercise(false);
                                        }}
                                    >
                                        <h4 className="text-lg font-bold">{exercise.name}</h4>
                                        <p className="text-gray-400">{exercise.category} - {exercise.muscleGroup}</p>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default TrainingPage;
