import React, { useState, useEffect, useMemo } from 'react';

// --- Iconos SVG para la interfaz ---
const DumbbellIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.25278C12 6.25278 10.8333 5 8.5 5C6.16667 5 5 6.25278 5 6.25278C5 6.25278 3.83333 7.50556 3.83333 10C3.83333 12.4944 5 13.7472 5 13.7472C5 13.7472 6.16667 15 8.5 15C10.8333 15 12 13.7472 12 13.7472M12 6.25278L12 17.7472M12 6.25278C12 6.25278 13.1667 5 15.5 5C17.8333 5 19 6.25278 19 6.25278C19 6.25278 20.1667 7.50556 20.1667 10C20.1667 12.4944 19 13.7472 19 13.7472C19 13.7472 17.8333 15 15.5 15C13.1667 15 12 13.7472 12 13.7472" />
    </svg>
);

const NutritionIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5.636 18.364a9 9 0 010-12.728m12.728 0a9 9 0 010 12.728m-9.9-2.828a4 4 0 010-5.656m5.656 0a4 4 0 010 5.656" />
    </svg>
);

const ShopIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
    </svg>
);

const ProfileIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
    </svg>
);

const LogoIcon = ({ className }) => (
    <svg className={className} viewBox="0 0 200 200" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M60 170 C 20 140, 20 80, 70 40 C 90 20, 110 20, 130 40 C 180 80, 180 140, 140 170 L 130 190 L 70 190 Z" stroke="white" strokeWidth="6" />
        <path d="M100 80 C 80 80, 80 110, 100 110 C 120 110, 120 80, 100 80 Z" fill="#FFD700" />
        <path d="M90 95 Q 100 85, 110 95" stroke="black" strokeWidth="3" strokeLinecap="round"/>
        <path d="M95 105 Q 100 95, 105 105" stroke="black" strokeWidth="3" strokeLinecap="round"/>
        <path d="M80 50 C 60 30, 50 60, 70 70" stroke="white" strokeWidth="5" strokeLinecap="round"/>
        <path d="M120 50 C 140 30, 150 60, 130 70" stroke="white" strokeWidth="5" strokeLinecap="round"/>
    </svg>
);

// --- Datos de la Aplicación ---
const LEVELS = {
    1: { name: "Novato", discount: 0 },
    2: { name: "Aprendiz", discount: 0.05 },
    3: { name: "Guerrero", discount: 0.10 },
    4: { name: "Titán", discount: 0.12 },
    5: { name: "Semental Supremo", discount: 0.15 },
};

const QUOTES = [
    "Lo que niegas te somete, lo que aceptas te transforma. - Carl Jung",
    "El éxito es la suma de pequeños esfuerzos repetidos día tras día. - Napoleon Hill",
    "Tu personalidad crea tu realidad personal. Cambia tu personalidad y cambiarás tu vida. - Joe Dispenza",
    "No eres producto de tus circunstancias. Eres producto de tus decisiones.",
    "La disciplina es el puente entre las metas y los logros."
];

const WORKOUTS = {
    fuerza: [
        { name: "Press de Banca", sets: 4, reps: 6, xp: 20 },
        { name: "Sentadillas", sets: 4, reps: 5, xp: 25 },
        { name: "Peso Muerto", sets: 3, reps: 5, xp: 30 },
    ],
    hipertrofia: [
        { name: "Curl de Bíceps", sets: 3, reps: 12, xp: 15 },
        { name: "Elevaciones Laterales", sets: 4, reps: 15, xp: 15 },
        { name: "Press Francés", sets: 3, reps: 10, xp: 15 },
    ],
    grasa: [
        { name: "Burpees", time: "30s", sets: 5, xp: 20 },
        { name: "Saltos de Cuerda", time: "5min", sets: 1, xp: 25 },
        { name: "Sprints", time: "15s", sets: 8, xp: 30 },
    ],
};

const PRODUCTS = [
    { id: 1, name: "Remera 'Legacy'", price: 25000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Legacy" },
    { id: 2, name: "Proteína 'Titan Whey'", price: 45000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Titan+Whey" },
    { id: 3, name: "Buzo 'Supremo'", price: 38000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Supremo" },
];

const NUTRITION_PLAN = {
    desayuno: "Avena con proteína, frutos rojos y un puñado de almendras.",
    almuerzo: "Pechuga de pollo a la plancha, quinoa y brócoli al vapor.",
    merienda: "Yogur griego con miel y nueces.",
    cena: "Salmón al horno con batatas y espárragos.",
};

// --- Componentes de la Interfaz ---

const SplashScreen = () => (
    <div className="flex flex-col items-center justify-center h-full bg-black text-white animate-fadeIn">
        <LogoIcon className="w-48 h-48 mb-4 text-yellow-400" />
        <h1 style={{ fontFamily: 'Cinzel, serif' }} className="text-5xl md:text-6xl font-bold tracking-widest">
            SE<span className="text-yellow-400">...</span>MENTAL
        </h1>
    </div>
);

const TrainingPage = ({ onWorkoutComplete }) => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [randomQuote, setRandomQuote] = useState('');

    useEffect(() => {
        setRandomQuote(QUOTES[Math.floor(Math.random() * QUOTES.length)]);
    }, []);

    const handleComplete = (xp) => {
        onWorkoutComplete(xp);
        alert(`¡Felicidades! Has ganado ${xp} XP.`);
        setSelectedCategory(null);
    }
    
    if (selectedCategory) {
        const totalXp = WORKOUTS[selectedCategory].reduce((sum, ex) => sum + ex.xp, 0);
        return (
            <div className="p-4 text-white animate-slideInUp">
                <button onClick={() => setSelectedCategory(null)} className="mb-4 text-yellow-400">&larr; Volver a categorías</button>
                <h2 className="text-3xl font-bold mb-4 capitalize">{selectedCategory.replace('_', ' ')}</h2>
                <ul className="space-y-4">
                    {WORKOUTS[selectedCategory].map(exercise => (
                        <li key={exercise.name} className="bg-gray-800 p-4 rounded-lg flex justify-between items-center">
                            <div>
                                <h3 className="font-bold text-lg">{exercise.name}</h3>
                                <p className="text-sm text-gray-400">
                                    {exercise.sets} series x {exercise.reps || exercise.time}
                                </p>
                            </div>
                            <div className="text-yellow-400 font-bold">+{exercise.xp} XP</div>
                        </li>
                    ))}
                </ul>
                <button onClick={() => handleComplete(totalXp)} className="w-full bg-yellow-400 text-black font-bold py-3 mt-6 rounded-lg hover:bg-yellow-300 transition-colors">
                    Completar Entrenamiento
                </button>
            </div>
        );
    }

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-2">Entrenamiento</h1>
            <p className="text-gray-400 mb-6">Elige tu enfoque para hoy.</p>
            
            <div className="bg-gray-800 p-4 rounded-lg mb-6 text-center">
                <p className="italic">"{randomQuote}"</p>
            </div>

            <div className="space-y-4">
                <div onClick={() => setSelectedCategory('fuerza')} className="bg-gray-800 p-6 rounded-lg cursor-pointer hover:bg-gray-700 transition-colors">
                    <h3 className="text-xl font-bold text-yellow-400">Fuerza</h3>
                    <p className="text-gray-300">Rutinas para maximizar tu poder.</p>
                </div>
                <div onClick={() => setSelectedCategory('hipertrofia')} className="bg-gray-800 p-6 rounded-lg cursor-pointer hover:bg-gray-700 transition-colors">
                    <h3 className="text-xl font-bold text-yellow-400">Hipertrofia</h3>
                    <p className="text-gray-300">Construye masa muscular de calidad.</p>
                </div>
                <div onClick={() => setSelectedCategory('grasa')} className="bg-gray-800 p-6 rounded-lg cursor-pointer hover:bg-gray-700 transition-colors">
                    <h3 className="text-xl font-bold text-yellow-400">Pérdida de Grasa</h3>
                    <p className="text-gray-300">Quema calorías y define tu físico.</p>
                </div>
            </div>
            <p className="text-center text-gray-500 mt-6 text-sm">Próximamente: Funcional y Crossfit.</p>
        </div>
    );
};

const NutritionPage = ({ user }) => (
    <div className="p-4 text-white animate-slideInUp">
        <h1 className="text-4xl font-bold mb-2">Nutrición</h1>
        <p className="text-gray-400 mb-6">El combustible para tu máquina.</p>

        <div className="bg-gray-800 p-4 rounded-lg">
            <h2 className="text-xl font-bold text-yellow-400 mb-4">Plan Sugerido para ti</h2>
            <p className="text-sm text-gray-500 mb-4">Adaptado para tu nivel <span className="font-bold">{user.levelName}</span> y objetivos.</p>
            <div className="space-y-3">
                <p><strong className="text-yellow-500">Desayuno:</strong> {NUTRITION_PLAN.desayuno}</p>
                <p><strong className="text-yellow-500">Almuerzo:</strong> {NUTRITION_PLAN.almuerzo}</p>
                <p><strong className="text-yellow-500">Merienda:</strong> {NUTRITION_PLAN.merienda}</p>
                <p><strong className="text-yellow-500">Cena:</strong> {NUTRITION_PLAN.cena}</p>
            </div>
        </div>
    </div>
);

const ShopPage = ({ user }) => {
    const formatPrice = (price) => {
        return new Intl.NumberFormat('es-AR', { style: 'currency', currency: 'ARS' }).format(price);
    };

    const getDiscountedPrice = (price) => {
        return price * (1 - user.discount);
    };
    
    const handleWhatsAppBuy = (product) => {
        const message = encodeURIComponent(`¡Hola! Estoy interesado en el producto "${product.name}" de la tienda SE...MENTAL.`);
        window.open(`https://wa.me/?text=${message}`, '_blank');
    }

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-2">Tienda</h1>
            <p className="text-yellow-400 mb-6">
                Tu nivel <span className="font-bold">{user.levelName}</span> te da un <span className="font-bold">{(user.discount * 100).toFixed(0)}% de descuento</span>.
            </p>

            <div className="grid grid-cols-2 gap-4">
                {PRODUCTS.map(product => {
                    const discountedPrice = getDiscountedPrice(product.price);
                    return (
                        <div key={product.id} className="bg-gray-800 rounded-lg overflow-hidden">
                            <img src={product.img} alt={product.name} className="w-full h-32 object-cover"/>
                            <div className="p-3">
                                <h3 className="font-bold">{product.name}</h3>
                                {user.discount > 0 && (
                                     <p className="text-sm text-gray-500 line-through">{formatPrice(product.price)}</p>
                                )}
                                <p className="text-lg font-bold text-yellow-400">{formatPrice(discountedPrice)}</p>
                                <button onClick={() => handleWhatsAppBuy(product)} className="w-full bg-green-500 text-white font-bold py-1 mt-2 text-sm rounded-lg hover:bg-green-400 transition-colors">
                                    Comprar
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

const ProfilePage = ({ user }) => {
    const xpPercentage = (user.xp / user.xpToNextLevel) * 100;

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-6">Tu Perfil</h1>
            
            <div className="bg-gray-800 p-6 rounded-lg text-center">
                <LogoIcon className="w-24 h-24 mx-auto mb-4"/>
                <h2 className="text-2xl font-bold text-yellow-400">{user.levelName}</h2>
                <p className="text-gray-400">Nivel {user.level}</p>
            </div>
            
            <div className="my-6">
                <div className="flex justify-between items-end mb-1">
                    <span className="text-sm font-bold">Progreso de Nivel</span>
                    <span className="text-sm text-gray-400">{user.xp} / {user.xpToNextLevel} XP</span>
                </div>
                <div className="w-full bg-gray-700 rounded-full h-2.5">
                    <div className="bg-yellow-400 h-2.5 rounded-full" style={{ width: `${xpPercentage}%` }}></div>
                </div>
            </div>

            <div className="bg-gray-800 p-4 rounded-lg">
                <h3 className="text-lg font-bold mb-2">Ranking de Usuarios (Top 3)</h3>
                <ul className="space-y-2">
                    <li className="flex items-center justify-between"><span className="text-yellow-400">1. AlphaBeast</span> <span className="text-gray-400">Nivel 5</span></li>
                    <li className="flex items-center justify-between"><span>2. IronWill</span> <span className="text-gray-400">Nivel 5</span></li>
                    <li className="flex items-center justify-between"><span className="font-bold">3. Tú</span> <span className="text-gray-400">Nivel {user.level}</span></li>
                </ul>
                <p className="text-center text-gray-500 mt-4 text-xs">La IA moderará y motivará en el ranking completo.</p>
            </div>
        </div>
    );
};

const App = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [page, setPage] = useState('training');
    const [user, setUser] = useState({
        level: 1,
        xp: 0,
    });
    
    // Simula la pantalla de carga inicial
    useEffect(() => {
        const timer = setTimeout(() => {
            setIsLoading(false);
        }, 2500);
        return () => clearTimeout(timer);
    }, []);

    // Calcula datos derivados del usuario
    const userData = useMemo(() => {
        const currentLevel = user.level;
        const levelInfo = LEVELS[currentLevel] || LEVELS[Object.keys(LEVELS).length];
        const xpToNextLevel = currentLevel * 100;
        return {
            ...user,
            levelName: levelInfo.name,
            discount: levelInfo.discount,
            xpToNextLevel
        };
    }, [user]);

    // Función para manejar la ganancia de XP
    const handleWorkoutComplete = (xpGained) => {
        let newXp = user.xp + xpGained;
        let newLevel = user.level;
        let xpForNext = userData.xpToNextLevel;

        while (newXp >= xpForNext) {
            newXp -= xpForNext;
            newLevel++;
            if (!LEVELS[newLevel]) {
                newLevel--;
                newXp = xpForNext;
                break;
            }
            xpForNext = newLevel * 100;
        }
        
        setUser({ level: newLevel, xp: newXp });
    };

    const renderPage = () => {
        switch (page) {
            case 'training':
                return <TrainingPage onWorkoutComplete={handleWorkoutComplete} />;
            case 'nutrition':
                return <NutritionPage user={userData} />;
            case 'shop':
                return <ShopPage user={userData} />;
            case 'profile':
                return <ProfilePage user={userData} />;
            default:
                return <TrainingPage onWorkoutComplete={handleWorkoutComplete} />;
        }
    };
    
    // Cargar la fuente de Google Fonts
    useEffect(() => {
        const link = document.createElement('link');
        link.href = "https://fonts.googleapis.com/css2?family=Cinzel:wght@700&family=Inter:wght@400;700&display=swap";
        link.rel = 'stylesheet';
        document.head.appendChild(link);
    }, []);

    if (isLoading) {
        return <div className="h-screen w-screen bg-black"><SplashScreen /></div>
    }

    return (
        <div style={{ fontFamily: 'Inter, sans-serif' }} className="w-full h-full bg-black flex justify-center items-center p-4">
            <div className="w-full max-w-md h-[85vh] max-h-[900px] bg-gray-900 rounded-3xl shadow-2xl overflow-hidden flex flex-col relative border-4 border-gray-700">
                {/* Contenido principal */}
                <main className="flex-grow overflow-y-auto pb-20">
                    {renderPage()}
                </main>

                {/* Barra de Navegación Inferior */}
                <nav className="absolute bottom-0 left-0 right-0 bg-gray-800/80 backdrop-blur-sm border-t border-gray-700">
                    <div className="flex justify-around items-center h-16">
                        <button onClick={() => setPage('training')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${page === 'training' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <DumbbellIcon />
                            <span className="text-xs mt-1">Entrenar</span>
                        </button>
                        <button onClick={() => setPage('nutrition')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${page === 'nutrition' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <NutritionIcon />
                            <span className="text-xs mt-1">Nutrición</span>
                        </button>
                        <button onClick={() => setPage('shop')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${page === 'shop' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <ShopIcon />
                            <span className="text-xs mt-1">Tienda</span>
                        </button>
                        <button onClick={() => setPage('profile')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${page === 'profile' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <ProfileIcon />
                            <span className="text-xs mt-1">Perfil</span>
                        </button>
                    </div>
                </nav>
            </div>
            
            <style jsx global>{`
                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }
                @keyframes slideInUp {
                    from { transform: translateY(20px); opacity: 0; }
                    to { transform: translateY(0); opacity: 1; }
                }
                .animate-fadeIn { animation: fadeIn 1.5s ease-in-out; }
                .animate-slideInUp { animation: slideInUp 0.5s ease-out; }
            `}</style>
        </div>
    );
};

export default App;
