import React, { useState, useEffect, useMemo } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import { getUsers, getUser, createUser, getAllExercises, createUserRoutine, getUserRoutine, updateExerciseInRoutine, initializeExercises, initializeExercisesByCategory } from './services/database';

// --- Iconos SVG para la interfaz ---
const DumbbellIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.25278C12 6.25278 10.8333 5 8.5 5C6.16667 5 5 6.25278 5 6.25278C5 6.25278 3.83333 7.50556 3.83333 10C3.83333 12.4944 5 13.7472 5 13.7472C5 13.7472 6.16667 15 8.5 15C10.8333 15 12 13.7472 12 13.7472M12 6.25278L12 17.7472M12 6.25278C12 6.25278 13.1667 5 15.5 5C17.8333 5 19 6.25278 19 6.25278C19 6.25278 20.1667 7.50556 20.1667 10C20.1667 12.4944 19 13.7472 19 13.7472C19 13.7472 17.8333 15 15.5 15C13.1667 15 12 13.7472 12 13.7472" />
    </svg>
);

const NutritionIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5.636 18.364a9 9 0 010-12.728m12.728 0a9 9 0 010 12.728m-9.9-2.828a4 4 0 010-5.656m5.656 0a4 4 0 010 5.656" />
    </svg>
);

const ShopIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
    </svg>
);

const ProfileIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
    </svg>
);

const LogoIcon = ({ className }) => (
    <img 
        src={process.env.PUBLIC_URL + '/logo.png'} 
        alt="Semental Fit Logo" 
        className={className}
        style={{ objectFit: 'contain', maxWidth: '100%', height: 'auto' }}
    />
);

// --- Datos de la Aplicación ---
const LEVELS = {
    1: { name: "Novato", discount: 0 },
    2: { name: "Aprendiz", discount: 0.05 },
    3: { name: "Guerrero", discount: 0.10 },
    4: { name: "Titán", discount: 0.12 },
    5: { name: "Semental Supremo", discount: 0.15 },
};

const QUOTES = [
    "El dolor que sientes hoy será la fuerza que sentirás mañana.",
    "No te rindas cuando estés cansado. Ríndete cuando hayas terminado.",
    "El éxito no es para los débiles. Es para los que se atreven a soñar.",
    "Tu cuerpo puede soportar casi cualquier cosa. Es tu mente la que necesitas convencer.",
    "La diferencia entre lo imposible y lo posible está en la determinación de una persona.",
    "No cuentes los días, haz que los días cuenten.",
    "El único entrenamiento malo es el que no se hace.",
    "La fuerza no viene de la capacidad física. Viene de una voluntad indomable.",
    "Si quieres algo que nunca has tenido, debes estar dispuesto a hacer algo que nunca has hecho.",
    "El dolor es temporal. El orgullo es para siempre."
];

const WORKOUTS = {
    fuerza: [
        { name: "Press de Banca", sets: 4, reps: 6, xp: 20 },
        { name: "Sentadillas", sets: 4, reps: 5, xp: 25 },
        { name: "Peso Muerto", sets: 3, reps: 5, xp: 30 },
    ],
    hipertrofia: [
        { name: "Curl de Bíceps", sets: 3, reps: 12, xp: 15 },
        { name: "Elevaciones Laterales", sets: 4, reps: 15, xp: 15 },
        { name: "Press Francés", sets: 3, reps: 10, xp: 15 },
    ],
    grasa: [
        { name: "Burpees", time: "30s", sets: 5, xp: 20 },
        { name: "Saltos de Cuerda", time: "5min", sets: 1, xp: 25 },
        { name: "Sprints", time: "15s", sets: 8, xp: 30 },
    ],
};

const PRODUCTS = [
    { id: 1, name: "Remera 'Legacy'", price: 25000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Legacy" },
    { id: 2, name: "Proteína 'Titan Whey'", price: 45000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Titan+Whey" },
    { id: 3, name: "Buzo 'Supremo'", price: 38000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Supremo" },
];

const NUTRITION_PLAN = {
    desayuno: "Avena con proteína, frutos rojos y un puñado de almendras.",
    almuerzo: "Pechuga de pollo a la plancha, quinoa y brócoli al vapor.",
    merienda: "Yogur griego con miel y nueces.",
    cena: "Salmón al horno con batatas y espárragos.",
};

// --- Componentes de la Interfaz ---

const HomePage = () => {
    const [quote, setQuote] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Seleccionar una frase aleatoria al cargar
        const randomQuote = QUOTES[Math.floor(Math.random() * QUOTES.length)];
        setQuote(randomQuote);

        // Función para reproducir el audio
        const speakQuote = () => {
            if ('speechSynthesis' in window) {
                // Cancelar cualquier reproducción anterior
                window.speechSynthesis.cancel();

                // Obtener todas las voces disponibles
                const voices = window.speechSynthesis.getVoices();
                
                // Buscar una voz femenina en español
                const femaleSpanishVoice = voices.find(voice => 
                    (voice.lang.includes('es') || voice.name.includes('Spanish') || voice.name.includes('Español')) &&
                    voice.name.toLowerCase().includes('female')
                ) || voices.find(voice => 
                    voice.lang.includes('es') || 
                    voice.name.includes('Spanish') || 
                    voice.name.includes('Español')
                );

                const utterance = new SpeechSynthesisUtterance(randomQuote);
                utterance.lang = 'es-ES';
                
                // Si encontramos una voz femenina en español, la usamos
                if (femaleSpanishVoice) {
                    utterance.voice = femaleSpanishVoice;
                }
                
                // Configurar la voz para que sea más natural y sensual
                utterance.rate = 0.8; // Velocidad más lenta
                utterance.pitch = 1.1; // Tono ligeramente más alto para sonar más femenino
                utterance.volume = 1; // Volumen máximo

                // Agregar pausas naturales
                utterance.text = randomQuote.replace(/\./g, '. ').replace(/,/g, ', ');

                utterance.onend = () => {
                    // Navegar a la página de entrenamiento después del audio
                    navigate('/training');
                };

                window.speechSynthesis.speak(utterance);
            }
        };

        // Esperar a que las voces estén cargadas
        if (window.speechSynthesis.getVoices().length > 0) {
            speakQuote();
        } else {
            window.speechSynthesis.onvoiceschanged = speakQuote;
        }

        // Limpiar al desmontar
        return () => {
            window.speechSynthesis.cancel();
        };
    }, [navigate]);

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-black p-4">
            <div className="text-center">
                <img 
                    src="/logo.png" 
                    alt="SementalFit Logo" 
                    className="w-64 h-64 mx-auto mb-8"
                />
                <div className="max-w-2xl mx-auto">
                    <p className="text-2xl text-yellow-400 mb-4 font-semibold">{quote}</p>
                </div>
            </div>
        </div>
    );
};

const TrainingPage = () => {
    const [selectedDay, setSelectedDay] = useState('Lunes');
    const [exercises, setExercises] = useState([]);
    const [userRoutine, setUserRoutine] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [selectedExercise, setSelectedExercise] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadExercises = async () => {
            try {
                const allExercises = await getAllExercises();
                setExercises(allExercises);
                setLoading(false);
            } catch (error) {
                console.error("Error al cargar ejercicios:", error);
                setLoading(false);
            }
        };

        const loadUserRoutine = async () => {
            try {
                const routine = await getUserRoutine();
                setUserRoutine(routine);
            } catch (error) {
                console.error("Error al cargar rutina:", error);
            }
        };

        loadExercises();
        loadUserRoutine();
    }, []);

    const categories = [
        'Pecho',
        'Espalda',
        'Hombros',
        'Piernas',
        'Gemelos',
        'Bíceps',
        'Tríceps',
        'Abdominales'
    ];

    // Filtrar ejercicios por categoría seleccionada
    const filteredExercises = selectedCategory 
        ? exercises.filter(ex => ex.category === selectedCategory)
        : [];

    const handleAddExercise = async () => {
        if (!selectedExercise) return;

        try {
            const newExercise = {
                name: selectedExercise,
                category: selectedCategory,
                series: [
                    { peso: '', repeticiones: '' }
                ]
            };

            const updatedRoutine = {
                ...userRoutine,
                [selectedDay]: [...(userRoutine?.[selectedDay] || []), newExercise]
            };

            await createUserRoutine(updatedRoutine);
            setUserRoutine(updatedRoutine);
            setSelectedExercise('');
        } catch (error) {
            console.error("Error al agregar ejercicio:", error);
        }
    };

    const handleRemoveExercise = async (index) => {
        try {
            const updatedExercises = [...userRoutine[selectedDay]];
            updatedExercises.splice(index, 1);
            
            const updatedRoutine = {
                ...userRoutine,
                [selectedDay]: updatedExercises
            };

            await createUserRoutine(updatedRoutine);
            setUserRoutine(updatedRoutine);
        } catch (error) {
            console.error("Error al eliminar ejercicio:", error);
        }
    };

    if (loading) {
        return (
            <div className="p-4 text-white">
                <p>Cargando ejercicios...</p>
            </div>
        );
    }

    return (
        <div className="p-4 text-white max-w-md mx-auto">
            <h2 className="text-2xl font-bold mb-4 text-yellow-400">Entrenamiento</h2>
            
            <div className="mb-4">
                <label className="block text-sm font-medium text-yellow-400 mb-2">
                    Día de la semana
                </label>
                <select
                    value={selectedDay}
                    onChange={(e) => setSelectedDay(e.target.value)}
                    className="w-full p-2 border rounded bg-gray-800 text-yellow-400 border-gray-700"
                >
                    {['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'].map(day => (
                        <option key={day} value={day}>{day}</option>
                    ))}
                </select>
            </div>

            <div className="mb-4">
                <label className="block text-sm font-medium text-yellow-400 mb-2">
                    Grupo Muscular
                </label>
                <select
                    value={selectedCategory}
                    onChange={(e) => {
                        setSelectedCategory(e.target.value);
                        setSelectedExercise('');
                    }}
                    className="w-full p-2 border rounded bg-gray-800 text-yellow-400 border-gray-700"
                >
                    <option value="">Seleccionar grupo muscular</option>
                    {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                    ))}
                </select>
            </div>

            {selectedCategory && (
                <div className="mb-4">
                    <label className="block text-sm font-medium text-yellow-400 mb-2">
                        Ejercicio
                    </label>
                    <select
                        value={selectedExercise}
                        onChange={(e) => setSelectedExercise(e.target.value)}
                        className="w-full p-2 border rounded bg-gray-800 text-yellow-400 border-gray-700"
                    >
                        <option value="">Seleccionar ejercicio</option>
                        {filteredExercises.map(exercise => (
                            <option key={exercise.id} value={exercise.name}>
                                {exercise.name}
                            </option>
                        ))}
                    </select>
                    <button
                        onClick={handleAddExercise}
                        className="w-full mt-2 bg-yellow-500 text-black px-4 py-2 rounded hover:bg-yellow-600"
                    >
                        Agregar Ejercicio
                    </button>
                </div>
            )}

            <div className="space-y-6 overflow-y-auto max-h-[calc(100vh-300px)]">
                {userRoutine?.[selectedDay]?.map((exercise, index) => (
                    <div key={index} className="bg-gray-800 p-4 rounded-lg">
                        <div className="flex justify-between items-center mb-4">
                            <h3 className="text-lg font-semibold text-yellow-400">{exercise.name}</h3>
                            <button
                                onClick={() => handleRemoveExercise(index)}
                                className="text-red-500 hover:text-red-600"
                            >
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                    <path fillRule="evenodd" d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                                </svg>
                            </button>
                        </div>
                        <div className="space-y-2">
                            {exercise.series.map((serie, serieIndex) => (
                                <div key={serieIndex} className="flex gap-2">
                                    <input
                                        type="number"
                                        value={serie.peso}
                                        onChange={(e) => {
                                            const newRoutine = { ...userRoutine };
                                            newRoutine[selectedDay][index].series[serieIndex].peso = e.target.value;
                                            setUserRoutine(newRoutine);
                                        }}
                                        placeholder="Peso"
                                        className="flex-1 p-2 border rounded bg-gray-700 text-yellow-400 border-gray-600"
                                    />
                                    <input
                                        type="number"
                                        value={serie.repeticiones}
                                        onChange={(e) => {
                                            const newRoutine = { ...userRoutine };
                                            newRoutine[selectedDay][index].series[serieIndex].repeticiones = e.target.value;
                                            setUserRoutine(newRoutine);
                                        }}
                                        placeholder="Reps"
                                        className="flex-1 p-2 border rounded bg-gray-700 text-yellow-400 border-gray-600"
                                    />
                                </div>
                            ))}
                            <button
                                onClick={() => {
                                    const newRoutine = { ...userRoutine };
                                    newRoutine[selectedDay][index].series.push({ peso: '', repeticiones: '' });
                                    setUserRoutine(newRoutine);
                                }}
                                className="w-full bg-gray-700 text-yellow-400 px-4 py-2 rounded hover:bg-gray-600"
                            >
                                Agregar Serie
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

const NutritionPage = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadUser = async () => {
            try {
                const userData = await getUser();
                setUser(userData || {
                    name: 'Usuario',
                    level: 'Principiante',
                    levelName: 'Novato',
                    experience: 0,
                    goals: []
                });
            } catch (err) {
                console.error('Error loading user:', err);
                setError('Error al cargar datos del usuario');
            } finally {
                setLoading(false);
            }
        };
        loadUser();
    }, []);

    if (loading) {
        return (
            <div className="min-h-screen bg-black text-white p-4">
                <div className="max-w-4xl mx-auto">
                    <div className="animate-pulse">
                        <div className="h-8 bg-gray-700 rounded w-1/4 mb-4"></div>
                        <div className="h-32 bg-gray-700 rounded mb-4"></div>
                        <div className="h-8 bg-gray-700 rounded w-1/2 mb-4"></div>
                        <div className="h-32 bg-gray-700 rounded"></div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-black text-white p-4">
            <div className="max-w-4xl mx-auto">
                <h1 className="text-3xl font-bold mb-6">Nutrición</h1>
                
                {/* Información del usuario */}
                <div className="bg-gray-800 p-6 rounded-lg mb-6">
                    <h2 className="text-xl font-semibold mb-4">Tu Plan de Nutrición</h2>
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm text-gray-400">Nivel</label>
                            <p className="text-lg">{user?.levelName || 'Novato'}</p>
                        </div>
                        <div>
                            <label className="block text-sm text-gray-400">Recomendaciones</label>
                            <ul className="list-disc list-inside space-y-2">
                                <li>Mantén una dieta balanceada con proteínas, carbohidratos y grasas saludables</li>
                                <li>Consume suficientes proteínas para el desarrollo muscular</li>
                                <li>Hidrátate adecuadamente antes, durante y después del entrenamiento</li>
                                <li>Planifica tus comidas con anticipación</li>
                            </ul>
                        </div>
                    </div>
                </div>

                {/* Sección de macronutrientes */}
                <div className="bg-gray-800 p-6 rounded-lg mb-6">
                    <h2 className="text-xl font-semibold mb-4">Macronutrientes</h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div className="bg-gray-700 p-4 rounded-lg">
                            <h3 className="text-lg font-semibold text-blue-400 mb-2">Proteínas</h3>
                            <p className="text-sm text-gray-300">1.6-2.2g por kg de peso corporal</p>
                        </div>
                        <div className="bg-gray-700 p-4 rounded-lg">
                            <h3 className="text-lg font-semibold text-green-400 mb-2">Carbohidratos</h3>
                            <p className="text-sm text-gray-300">4-6g por kg de peso corporal</p>
                        </div>
                        <div className="bg-gray-700 p-4 rounded-lg">
                            <h3 className="text-lg font-semibold text-yellow-400 mb-2">Grasas</h3>
                            <p className="text-sm text-gray-300">0.5-1g por kg de peso corporal</p>
                        </div>
                    </div>
                </div>

                {/* Sección de consejos */}
                <div className="bg-gray-800 p-6 rounded-lg">
                    <h2 className="text-xl font-semibold mb-4">Consejos de Nutrición</h2>
                    <div className="space-y-4">
                        <div className="bg-gray-700 p-4 rounded-lg">
                            <h3 className="text-lg font-semibold mb-2">Pre-entrenamiento</h3>
                            <p className="text-sm text-gray-300">Consume carbohidratos complejos 2-3 horas antes del entrenamiento</p>
                        </div>
                        <div className="bg-gray-700 p-4 rounded-lg">
                            <h3 className="text-lg font-semibold mb-2">Post-entrenamiento</h3>
                            <p className="text-sm text-gray-300">Proteínas y carbohidratos simples dentro de los 30 minutos posteriores</p>
                        </div>
                        <div className="bg-gray-700 p-4 rounded-lg">
                            <h3 className="text-lg font-semibold mb-2">Hidratación</h3>
                            <p className="text-sm text-gray-300">Bebe al menos 2-3 litros de agua al día</p>
                        </div>
                    </div>
                </div>

                {error && (
                    <div className="bg-red-900/50 p-4 rounded-lg mt-4">
                        <p className="text-red-400">{error}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

const ShopPage = ({ user }) => {
    const formatPrice = (price) => {
        return new Intl.NumberFormat('es-AR', { style: 'currency', currency: 'ARS' }).format(price);
    };

    const getDiscountedPrice = (price) => {
        return price * (1 - user.discount);
    };
    
    const handleWhatsAppBuy = (product) => {
        const message = encodeURIComponent(`¡Hola! Estoy interesado en el producto "${product.name}" de la tienda SE...MENTAL.`);
        window.open(`https://wa.me/?text=${message}`, '_blank');
    }

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-2">Tienda</h1>
            <p className="text-yellow-400 mb-6">
                Tu nivel <span className="font-bold">{user.levelName}</span> te da un <span className="font-bold">{(user.discount * 100).toFixed(0)}% de descuento</span>.
            </p>

            <div className="grid grid-cols-2 gap-4">
                {PRODUCTS.map(product => {
                    const discountedPrice = getDiscountedPrice(product.price);
                    return (
                        <div key={product.id} className="bg-gray-800 rounded-lg overflow-hidden">
                            <img src={product.img} alt={product.name} className="w-full h-32 object-cover"/>
                            <div className="p-3">
                                <h3 className="font-bold">{product.name}</h3>
                                {user.discount > 0 && (
                                     <p className="text-sm text-gray-500 line-through">{formatPrice(product.price)}</p>
                                )}
                                <p className="text-lg font-bold text-yellow-400">{formatPrice(discountedPrice)}</p>
                                <button onClick={() => handleWhatsAppBuy(product)} className="w-full bg-green-500 text-white font-bold py-1 mt-2 text-sm rounded-lg hover:bg-green-400 transition-colors">
                                    Comprar
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

