import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db } from '../firebase';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    category: ''
  });

  const [exercises, setExercises] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    loadCategories();
    loadExercises();
  }, []);

  const loadCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'categories'));
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categorías:', error);
      alert('Error al cargar categorías: ' + error.message);
    }
  };

  const loadExercises = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'exercises'));
      const exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setExercises(exercisesList);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
      alert('Error al cargar ejercicios: ' + error.message);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) return;

    setIsLoading(true);
    try {
      await addDoc(collection(db, 'categories'), {
        name: newCategoryName.trim(),
        createdAt: new Date().toISOString()
      });
      setNewCategoryName('');
      loadCategories();
      alert('Categoría agregada correctamente');
    } catch (error) {
      console.error('Error al agregar categoría:', error);
      alert('Error al agregar categoría: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await addDoc(collection(db, 'exercises'), {
        ...exercise,
        createdAt: new Date().toISOString()
      });
      
      setExercise({
        name: '',
        category: ''
      });
      setShowAddForm(false);
      loadExercises();
      alert('Ejercicio agregado correctamente');
    } catch (error) {
      console.error('Error al agregar ejercicio:', error);
      alert('Error al agregar ejercicio: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.id === categoryId);
    return category ? category.name : 'Sin categoría';
  };

  return (
    <div className="min-h-screen bg-black text-white p-6 pb-20">
      <h1 className="text-3xl font-extrabold text-yellow-500 mb-8 text-center">Panel de Control Administrativo</h1>
      
      <div className="bg-gray-800 rounded-2xl shadow-lg p-6 mb-8 border border-gray-700">
        <h2 className="text-2xl font-bold text-yellow-400 mb-5">Gestionar Categorías</h2>
        <form onSubmit={handleAddCategory} className="flex flex-col sm:flex-row gap-3 mb-5">
          <input
            type="text"
            value={newCategoryName}
            onChange={(e) => setNewCategoryName(e.target.value)}
            placeholder="Nombre de la nueva categoría"
            className="flex-1 p-3 bg-gray-700 rounded-lg text-white placeholder-gray-400 border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
            required
            disabled={isLoading}
          />
          <button
            type="submit"
            className="bg-yellow-500 text-black font-bold py-3 px-6 rounded-lg hover:bg-yellow-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={isLoading}
          >
            {isLoading ? 'Agregando...' : 'Agregar Categoría'}
          </button>
        </form>
        <div className="space-y-3">
          {categories.length === 0 ? (
            <p className="text-gray-400 text-center py-4">No hay categorías. Agrega una arriba.</p>
          ) : (
            categories.map(category => (
              <div key={category.id} className="bg-gray-700 p-4 rounded-lg flex justify-between items-center border border-gray-600">
                <span className="text-gray-200 text-lg">{category.name}</span>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="bg-gray-800 rounded-2xl shadow-lg p-6 mb-8 border border-gray-700">
        <div className="flex flex-col sm:flex-row justify-between items-center mb-5 gap-4">
          <h2 className="text-2xl font-bold text-yellow-400">Ejercicios Existentes</h2>
          <button
            onClick={() => setShowAddForm(!showAddForm)}
            className="bg-yellow-500 text-black font-bold py-2 px-5 rounded-lg hover:bg-yellow-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={isLoading}
          >
            {showAddForm ? 'Cancelar Adición' : 'Agregar Nuevo Ejercicio'}
          </button>
        </div>

        {showAddForm && (
          <form onSubmit={handleSubmit} className="mb-6 space-y-5 p-4 border border-gray-700 rounded-xl bg-gray-900">
            <h3 className="text-xl font-semibold text-yellow-300 mb-4">Formulario de Nuevo Ejercicio</h3>
            <div>
              <label htmlFor="exercise-name" className="block text-gray-300 mb-2 font-medium">Nombre del Ejercicio</label>
              <input
                id="exercise-name"
                type="text"
                name="name"
                value={exercise.name}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white placeholder-gray-400 border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
                required
                disabled={isLoading}
              />
            </div>

            <div>
              <label htmlFor="exercise-category" className="block text-gray-300 mb-2 font-medium">Categoría</label>
              <select
                id="exercise-category"
                name="categoryId"
                value={exercise.categoryId}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
                required
                disabled={isLoading}
              >
                <option value="">Selecciona una categoría</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-yellow-500 text-black font-bold py-3 rounded-lg hover:bg-yellow-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed text-lg tracking-wide"
              disabled={isLoading}
            >
              {isLoading ? 'Agregando Ejercicio...' : 'Guardar Ejercicio'}
            </button>
          </form>
        )}

        <div className="space-y-3">
          {exercises.length === 0 ? (
            <p className="text-gray-400 text-center py-4">No hay ejercicios. Agrega uno utilizando el formulario de arriba.</p>
          ) : (
            exercises.map(exercise => (
              <div key={exercise.id} className="bg-gray-700 p-4 rounded-lg border border-gray-600">
                <h3 className="font-semibold text-yellow-500 text-lg mb-1">{exercise.name}</h3>
                <p className="text-gray-300 text-sm">Categoría: <span className="font-medium">{getCategoryName(exercise.categoryId)}</span></p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
