import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { FaHome, FaDumbbell, FaAppleAlt, FaShoppingCart, FaSignOutAlt } from 'react-icons/fa';

const BottomNavigation = () => {
    const navigate = useNavigate();
    const location = useLocation();

    const isActive = (path) => {
        return location.pathname === path;
    };

    return (
        <div className="fixed bottom-0 left-0 right-0 bg-gray-900 border-t border-gray-800">
            <div className="max-w-sm mx-auto px-4">
                <div className="flex justify-between items-center h-16">
                    <button 
                        onClick={() => navigate('/')}
                        className={`flex flex-col items-center justify-center w-1/5 ${isActive('/') ? 'text-yellow-500' : 'text-gray-400'}`}
                    >
                        <FaHome className="text-xl" />
                        <span className="text-xs mt-1">Inicio</span>
                    </button>
                    <button 
                        onClick={() => navigate('/training')}
                        className={`flex flex-col items-center justify-center w-1/5 ${isActive('/training') ? 'text-yellow-500' : 'text-gray-400'}`}
                    >
                        <FaDumbbell className="text-xl" />
                        <span className="text-xs mt-1">Rutina</span>
                    </button>
                    <button 
                        onClick={() => navigate('/nutrition')}
                        className={`flex flex-col items-center justify-center w-1/5 ${isActive('/nutrition') ? 'text-yellow-500' : 'text-gray-400'}`}
                    >
                        <FaAppleAlt className="text-xl" />
                        <span className="text-xs mt-1">Nutrici√≥n</span>
                    </button>
                    <button 
                        onClick={() => navigate('/shop')}
                        className={`flex flex-col items-center justify-center w-1/5 ${isActive('/shop') ? 'text-yellow-500' : 'text-gray-400'}`}
                    >
                        <FaShoppingCart className="text-xl" />
                        <span className="text-xs mt-1">Shop</span>
                    </button>
                    <button 
                        onClick={() => navigate('/')}
                        className="flex flex-col items-center justify-center w-1/5 text-gray-400"
                    >
                        <FaSignOutAlt className="text-xl" />
                        <span className="text-xs mt-1">Salir</span>
                    </button>
                </div>
            </div>
        </div>
    );
};

export default BottomNavigation;
