import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs, updateDoc, deleteDoc, doc, query, where } from 'firebase/firestore';
import { db } from '../firebase';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    category: ''
  });

  const [exercises, setExercises] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [editExercise, setEditExercise] = useState(null);
  const [editCategory, setEditCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');

  useEffect(() => {
    loadCategories();
    loadExercises();
  }, [searchTerm, filterCategory]);

  const loadCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'categories'));
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categorías:', error);
      alert('Error al cargar categorías: ' + error.message);
    }
  };

  const loadExercises = async () => {
    try {
      let exercisesQuery = collection(db, 'exercises');
      
      if (filterCategory) {
        exercisesQuery = query(exercisesQuery, where('categoryId', '==', filterCategory));
      }

      const querySnapshot = await getDocs(exercisesQuery);
      let exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      if (searchTerm) {
        exercisesList = exercisesList.filter(exercise =>
          exercise.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }
      setExercises(exercisesList);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
      alert('Error al cargar ejercicios: ' + error.message);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) return;

    setIsLoading(true);
    try {
      if (editCategory) {
        const categoryRef = doc(db, 'categories', editCategory.id);
        await updateDoc(categoryRef, {
          name: newCategoryName.trim(),
        });
        alert('Categoría actualizada correctamente');
        setEditCategory(null);
      } else {
        await addDoc(collection(db, 'categories'), {
          name: newCategoryName.trim(),
          createdAt: new Date().toISOString()
        });
        alert('Categoría agregada correctamente');
      }
      setNewCategoryName('');
      loadCategories();
    } catch (error) {
      console.error(`Error al ${editCategory ? 'actualizar' : 'agregar'} categoría:`, error);
      alert(`Error al ${editCategory ? 'actualizar' : 'agregar'} categoría: ` + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleEditCategory = (categoryToEdit) => {
    setEditCategory(categoryToEdit);
    setNewCategoryName(categoryToEdit.name);
  };

  const handleDeleteCategory = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar esta categoría? Esto no eliminará los ejercicios asociados a ella.')) {
      setIsLoading(true);
      try {
        await deleteDoc(doc(db, 'categories', id));
        loadCategories();
        alert('Categoría eliminada correctamente');
      } catch (error) {
        console.error('Error al eliminar categoría:', error);
        alert('Error al eliminar categoría: ' + error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      if (editExercise) {
        const exerciseRef = doc(db, 'exercises', editExercise.id);
        await updateDoc(exerciseRef, {
          name: exercise.name,
          categoryId: exercise.categoryId || '',
        });
        alert('Ejercicio actualizado correctamente');
        setEditExercise(null);
      } else {
        await addDoc(collection(db, 'exercises'), {
          ...exercise,
          createdAt: new Date().toISOString()
        });
        alert('Ejercicio agregado correctamente');
      }
      
      setExercise({
        name: '',
        categoryId: ''
      });
      setShowAddForm(false);
      loadExercises();
    } catch (error) {
      console.error(`Error al ${editExercise ? 'actualizar' : 'agregar'} ejercicio:`, error);
      alert(`Error al ${editExercise ? 'actualizar' : 'agregar'} ejercicio: ` + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleEdit = (exerciseToEdit) => {
    setEditExercise(exerciseToEdit);
    setExercise({ name: exerciseToEdit.name, categoryId: exerciseToEdit.categoryId });
    setShowAddForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este ejercicio?')) {
      setIsLoading(true);
      try {
        await deleteDoc(doc(db, 'exercises', id));
        loadExercises();
        alert('Ejercicio eliminado correctamente');
      } catch (error) {
        console.error('Error al eliminar ejercicio:', error);
        alert('Error al eliminar ejercicio: ' + error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.id === categoryId);
    return category ? category.name : 'Sin categoría';
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6 pb-20">
      <h1 className="text-3xl sm:text-4xl font-extrabold text-yellow-500 mb-8 text-center">Panel de Control Administrativo</h1>
      
      <div className="bg-gray-800 rounded-2xl shadow-lg p-6 mb-8 border border-gray-700">
        <h2 className="text-2xl font-bold text-yellow-400 mb-5">Gestionar Categorías</h2>
        <form onSubmit={handleAddCategory} className="flex flex-col sm:flex-row gap-3 mb-5">
          <input
            type="text"
            value={editCategory ? editCategory.name : newCategoryName}
            onChange={(e) => { 
              setNewCategoryName(e.target.value);
              if (editCategory) setEditCategory(prev => ({ ...prev, name: e.target.value }));
            }}
            placeholder="Nombre de la nueva categoría"
            className="flex-1 p-3 bg-gray-700 rounded-lg text-white placeholder-gray-400 border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
            required
            disabled={isLoading}
          />
          <button
            type="submit"
            className="bg-yellow-500 text-black font-bold py-3 px-6 rounded-lg hover:bg-yellow-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={isLoading}
          >
            {isLoading ? (editCategory ? 'Actualizando...' : 'Agregando...') : (editCategory ? 'Actualizar Categoría' : 'Agregar Categoría')}
          </button>
          {editCategory && (
            <button
              type="button"
              onClick={() => {
                setEditCategory(null);
                setNewCategoryName('');
              }}
              className="bg-gray-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-gray-700 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={isLoading}
            >
              Cancelar
            </button>
          )}
        </form>
        <div className="space-y-3">
          {categories.length === 0 ? (
            <p className="text-gray-400 text-center py-4">No hay categorías. Agrega una arriba.</p>
          ) : (
            categories.map(category => (
              <div key={category.id} className="bg-gray-700 p-4 rounded-lg flex justify-between items-center border border-gray-600">
                <span className="text-gray-200 text-lg">{category.name}</span>
                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEditCategory(category)}
                    className="bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-600 transition-colors duration-200 text-sm"
                    disabled={isLoading}
                  >
                    Editar
                  </button>
                  <button
                    onClick={() => handleDeleteCategory(category.id)}
                    className="bg-red-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-red-600 transition-colors duration-200 text-sm"
                    disabled={isLoading}
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="bg-gray-800 rounded-2xl shadow-lg p-6 mb-8 border border-gray-700">
        <div className="flex flex-col sm:flex-row justify-between items-center mb-5 gap-4">
          <h2 className="text-2xl font-bold text-yellow-400">Ejercicios Existentes</h2>
          <button
            onClick={() => {
              setShowAddForm(!showAddForm);
              if (showAddForm) {
                setEditExercise(null);
                setExercise({ name: '', categoryId: '' });
              }
            }}
            className="bg-yellow-500 text-black font-bold py-2 px-5 rounded-lg hover:bg-yellow-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={isLoading}
          >
            {editExercise ? 'Cancelar Edición' : (showAddForm ? 'Cancelar Adición' : 'Agregar Nuevo Ejercicio')}
          </button>
        </div>

        <div className="mb-6 flex flex-col sm:flex-row gap-3">
          <input
            type="text"
            placeholder="Buscar ejercicio por nombre..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-1 p-3 bg-gray-700 rounded-lg text-white placeholder-gray-400 border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
            disabled={isLoading}
          />
          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
            disabled={isLoading}
          >
            <option value="">Todas las categorías</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>

        {showAddForm && (
          <form onSubmit={handleSubmit} className="mb-6 space-y-5 p-4 border border-gray-700 rounded-xl bg-gray-900">
            <h3 className="text-xl font-semibold text-yellow-300 mb-4">{editExercise ? 'Editar Ejercicio' : 'Formulario de Nuevo Ejercicio'}</h3>
            <div>
              <label htmlFor="exercise-name" className="block text-gray-300 mb-2 font-medium">Nombre del Ejercicio</label>
              <input
                id="exercise-name"
                type="text"
                name="name"
                value={exercise.name}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white placeholder-gray-400 border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
                required
                disabled={isLoading}
              />
            </div>

            <div>
              <label htmlFor="exercise-category" className="block text-gray-300 mb-2 font-medium">Categoría</label>
              <select
                id="exercise-category"
                name="categoryId"
                value={exercise.categoryId}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-all duration-200"
                required
                disabled={isLoading}
              >
                <option value="">Selecciona una categoría</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-yellow-500 text-black font-bold py-3 rounded-lg hover:bg-yellow-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed text-lg tracking-wide"
              disabled={isLoading}
            >
              {isLoading ? (editExercise ? 'Actualizando Ejercicio...' : 'Agregando Ejercicio...') : (editExercise ? 'Actualizar Ejercicio' : 'Guardar Ejercicio')}
            </button>
          </form>
        )}

        <div className="space-y-3">
          {exercises.length === 0 ? (
            <p className="text-gray-400 text-center py-4">No hay ejercicios. Agrega uno utilizando el formulario de arriba.</p>
          ) : (
            exercises.map(exercise => (
              <div key={exercise.id} className="bg-gray-700 p-4 rounded-lg border border-gray-600 flex justify-between items-center">
                <div>
                  <h3 className="font-semibold text-yellow-500 text-lg mb-1">{exercise.name}</h3>
                  <p className="text-gray-300 text-sm">Categoría: <span className="font-medium">{getCategoryName(exercise.categoryId)}</span></p>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEdit(exercise)}
                    className="bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-600 transition-colors duration-200 text-sm"
                    disabled={isLoading}
                  >
                    Editar
                  </button>
                  <button
                    onClick={() => handleDelete(exercise.id)}
                    className="bg-red-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-red-600 transition-colors duration-200 text-sm"
                    disabled={isLoading}
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
