import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import { onAuthStateChanged, signInWithEmailAndPassword, createUserWithEmailAndPassword } from 'firebase/auth';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { db, auth } from './services/firebase';
import TrainingPage from './components/TrainingPage';

// Frases motivadoras
const QUOTES = [
    "Tu cuerpo puede aguantar casi cualquier cosa. Es tu mente la que necesitas convencer.",
    "El dolor que sientes hoy será la fuerza que sentirás mañana.",
    "No te rindas cuando estés cansado. Ríndete cuando hayas terminado.",
    "El éxito no es siempre grande. Es el progreso constante lo que importa.",
    "Tu cuerpo es el reflejo de tu estilo de vida.",
    "La disciplina es elegir entre lo que quieres ahora y lo que quieres más.",
    "No hay atajos para cualquier lugar que valga la pena ir.",
    "El único entrenamiento malo es el que no se hace.",
    "Tu límite es solo tu mente.",
    "El cambio no es fácil, pero siempre es posible."
];

const HomePage = () => {
    const [quote, setQuote] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Seleccionar una frase aleatoria al cargar
        const randomQuote = QUOTES[Math.floor(Math.random() * QUOTES.length)];
        setQuote(randomQuote);

        // Función para reproducir el audio
        const speakQuote = () => {
            if ('speechSynthesis' in window) {
                const voices = window.speechSynthesis.getVoices();
                // Buscar una voz femenina en español
                const femaleSpanishVoice = voices.find(voice => 
                    (voice.lang.includes('es') || voice.name.includes('Spanish') || voice.name.includes('Español')) &&
                    voice.name.toLowerCase().includes('female')
                ) || voices.find(voice => 
                    voice.lang.includes('es') || 
                    voice.name.includes('Spanish') || 
                    voice.name.includes('Español')
                );

                const utterance = new SpeechSynthesisUtterance(randomQuote);
                utterance.lang = 'es-ES';
                if (femaleSpanishVoice) utterance.voice = femaleSpanishVoice;

                // Configuración para una voz más sensual
                utterance.rate = 0.7; // Velocidad más lenta para un tono más sensual
                utterance.pitch = 1.2; // Tono ligeramente más alto para sonar más femenino
                utterance.volume = 0.9; // Volumen ligeramente más bajo para un tono más suave

                // Agregar pausas naturales para un ritmo más sensual
                utterance.text = randomQuote.replace(/\./g, '. ').replace(/,/g, ', ');

                utterance.onend = () => {
                    const currentUser = auth.currentUser;
                    if (currentUser) {
                        navigate('/training');
                    } else {
                        navigate('/login');
                    }
                };

                window.speechSynthesis.speak(utterance);
            }
        };

        // Reproducir inmediatamente si las voces están disponibles
        if (window.speechSynthesis.getVoices().length > 0) {
            speakQuote();
        } else {
            window.speechSynthesis.onvoiceschanged = speakQuote;
        }

        return () => {
            window.speechSynthesis.cancel();
        };
    }, [navigate]);

    return (
        <div className="min-h-screen bg-black flex flex-col items-center justify-center p-4">
            <div className="text-center">
                <img 
                    src="/logo.png" 
                    alt="SementalFit Logo" 
                    className="w-48 h-48 mx-auto mb-8"
                />
                <div className="max-w-2xl mx-auto">
                    <p className="text-2xl text-yellow-400 mb-4 font-semibold">{quote}</p>
                </div>
            </div>
        </div>
    );
};

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                navigate('/training');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            // Validar el correo electrónico
            if (!email.includes('@') || !email.includes('.')) {
                setError('Por favor ingresa un correo electrónico válido');
                setLoading(false);
                return;
            }

            // Validar la contraseña
            if (password.length < 6) {
                setError('La contraseña debe tener al menos 6 caracteres');
                setLoading(false);
                return;
            }

            // Intentar iniciar sesión
            const userCredential = await signInWithEmailAndPassword(auth, email.toLowerCase().trim(), password);
            const user = userCredential.user;

            // Actualizar último inicio de sesión
            await setDoc(doc(db, 'users', user.uid), {
                lastLogin: new Date().toISOString()
            }, { merge: true });

            // Verificar si el usuario es administrador
            const userDoc = await getDoc(doc(db, 'users', user.uid));
            if (userDoc.exists() && userDoc.data().role === 'admin') {
                navigate('/admin');
            } else {
                navigate('/training');
            }
        } catch (error) {
            console.error('Error en login:', error);
            switch (error.code) {
                case 'auth/user-not-found':
                    setError('Usuario no encontrado');
                    break;
                case 'auth/wrong-password':
                    setError('Contraseña incorrecta');
                    break;
                case 'auth/invalid-email':
                    setError('Correo electrónico inválido');
                    break;
                case 'auth/too-many-requests':
                    setError('Demasiados intentos fallidos. Intenta más tarde');
                    break;
                case 'auth/network-request-failed':
                    setError('Error de conexión. Por favor, verifica tu conexión a internet');
                    break;
                default:
                    setError('Error al iniciar sesión. Por favor, intenta de nuevo');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black flex items-center justify-center p-4">
            <div className="w-full max-w-md bg-gray-900 rounded-xl shadow-2xl p-8">
                <div className="text-center mb-8">
                    <img 
                        src="/logo.png" 
                        alt="SementalFit Logo" 
                        className="w-32 h-32 mx-auto mb-4"
                    />
                    <h1 className="text-4xl font-bold text-yellow-500 mb-2">SementalFit</h1>
                    <p className="text-gray-400">Inicia sesión para continuar</p>
                </div>

                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-colors"
                            placeholder="tu@email.com"
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-colors"
                            placeholder="••••••••"
                        />
                    </div>

                    {error && (
                        <div className="text-red-500 text-sm text-center bg-red-900/20 p-3 rounded-lg">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black py-3 px-4 rounded-lg hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400 transition-colors font-semibold text-lg"
                    >
                        {loading ? (
                            <div className="flex items-center justify-center">
                                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-black"></div>
                            </div>
                        ) : (
                            'Iniciar sesión'
                        )}
                    </button>

                    <div className="text-center">
                        <p className="text-gray-400">
                            ¿No tienes una cuenta?{' '}
                            <button
                                type="button"
                                onClick={() => navigate('/register')}
                                className="text-yellow-500 hover:text-yellow-400 font-medium"
                            >
                                Regístrate
                            </button>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

const RegisterPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [name, setName] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                navigate('/training');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleRegister = async (e) => {
        e.preventDefault();
        setError('');

        // Validaciones
        if (password !== confirmPassword) {
            setError('Las contraseñas no coinciden');
            return;
        }

        if (password.length < 6) {
            setError('La contraseña debe tener al menos 6 caracteres');
            return;
        }

        if (!name.trim()) {
            setError('Por favor ingresa tu nombre');
            return;
        }

        if (!email.includes('@') || !email.includes('.')) {
            setError('Por favor ingresa un correo electrónico válido');
            return;
        }

        setLoading(true);

        try {
            // Crear usuario en Firebase Auth
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;

            // Crear documento del usuario en Firestore
            await setDoc(doc(db, 'users', user.uid), {
                name: name.trim(),
                email: email.toLowerCase().trim(),
                role: 'user',
                createdAt: new Date().toISOString(),
                lastLogin: new Date().toISOString(),
                profile: {
                    height: null,
                    weight: null,
                    age: null,
                    gender: null,
                    fitnessLevel: null
                }
            });

            // Redirigir al usuario a la página de entrenamiento
            navigate('/training');
        } catch (error) {
            console.error('Error en registro:', error);
            switch (error.code) {
                case 'auth/email-already-in-use':
                    setError('Este correo electrónico ya está registrado');
                    break;
                case 'auth/invalid-email':
                    setError('Correo electrónico inválido');
                    break;
                case 'auth/weak-password':
                    setError('La contraseña debe tener al menos 6 caracteres');
                    break;
                case 'auth/network-request-failed':
                    setError('Error de conexión. Por favor, verifica tu conexión a internet');
                    break;
                case 'auth/operation-not-allowed':
                    setError('El registro con correo electrónico no está habilitado');
                    break;
                default:
                    setError('Error al crear la cuenta. Por favor, intenta de nuevo');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black flex items-center justify-center p-4">
            <div className="w-full max-w-md bg-gray-900 rounded-xl shadow-2xl p-8">
                <div className="text-center mb-8">
                    <img 
                        src="/logo.png" 
                        alt="SementalFit Logo" 
                        className="w-32 h-32 mx-auto mb-4"
                    />
                    <h1 className="text-4xl font-bold text-yellow-500 mb-2">SementalFit</h1>
                    <p className="text-gray-400">Crea tu cuenta</p>
                </div>

                <form onSubmit={handleRegister} className="space-y-6">
                    <div>
                        <label htmlFor="name" className="block text-sm font-medium text-gray-300 mb-2">
                            Nombre completo
                        </label>
                        <input
                            type="text"
                            id="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                            className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-colors"
                            placeholder="Tu nombre"
                        />
                    </div>

                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-colors"
                            placeholder="tu@email.com"
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-colors"
                            placeholder="••••••••"
                        />
                        <p className="text-xs text-gray-500 mt-1">Mínimo 6 caracteres</p>
                    </div>

                    <div>
                        <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-300 mb-2">
                            Confirmar contraseña
                        </label>
                        <input
                            type="password"
                            id="confirmPassword"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                            className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500 focus:ring-1 focus:ring-yellow-500 transition-colors"
                            placeholder="••••••••"
                        />
                    </div>

                    {error && (
                        <div className="text-red-500 text-sm text-center bg-red-900/20 p-3 rounded-lg">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black py-3 px-4 rounded-lg hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400 transition-colors font-semibold text-lg"
                    >
                        {loading ? (
                            <div className="flex items-center justify-center">
                                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-black"></div>
                            </div>
                        ) : (
                            'Crear cuenta'
                        )}
                    </button>

                    <div className="text-center">
                        <p className="text-gray-400">
                            ¿Ya tienes una cuenta?{' '}
                            <button
                                type="button"
                                onClick={() => navigate('/login')}
                                className="text-yellow-500 hover:text-yellow-400 font-medium"
                            >
                                Inicia sesión
                            </button>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

function App() {
    return (
        <div className="min-h-screen bg-black">
            <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/training" element={<TrainingPage />} />
            </Routes>
        </div>
    );
}

export default App;
