import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { db } from '../firebase';
import { collection, addDoc, getDocs, query, where } from 'firebase/firestore';

const RoutineForm = () => {
    const { currentUser } = useAuth();
    const [routineName, setRoutineName] = useState('');
    const [routineDesc, setRoutineDesc] = useState('');
    const [categories, setCategories] = useState([]);
    const [exercises, setExercises] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [selectedExercise, setSelectedExercise] = useState('');
    const [routineExercises, setRoutineExercises] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const loadCategories = async () => {
            const categoriesSnapshot = await getDocs(collection(db, 'categories'));
            setCategories(categoriesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        };
        loadCategories();
    }, []);

    useEffect(() => {
        const loadExercises = async () => {
            if (!selectedCategory) return;
            const exercisesSnapshot = await getDocs(
                query(collection(db, 'exercises'), where('categoryId', '==', selectedCategory))
            );
            setExercises(exercisesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        };
        loadExercises();
    }, [selectedCategory]);

    const addExercise = () => {
        if (!selectedExercise) return;
        setRoutineExercises(prev => ([
            ...prev,
            {
                exerciseId: selectedExercise,
                categoryId: selectedCategory,
                sets: [
                    { weight: 0, reps: 0 }
                ]
            }
        ]));
    };

    const addSet = (exerciseIndex) => {
        setRoutineExercises(prev => {
            const newExercises = [...prev];
            newExercises[exerciseIndex].sets.push({ weight: 0, reps: 0 });
            return newExercises;
        });
    };

    const removeSet = (exerciseIndex, setIndex) => {
        setRoutineExercises(prev => {
            const newExercises = [...prev];
            newExercises[exerciseIndex].sets = newExercises[exerciseIndex].sets.filter((_, i) => i !== setIndex);
            return newExercises;
        });
    };

    const updateSet = (exerciseIndex, setIndex, field, value) => {
        setRoutineExercises(prev => {
            const newExercises = [...prev];
            newExercises[exerciseIndex].sets[setIndex][field] = value;
            return newExercises;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!currentUser) return;
        if (routineExercises.length === 0) {
            alert('Debes agregar al menos un ejercicio a la rutina.');
            return;
        }
        // Validar estructura de ejercicios y sets
        for (const ex of routineExercises) {
            if (!ex.exerciseId || !ex.categoryId) {
                alert('Todos los ejercicios deben tener grupo muscular y ejercicio seleccionado.');
                return;
            }
            if (!Array.isArray(ex.sets) || ex.sets.length === 0) {
                alert('Cada ejercicio debe tener al menos una serie.');
                return;
            }
            for (const set of ex.sets) {
                if (typeof set.weight !== 'number' || typeof set.reps !== 'number') {
                    alert('El peso y las repeticiones deben ser números en todas las series.');
                    return;
                }
            }
        }
        setLoading(true);
        try {
            const dataToSave = {
                name: routineName,
                description: routineDesc,
                createdBy: currentUser.uid,
                exercises: routineExercises
            };
            console.log('Guardando rutina prediseñada:', dataToSave);
            await addDoc(collection(db, 'routines'), dataToSave);
            setRoutineName('');
            setRoutineDesc('');
            setRoutineExercises([]);
            alert('Rutina prediseñada creada exitosamente');
        } catch (error) {
            alert('Error al crear la rutina');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="w-full px-2 py-4">
            <h2 className="text-xl font-bold text-yellow-400 mb-3">Crear Rutina Prediseñada</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-white mb-2">Nombre de la Rutina</label>
                    <input
                        type="text"
                        value={routineName}
                        onChange={e => setRoutineName(e.target.value)}
                        className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700"
                        required
                    />
                </div>
                <div>
                    <label className="block text-white mb-2">Descripción</label>
                    <textarea
                        value={routineDesc}
                        onChange={e => setRoutineDesc(e.target.value)}
                        className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700"
                        rows={2}
                    />
                </div>
                <div className="mb-4">
                    <h3 className="text-lg text-white mb-2">Agregar Ejercicio</h3>
                    <div className="flex flex-col gap-2 mb-3">
                        <select
                            value={selectedCategory}
                            onChange={e => setSelectedCategory(e.target.value)}
                            className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700 text-sm"
                        >
                            <option value="">Seleccionar Grupo Muscular</option>
                            {categories.map(cat => (
                                <option key={cat.id} value={cat.id}>{cat.name}</option>
                            ))}
                        </select>
                        <select
                            value={selectedExercise}
                            onChange={e => setSelectedExercise(e.target.value)}
                            className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700 text-sm"
                            disabled={!selectedCategory}
                        >
                            <option value="">Seleccionar Ejercicio</option>
                            {exercises.map(ex => (
                                <option key={ex.id} value={ex.id}>{ex.name}</option>
                            ))}
                        </select>
                        <button
                            type="button"
                            onClick={addExercise}
                            disabled={!selectedExercise}
                            className="w-full px-3 py-2 bg-yellow-500 text-black rounded text-sm hover:bg-yellow-600 disabled:opacity-50"
                        >
                            Agregar Ejercicio
                        </button>
                    </div>
                </div>
                <div className="space-y-3">
                    {routineExercises.map((exercise, exerciseIndex) => {
                        const exerciseData = exercises.find(e => e.id === exercise.exerciseId);
                        return (
                            <div key={exerciseIndex} className="bg-gray-800 p-3 rounded">
                                <h4 className="text-base text-white mb-2">{exerciseData?.name || 'Ejercicio'}</h4>
                                <div className="overflow-x-auto">
                                    <table className="w-full text-sm text-white">
                                        <thead>
                                            <tr>
                                                <th className="px-2 py-1">Serie</th>
                                                <th className="px-2 py-1">Peso</th>
                                                <th className="px-2 py-1">Repeticiones</th>
                                                <th className="px-2 py-1">Eliminar</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {exercise.sets.map((set, setIndex) => (
                                                <tr key={setIndex}>
                                                    <td className="px-2 py-1">{setIndex + 1}</td>
                                                    <td className="px-2 py-1">
                                                        <input
                                                            type="number"
                                                            value={set.weight}
                                                            onChange={e => updateSet(exerciseIndex, setIndex, 'weight', e.target.value)}
                                                            placeholder="Peso (kg)"
                                                            className="w-16 p-1 rounded bg-gray-700 text-white border border-gray-600 text-sm"
                                                        />
                                                    </td>
                                                    <td className="px-2 py-1">
                                                        <input
                                                            type="number"
                                                            value={set.reps}
                                                            onChange={e => updateSet(exerciseIndex, setIndex, 'reps', e.target.value)}
                                                            placeholder="Reps"
                                                            className="w-16 p-1 rounded bg-gray-700 text-white border border-gray-600 text-sm"
                                                        />
                                                    </td>
                                                    <td className="px-2 py-1">
                                                        <button
                                                            type="button"
                                                            onClick={() => removeSet(exerciseIndex, setIndex)}
                                                            className="px-2 py-1 bg-red-500 text-white rounded text-xs"
                                                        >
                                                            Eliminar
                                                        </button>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                    <button
                                        type="button"
                                        onClick={() => addSet(exerciseIndex)}
                                        className="text-yellow-400 hover:text-yellow-500 text-sm mt-2"
                                    >
                                        + Agregar Serie
                                    </button>
                                </div>
                            </div>
                        );
                    })}
                </div>
                <button
                    type="submit"
                    disabled={loading || routineExercises.length === 0}
                    className="mt-4 w-full bg-yellow-500 text-black font-bold py-2 px-4 rounded text-sm hover:bg-yellow-600 disabled:opacity-50"
                >
                    {loading ? 'Creando...' : 'Crear Rutina Prediseñada'}
                </button>
            </form>
        </div>
    );
};

export default RoutineForm;
