import React, { useState, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import Header from './Header';
import { FaShoppingBag, FaShoppingCart, FaStar, FaFilter } from 'react-icons/fa';
import Particles from 'react-tsparticles';
import { loadSlim } from 'tsparticles-slim';

export default function ShopPage() {
    const { currentUser } = useAuth();
    const [selectedCategory, setSelectedCategory] = useState('all');

    const particlesInit = useCallback(async (engine) => {
        await loadSlim(engine);
    }, []);

    const particlesLoaded = useCallback(async (container) => {
        console.log("Particles container loaded", container);
    }, []);

    const products = [
        {
            id: 1,
            name: "Proteína Premium",
            price: 49.99,
            image: "/images/products/protein.jpg",
            description: "Proteína de suero de leche de alta calidad"
        },
        {
            id: 2,
            name: "BCAA",
            price: 29.99,
            image: "/images/products/bcaa.jpg",
            description: "Aminoácidos ramificados para recuperación muscular"
        },
        {
            id: 3,
            name: "Pre-Workout",
            price: 39.99,
            image: "/images/products/preworkout.jpg",
            description: "Energía y rendimiento mejorados"
        },
        {
            id: 4,
            name: "Creatina",
            price: 24.99,
            image: "/images/products/creatine.jpg",
            description: "Monohidrato de creatina pura"
        }
    ];

    const categories = [
        { id: 'all', name: 'Todos' },
        { id: 'suplementos', name: 'Suplementos' },
        { id: 'ropa', name: 'Ropa' },
        { id: 'accesorios', name: 'Accesorios' }
    ];

    const filteredProducts = selectedCategory === 'all' 
        ? products 
        : products.filter(product => product.category === selectedCategory);

    return (
        <div className="min-h-screen text-white p-4 pb-24 neon-luxury">
            <div className="neon-glow-luxury"></div>

            <Particles
                id="tsparticles"
                init={particlesInit}
                loaded={particlesLoaded}
                options={{
                    fullScreen: { enable: false },
                    background: {
                        color: {
                            value: "transparent",
                        },
                    },
                    fpsLimit: 60,
                    particles: {
                        color: {
                            value: "#ffd700",
                        },
                        links: {
                            color: "#ffd700",
                            distance: 150,
                            enable: false,
                            opacity: 0.5,
                            width: 1,
                        },
                        move: {
                            enable: true,
                            outModes: {
                                default: "out",
                            },
                            random: true,
                            speed: 1,
                            straight: false,
                        },
                        number: {
                            density: {
                                enable: true,
                                area: 800,
                            },
                            value: 20,
                        },
                        opacity: {
                            value: 0.5,
                        },
                        shape: {
                            type: "circle",
                        },
                        size: {
                            value: { min: 1, max: 3 },
                        },
                    },
                    detectRetina: true,
                }}
                style={{
                    position: "fixed",
                    top: 0,
                    left: 0,
                    width: "100%",
                    height: "100%",
                    zIndex: -1,
                    opacity: 0.3,
                }}
            />

            <Header />

            <div className="max-w-6xl mx-auto space-y-8">
                {/* Hero Section */}
                <div className="text-center py-12">
                    <div className="inline-block p-3 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-full shadow-neon-sm mb-4">
                        <FaShoppingBag className="text-4xl text-black" />
                    </div>
                    <h1 className="text-4xl md:text-5xl font-bold mb-4 bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
                        Shop
                    </h1>
                    <p className="text-xl text-gray-300">
                        Productos premium para potenciar tu entrenamiento
                    </p>
                </div>

                {/* Categories */}
                <div className="flex flex-wrap gap-4 justify-center">
                    {categories.map(category => (
                        <button
                            key={category.id}
                            onClick={() => setSelectedCategory(category.id)}
                            className={`px-6 py-2 rounded-full border ${
                                selectedCategory === category.id
                                    ? 'border-neon-gold bg-neon-gold/10 text-neon-gold shadow-neon'
                                    : 'border-neon-gold/30 text-gray-300 hover:border-neon-gold/50'
                            } transition-all duration-300`}
                        >
                            {category.name}
                        </button>
                    ))}
                </div>

                {/* Products Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                    {filteredProducts.map(product => (
                        <div
                            key={product.id}
                            className="bg-black/40 backdrop-blur-sm rounded-xl overflow-hidden border border-neon-gold/30 shadow-neon-sm hover:shadow-neon transition-all duration-300"
                        >
                            <div className="relative">
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="w-full h-48 object-cover rounded-t-lg"
                                    onError={(e) => {
                                        e.target.onerror = null;
                                        e.target.src = '/images/products/placeholder.jpg';
                                    }}
                                />
                                <div className="absolute top-2 right-2 bg-black/60 px-2 py-1 rounded-full text-neon-gold text-sm flex items-center gap-1">
                                    <FaStar className="text-yellow-400" />
                                    {product.rating}
                                </div>
                            </div>
                            <div className="p-4">
                                <h3 className="text-lg font-semibold text-white mb-2">{product.name}</h3>
                                <p className="text-gray-300 text-sm mb-4">{product.description}</p>
                                <div className="flex items-center justify-between">
                                    <span className="text-xl font-bold text-neon-gold">${product.price}</span>
                                    <button className="px-4 py-2 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300 flex items-center gap-2">
                                        <FaShoppingCart />
                                        Comprar
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Featured Products */}
                <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
                    <h3 className="text-xl font-bold text-neon-gold mb-4">Productos Destacados</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="bg-black/20 rounded-lg p-4 border border-neon-gold/20">
                            <div className="flex items-center gap-4">
                                <img
                                    src="https://via.placeholder.com/100x100"
                                    alt="Featured Product"
                                    className="w-20 h-20 object-cover rounded-lg"
                                />
                                <div>
                                    <h4 className="text-lg font-semibold text-white">Pack Premium</h4>
                                    <p className="text-gray-300 text-sm">Ahorra 20% en tu primera compra</p>
                                    <button className="mt-2 px-4 py-1 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300 text-sm">
                                        Ver Oferta
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className="bg-black/20 rounded-lg p-4 border border-neon-gold/20">
                            <div className="flex items-center gap-4">
                                <img
                                    src="https://via.placeholder.com/100x100"
                                    alt="Featured Product"
                                    className="w-20 h-20 object-cover rounded-lg"
                                />
                                <div>
                                    <h4 className="text-lg font-semibold text-white">Nueva Colección</h4>
                                    <p className="text-gray-300 text-sm">Ropa deportiva de última generación</p>
                                    <button className="mt-2 px-4 py-1 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300 text-sm">
                                        Explorar
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
