import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db } from '../firebase';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    category: '',
    type: 'compound', // compound o isolation
    equipment: 'barra', // barra, mancuernas, máquina, polea, peso corporal
    description: ''
  });

  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState('');

  useEffect(() => {
    console.log('Iniciando carga de categorías...');
    loadCategories();
  }, []);

  const loadCategories = async () => {
    try {
      console.log('Intentando conectar a Firestore...');
      const querySnapshot = await getDocs(collection(db, 'categories'));
      console.log('Conexión exitosa, documentos encontrados:', querySnapshot.docs.length);
      
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      console.log('Categorías cargadas:', categoriesList);
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categorías:', error);
      alert('Error al cargar categorías: ' + error.message);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategory.trim()) return;

    try {
      console.log('Intentando agregar categoría:', newCategory);
      const docRef = await addDoc(collection(db, 'categories'), {
        name: newCategory.trim(),
        createdAt: new Date().toISOString()
      });
      console.log('Categoría agregada con ID:', docRef.id);
      
      setNewCategory('');
      loadCategories();
      alert('Categoría agregada correctamente');
    } catch (error) {
      console.error('Error al agregar categoría:', error);
      alert('Error al agregar categoría: ' + error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Intentando agregar ejercicio:', exercise);
      const docRef = await addDoc(collection(db, 'exercises'), {
        ...exercise,
        createdAt: new Date().toISOString()
      });
      console.log('Ejercicio agregado con ID:', docRef.id);
      
      setExercise({
        name: '',
        category: '',
        type: 'compound',
        equipment: 'barra',
        description: ''
      });
      
      alert('Ejercicio agregado correctamente');
    } catch (error) {
      console.error('Error al agregar ejercicio:', error);
      alert('Error al agregar ejercicio: ' + error.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="min-h-screen bg-black text-white p-4 pb-20">
      <h1 className="text-2xl font-bold text-yellow-500 mb-6">Panel de Control</h1>
      
      {/* Sección de Categorías */}
      <div className="bg-gray-800 rounded-xl p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4">Gestionar Categorías</h2>
        
        <form onSubmit={handleAddCategory} className="flex gap-2 mb-4">
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            placeholder="Nueva categoría"
            className="flex-1 p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
            required
          />
          <button
            type="submit"
            className="bg-yellow-500 text-black font-bold py-3 px-6 rounded-lg hover:bg-yellow-600 transition-colors"
          >
            Agregar
          </button>
        </form>

        <div className="space-y-2">
          {categories.map(category => (
            <div key={category.id} className="bg-gray-700 p-3 rounded-lg flex justify-between items-center">
              <span className="text-gray-300">{category.name}</span>
            </div>
          ))}
        </div>
      </div>
      
      {/* Sección de Ejercicios */}
      <div className="bg-gray-800 rounded-xl p-6">
        <h2 className="text-xl font-semibold mb-4">Agregar Nuevo Ejercicio</h2>
        
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-300 mb-2">Nombre del Ejercicio</label>
            <input
              type="text"
              name="name"
              value={exercise.name}
              onChange={handleChange}
              className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
              required
            />
          </div>

          <div>
            <label className="block text-gray-300 mb-2">Categoría</label>
            <select
              name="category"
              value={exercise.category}
              onChange={handleChange}
              className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
              required
            >
              <option value="">Selecciona una categoría</option>
              {categories.map(category => (
                <option key={category.id} value={category.name}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-gray-300 mb-2">Tipo de Ejercicio</label>
            <select
              name="type"
              value={exercise.type}
              onChange={handleChange}
              className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
              required
            >
              <option value="compound">Compuesto</option>
              <option value="isolation">Aislado</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-300 mb-2">Equipo Necesario</label>
            <select
              name="equipment"
              value={exercise.equipment}
              onChange={handleChange}
              className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
              required
            >
              <option value="barra">Barra</option>
              <option value="mancuernas">Mancuernas</option>
              <option value="maquina">Máquina</option>
              <option value="polea">Polea</option>
              <option value="peso corporal">Peso Corporal</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-300 mb-2">Descripción</label>
            <textarea
              name="description"
              value={exercise.description}
              onChange={handleChange}
              className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
              rows="3"
              required
            />
          </div>

          <button
            type="submit"
            className="w-full bg-yellow-500 text-black font-bold py-3 rounded-lg hover:bg-yellow-600 transition-colors"
          >
            Agregar Ejercicio
          </button>
        </form>
      </div>
    </div>
  );
}
