import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FaGoogle } from 'react-icons/fa';
import './LoginPage.css';
import Header from './Header';

export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { login, loginWithGoogle } = useAuth();
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setError('');
            setLoading(true);
            const user = await login(email, password);

            // Redirigir según el rol del usuario
            if (user.role === 3) {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (error) {
            setError('Error al iniciar sesión: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

    async function handleGoogleLogin() {
        try {
            setLoading(true);
            await loginWithGoogle();
            navigate('/');
        } catch (error) {
            console.error('Error en login con Google:', error);
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="min-h-screen bg-black text-white">
            <Header />
            <div className="max-w-md mx-auto mt-8 p-6 bg-gray-900 rounded-lg shadow-lg">
                <h2 className="text-2xl font-bold text-yellow-400 mb-6 text-center">Iniciar Sesión</h2>
                {error && <div className="bg-red-500 text-white p-3 rounded mb-4">{error}</div>}
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label className="block text-yellow-400 mb-2">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700 focus:border-yellow-500 focus:outline-none"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-yellow-400 mb-2">Contraseña</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full p-2 rounded bg-gray-800 text-white border border-gray-700 focus:border-yellow-500 focus:outline-none"
                            required
                        />
                    </div>
                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black font-bold py-2 px-4 rounded hover:bg-yellow-600 disabled:opacity-50"
                    >
                        {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
                    </button>
                </form>

                <div className="relative my-6">
                    <div className="absolute inset-0 flex items-center">
                        <div className="w-full border-t border-gray-600"></div>
                    </div>
                    <div className="relative flex justify-center text-sm">
                        <span className="px-2 bg-gray-800 text-gray-400">o</span>
                    </div>
                </div>

                <button 
                    onClick={handleGoogleLogin}
                    disabled={loading}
                    className="w-full py-2 bg-gray-700 text-white font-semibold rounded hover:bg-gray-600 disabled:opacity-50 flex items-center justify-center gap-2"
                >
                    <FaGoogle className="text-yellow-400" />
                    Continuar con Google
                </button>

                <div className="mt-6 text-center text-sm text-gray-400">
                    ¿No tienes una cuenta?{' '}
                    <Link to="/register" className="text-yellow-400 hover:text-yellow-300">
                        Regístrate aquí
                    </Link>
                </div>
            </div>
        </div>
    );
}
