import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { db } from '../firebase';
import Header from './Header';
import { FaDumbbell, FaAppleAlt, FaShoppingBag, FaClipboardList, FaCrown } from 'react-icons/fa';
import { GiWeightLiftingUp } from 'react-icons/gi';
import Particles from 'react-tsparticles';
import { loadSlim } from 'tsparticles-slim';

export default function HomePage() {
  const { currentUser } = useAuth();
  const [userRoutine, setUserRoutine] = useState(null);
  const [loading, setLoading] = useState(true);

  const particlesInit = useCallback(async (engine) => {
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback(async (container) => {
    console.log("Particles container loaded", container);
  }, []);

  useEffect(() => {
    const fetchUserRoutine = async () => {
      if (currentUser) {
        try {
          const routinesRef = collection(db, 'routines');
          const q = query(routinesRef, where('userId', '==', currentUser.uid));
          const querySnapshot = await getDocs(q);

          if (!querySnapshot.empty) {
            setUserRoutine(querySnapshot.docs[0].data());
          }
        } catch (error) {
          console.error("Error fetching routine:", error);
        }
      }
      setLoading(false);
    };

    fetchUserRoutine();
  }, [currentUser]);

  if (loading) {
    return (
      <div className="min-h-screen bg-black text-white flex items-center justify-center">
        <div className="animate-pulse text-neon-gold text-2xl">Cargando...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen text-white p-4 pb-24 neon-luxury">
      <div className="neon-glow-luxury"></div>

      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          fullScreen: { enable: false },
          background: {
            color: {
              value: "transparent",
            },
          },
          fpsLimit: 60,
          particles: {
            color: {
              value: "#ffd700",
            },
            links: {
              color: "#ffd700",
              distance: 150,
              enable: false,
              opacity: 0.5,
              width: 1,
            },
            move: {
              enable: true,
              outModes: {
                default: "out",
              },
              random: true,
              speed: 1,
              straight: false,
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 20,
            },
            opacity: {
              value: 0.5,
            },
            shape: {
              type: "circle",
            },
            size: {
              value: { min: 1, max: 3 },
            },
          },
          detectRetina: true,
        }}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
          opacity: 0.3,
        }}
      />

      <Header />

      <div className="max-w-4xl mx-auto space-y-6">
        {/* Hero Section */}
        <div className="text-center py-12">
          <h1 className="text-4xl md:text-5xl font-bold mb-4 bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
            Bienvenido a Semental Fit
          </h1>
          <p className="text-xl text-gray-300">
            Tu camino hacia una vida más saludable comienza aquí
          </p>
        </div>

        {/* Quick Access Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Mi Rutina */}
          <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm hover:shadow-neon transition-all duration-300">
            <div className="flex items-center gap-3 mb-4">
              <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
                <GiWeightLiftingUp className="text-2xl text-black" />
              </div>
              <h2 className="text-xl font-bold bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
                Mi Rutina
              </h2>
            </div>
            {userRoutine ? (
              <div className="space-y-3">
                <h3 className="text-lg font-semibold text-neon-gold">{userRoutine.name}</h3>
                <p className="text-gray-300">{userRoutine.description}</p>
                <button className="w-full py-2 px-4 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300">
                  Ver Detalles
                </button>
              </div>
            ) : (
              <div className="text-center py-4">
                <p className="text-gray-400 mb-4">No tienes una rutina asignada</p>
                <button className="py-2 px-6 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300">
                  Crear Rutina
                </button>
              </div>
            )}
          </div>

          {/* Nutrición */}
          <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm hover:shadow-neon transition-all duration-300">
            <div className="flex items-center gap-3 mb-4">
              <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
                <FaAppleAlt className="text-2xl text-black" />
              </div>
              <h2 className="text-xl font-bold bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
                Nutrición
              </h2>
            </div>
            <p className="text-gray-300 mb-4">
              Planes nutricionales personalizados para alcanzar tus objetivos
            </p>
            <button className="w-full py-2 px-4 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300">
              Ver Planes
            </button>
          </div>

          {/* Shop */}
          <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm hover:shadow-neon transition-all duration-300">
            <div className="flex items-center gap-3 mb-4">
              <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
                <FaShoppingBag className="text-2xl text-black" />
              </div>
              <h2 className="text-xl font-bold bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
                Shop
              </h2>
            </div>
            <p className="text-gray-300 mb-4">
              Productos premium para complementar tu entrenamiento
            </p>
            <button className="w-full py-2 px-4 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300">
              Explorar
            </button>
          </div>
        </div>

        {/* Premium Banner */}
        <div className="bg-gradient-to-r from-neon-gold/20 to-neon-gold-dark/20 rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
          <div className="flex items-center gap-4">
            <div className="p-3 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
              <FaCrown className="text-3xl text-black" />
            </div>
            <div>
              <h3 className="text-xl font-bold text-neon-gold">¡Hazte Premium!</h3>
              <p className="text-gray-300">
                Accede a contenido exclusivo y planes personalizados
              </p>
            </div>
            <button className="ml-auto py-2 px-6 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300">
              Upgrade
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
