import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db } from '../firebase';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    categoryId: ''
  });

  const [exercises, setExercises] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    loadCategories();
    loadExercises();
  }, []);

  const loadCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'categories'));
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categorías:', error);
      alert('Error al cargar categorías: ' + error.message);
    }
  };

  const loadExercises = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'exercises'));
      const exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setExercises(exercisesList);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
      alert('Error al cargar ejercicios: ' + error.message);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) return;

    setIsLoading(true);
    try {
      await addDoc(collection(db, 'categories'), {
        name: newCategoryName.trim(),
        createdAt: new Date().toISOString()
      });
      setNewCategoryName('');
      loadCategories();
      alert('Categoría agregada correctamente');
    } catch (error) {
      console.error('Error al agregar categoría:', error);
      alert('Error al agregar categoría: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await addDoc(collection(db, 'exercises'), {
        ...exercise,
        createdAt: new Date().toISOString()
      });
      
      setExercise({
        name: '',
        categoryId: ''
      });
      setShowAddForm(false);
      loadExercises();
      alert('Ejercicio agregado correctamente');
    } catch (error) {
      console.error('Error al agregar ejercicio:', error);
      alert('Error al agregar ejercicio: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.id === categoryId);
    return category ? category.name : 'Sin categoría';
  };

  return (
    <div className="min-h-screen bg-black text-white p-4 pb-20">
      <h1 className="text-2xl font-bold text-yellow-500 mb-6">Panel de Control</h1>
      
      <div className="bg-gray-800 rounded-xl p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4">Gestionar Categorías</h2>
        <form onSubmit={handleAddCategory} className="flex gap-2 mb-4">
          <input
            type="text"
            value={newCategoryName}
            onChange={(e) => setNewCategoryName(e.target.value)}
            placeholder="Nueva categoría"
            className="flex-1 p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
            required
            disabled={isLoading}
          />
          <button
            type="submit"
            className="bg-yellow-500 text-black font-bold py-3 px-6 rounded-lg hover:bg-yellow-600 transition-colors disabled:opacity-50"
            disabled={isLoading}
          >
            Agregar Categoría
          </button>
        </form>
        <div className="space-y-2">
          {categories.length === 0 ? (
            <p className="text-gray-400">No hay categorías. Agrega una arriba.</p>
          ) : (
            categories.map(category => (
              <div key={category.id} className="bg-gray-700 p-3 rounded-lg">
                <span className="text-gray-300">{category.name}</span>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="bg-gray-800 rounded-xl p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Ejercicios Existentes</h2>
          <button
            onClick={() => setShowAddForm(!showAddForm)}
            className="bg-yellow-500 text-black font-bold py-2 px-4 rounded-lg hover:bg-yellow-600 transition-colors"
            disabled={isLoading}
          >
            {showAddForm ? 'Cancelar' : 'Agregar Ejercicio'}
          </button>
        </div>

        {showAddForm && (
          <form onSubmit={handleSubmit} className="mb-6 space-y-4">
            <div>
              <label className="block text-gray-300 mb-2">Nombre del Ejercicio</label>
              <input
                type="text"
                name="name"
                value={exercise.name}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
                required
                disabled={isLoading}
              />
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Categoría</label>
              <select
                name="categoryId"
                value={exercise.categoryId}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
                required
                disabled={isLoading}
              >
                <option value="">Selecciona una categoría</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>

            <button
              type="submit"
              className="w-full bg-yellow-500 text-black font-bold py-3 rounded-lg hover:bg-yellow-600 transition-colors disabled:opacity-50"
              disabled={isLoading}
            >
              {isLoading ? 'Agregando...' : 'Agregar Ejercicio'}
            </button>
          </form>
        )}

        <div className="space-y-2">
          {exercises.length === 0 ? (
            <p className="text-gray-400">No hay ejercicios. Agrega uno.</p>
          ) : (
            exercises.map(exercise => (
              <div key={exercise.id} className="bg-gray-700 p-3 rounded-lg">
                <h3 className="font-semibold text-yellow-500">{exercise.name}</h3>
                <p className="text-gray-300">Categoría: {getCategoryName(exercise.categoryId)}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
