    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadUser = async () => {
            try {
                const userData = await getUser();
                setUser(userData || {
                    name: 'Usuario',
                    level: 'Principiante',
                    experience: 0,
                    goals: []
                });
            } catch (err) {
                console.error('Error loading user:', err);
                setError('Error al cargar datos del usuario');
            } finally {
                setLoading(false);
            }
        };
