import React, { useState, useEffect, useRef } from 'react';
import { FaTrash, FaPlay, FaPause, FaFire, FaDumbbell, FaCrown, FaBolt } from 'react-icons/fa';
import { GiWeightLiftingUp, GiDiamondTrophy } from 'react-icons/gi';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';

const DAYS = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

const styles = `
  /* ... todos tus estilos anteriores ... (ya están incluidos aquí) */
  .neon-luxury {
    position: relative;
    overflow-x: hidden;
    background: radial-gradient(ellipse at center, #0a0a0a 0%, #000000 100%);
  }
  /* (resto de estilos igual que tu código anterior) */
`;

function TrainingPage() {
  const [today, setToday] = useState(new Date().getDay() - 1);
  const [selectedExercise, setSelectedExercise] = useState('');
  const [routine, setRoutine] = useState({});
  const [timers, setTimers] = useState({});

  const handleDayChange = (dayIndex) => setToday(dayIndex);

  const handleAddExercise = () => {
    if (!selectedExercise) return;
    const current = [...(routine[DAYS[today]] || [])];
    current.push({
      name: selectedExercise,
      series: [{ reps: '', weight: '', timer: 60 }],
    });
    setRoutine({ ...routine, [DAYS[today]]: current });
    setSelectedExercise('');
  };

  const handleRemoveExercise = (exIdx) => {
    const updated = [...(routine[DAYS[today]] || [])];
    updated.splice(exIdx, 1);
    setRoutine({ ...routine, [DAYS[today]]: updated });
  };

  const startTimer = (exIdx, setIdx, duration) => {
    const key = `${exIdx}-${setIdx}`;
    const newTimers = { ...timers };
    if (newTimers[key]?.interval) return;

    newTimers[key] = {
      running: true,
      timeLeft: duration,
      interval: setInterval(() => {
        setTimers((prev) => {
          const updated = { ...prev };
          if (updated[key].timeLeft <= 1) {
            clearInterval(updated[key].interval);
            updated[key] = { running: false, timeLeft: 0 };
          } else {
            updated[key] = {
              ...updated[key],
              timeLeft: updated[key].timeLeft - 1,
            };
          }
          return updated;
        });
      }, 1000),
    };
    setTimers(newTimers);
  };

  const pauseTimer = (exIdx, setIdx) => {
    const key = `${exIdx}-${setIdx}`;
    if (timers[key]?.interval) {
      clearInterval(timers[key].interval);
    }
    setTimers((prev) => ({
      ...prev,
      [key]: { ...prev[key], running: false },
    }));
  };

  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <div className="min-h-screen text-white p-4 pb-24 max-w-md mx-auto neon-luxury">
      <div className="neon-glow-luxury"></div>

      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          fullScreen: { enable: false },
          particles: {
            number: { value: 20 },
            size: { value: 2, random: true },
            color: { value: "#ffd700" },
            links: { enable: false },
            move: {
              enable: true,
              speed: 1,
              direction: "none",
              random: true,
              straight: false,
              outModes: { default: "out" },
            },
          },
        }}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
          opacity: 0.3,
        }}
      />

      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-yellow-400 flex items-center">
          <GiDiamondTrophy className="mr-2 text-yellow-300" />
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-200">
            POWER TRAINING
          </span>
        </h1>
        <div className="flex items-center bg-black/50 px-3 py-1 rounded-full border border-yellow-400/30">
          <FaCrown className="text-yellow-400 mr-1" />
          <span className="text-xs text-yellow-300">PREMIUM</span>
        </div>
      </div>

      <div className="flex justify-between mb-6 overflow-x-auto py-2 scrollbar-hide">
        {DAYS.map((d, i) => (
          <button
            key={d}
            onClick={() => handleDayChange(i)}
            className={`flex-1 min-w-[40px] mx-1 py-2 rounded-lg text-sm font-medium transition-all duration-300 flex flex-col items-center ${
              today === i
                ? 'neon-btn-active-luxury'
                : 'neon-btn-luxury text-gray-300'
            }`}
          >
            <span>{d.slice(0, 3)}</span>
            {today === i && <FaBolt className="text-xs mt-1 text-black animate-bounce" />}
          </button>
        ))}
      </div>

      <div className="neon-panel-luxury rounded-xl p-4 mb-6">
        <h2 className="text-xl font-bold mb-4 flex items-center">
          <GiWeightLiftingUp className="mr-2 text-yellow-400" />
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-200">
            NUEVO EJERCICIO
          </span>
        </h2>

        <input
          type="text"
          placeholder="Nombre del ejercicio"
          value={selectedExercise}
          onChange={(e) => setSelectedExercise(e.target.value)}
          className="w-full p-2 rounded-lg bg-black/30 text-white border border-yellow-400/30 mb-2"
        />

        <button
          onClick={handleAddExercise}
          disabled={!selectedExercise}
          className="neon-btn-add-luxury mt-4 px-4 py-3 rounded-lg font-bold transition-all transform hover:scale-105 active:scale-95 flex items-center justify-center w-full"
        >
          <FaFire className="mr-2 animate-pulse" />
          AGREGAR SET
        </button>
      </div>

      <div className="space-y-4">
        {(routine[DAYS[today]] || []).map((ex, exIdx) => (
          <div key={exIdx} className="neon-exercise-card-luxury p-4 rounded-xl">
            <div className="flex justify-between items-center mb-3">
              <h3 className="font-bold text-lg flex items-center">
                <FaDumbbell className="mr-2 text-yellow-400" />
                <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-200">
                  {ex.name}
                </span>
              </h3>
              <button
                onClick={() => handleRemoveExercise(exIdx)}
                className="text-red-400 hover:text-red-300 p-2 rounded-full hover:bg-red-900/30 transition-colors"
              >
                <FaTrash />
              </button>
            </div>

            <div className="space-y-3">
              {ex.series.map((set, setIdx) => {
                const key = `${exIdx}-${setIdx}`;
                const timerState = timers[key] || { running: false, timeLeft: set.timer };

                return (
                  <div key={setIdx} className="neon-set-card-luxury p-3 rounded-lg flex items-center justify-between">
                    <span>Reps: {set.reps || '--'} | Peso: {set.weight || '--'}kg | Descanso: {timerState.timeLeft}s</span>
                    <button
                      type="button"
                      className={`p-2 rounded-full ml-2 ${
                        timerState.running
                          ? 'timer-active-luxury bg-yellow-400 text-black'
                          : 'bg-gray-700 text-yellow-400'
                      }`}
                      onClick={() =>
                        timerState.running
                          ? pauseTimer(exIdx, setIdx)
                          : startTimer(exIdx, setIdx, Number(set.timer) || 0)
                      }
                    >
                      {timerState.running ? <FaPause /> : <FaPlay />}
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>

      <style>{styles}</style>
    </div>
  );
}

export default TrainingPage;
