import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { getUserRoutine, saveUserRoutine, getUserProfile, recalculateUserStats } from '../services/database';

const TrainingPage = () => {
    const { user } = useAuth();
    const [userRoutine, setUserRoutine] = useState(null);
    const [selectedDay, setSelectedDay] = useState('lunes');
    const [selectedExercise, setSelectedExercise] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isAddingExercise, setIsAddingExercise] = useState(false);
    const [profile, setProfile] = useState(null);
    const [error, setError] = useState(null);
    const [isRecalculating, setIsRecalculating] = useState(false);

    useEffect(() => {
        const loadUserData = async () => {
            if (user) {
                try {
                    const routine = await getUserRoutine(user.uid);
                    setUserRoutine(routine);
                    const userProfile = await getUserProfile(user.uid);
                    setProfile(userProfile);
                } catch (error) {
                    console.error('Error loading user data:', error);
                    setError('Error al cargar los datos del usuario');
                }
            }
        };
        loadUserData();
    }, [user]);

    const handleRecalculateStats = async () => {
        try {
            setIsRecalculating(true);
            setError(null);
            await recalculateUserStats(user.uid);
            const updatedProfile = await getUserProfile(user.uid);
            setProfile(updatedProfile);
            alert('Estadísticas recalculadas correctamente');
        } catch (error) {
            console.error('Error al recalcular estadísticas:', error);
            setError('Error al recalcular estadísticas');
        } finally {
            setIsRecalculating(false);
        }
    };

    const handleUpdateExercise = async (exerciseId, updatedExercise) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            const exerciseIndex = updatedRoutine[selectedDay].findIndex(ex => ex.id === exerciseId);
            
            if (exerciseIndex !== -1) {
                updatedRoutine[selectedDay][exerciseIndex] = updatedExercise;
                await saveUserRoutine(user.uid, updatedRoutine);
                setUserRoutine(updatedRoutine);
                await recalculateUserStats(user.uid);
                const updatedProfile = await getUserProfile(user.uid);
                setProfile(updatedProfile);
            }
        } catch (error) {
            console.error('Error al actualizar ejercicio:', error);
            setError('Error al actualizar ejercicio');
        }
    };

    const handleAddExercise = async (exercise) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            if (!updatedRoutine[selectedDay]) {
                updatedRoutine[selectedDay] = [];
            }

            const newExercise = {
                ...exercise,
                id: Date.now().toString(),
                series: [{ reps: 12, weight: 0 }]
            };

            updatedRoutine[selectedDay].push(newExercise);
            await saveUserRoutine(user.uid, updatedRoutine);
            setUserRoutine(updatedRoutine);
            setIsAddingExercise(false);
        } catch (error) {
            console.error('Error al agregar ejercicio:', error);
            setError('Error al agregar ejercicio');
        }
    };

    const handleDeleteExercise = async (exerciseId) => {
        try {
            if (!user || !userRoutine) return;

            const updatedRoutine = { ...userRoutine };
            updatedRoutine[selectedDay] = updatedRoutine[selectedDay].filter(ex => ex.id !== exerciseId);
            await saveUserRoutine(user.uid, updatedRoutine);
            setUserRoutine(updatedRoutine);
            await recalculateUserStats(user.uid);
            const updatedProfile = await getUserProfile(user.uid);
            setProfile(updatedProfile);
        } catch (error) {
            console.error('Error al eliminar ejercicio:', error);
            setError('Error al eliminar ejercicio');
        }
    };

    return (
        <div className="training-page">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                <h2>Entrenamiento</h2>
                <button 
                    onClick={handleRecalculateStats}
                    disabled={isRecalculating}
                    style={{
                        backgroundColor: isRecalculating ? '#cccccc' : '#4CAF50',
                        color: 'white',
                        padding: '10px 20px',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: isRecalculating ? 'not-allowed' : 'pointer',
                        fontSize: '14px'
                    }}
                >
                    {isRecalculating ? 'Recalculando...' : 'Recalcular Estadísticas'}
                </button>
            </div>
            {error && (
                <div style={{
                    color: '#ff0000',
                    margin: '10px 0',
                    padding: '10px',
                    backgroundColor: '#ffebee',
                    borderRadius: '5px'
                }}>
                    {error}
                </div>
            )}

            <div className="days-selector">
                {['lunes', 'martes', 'miércoles', 'jueves', 'viernes', 'sábado', 'domingo'].map(day => (
                    <button
                        key={day}
                        onClick={() => setSelectedDay(day)}
                        style={{
                            backgroundColor: selectedDay === day ? '#4CAF50' : '#333',
                            color: 'white',
                            padding: '10px 20px',
                            border: 'none',
                            borderRadius: '5px',
                            margin: '0 5px',
                            cursor: 'pointer'
                        }}
                    >
                        {day.charAt(0).toUpperCase() + day.slice(1)}
                    </button>
                ))}
            </div>

            <div className="exercises-container">
                {userRoutine && userRoutine[selectedDay] && userRoutine[selectedDay].map(exercise => (
                    <div key={exercise.id} className="exercise-card">
                        <h3>{exercise.name}</h3>
                        <div className="series-container">
                            {exercise.series.map((serie, index) => (
                                <div key={index} className="serie">
                                    <span>Serie {index + 1}:</span>
                                    <input
                                        type="number"
                                        value={serie.reps}
                                        onChange={(e) => {
                                            const updatedExercise = { ...exercise };
                                            updatedExercise.series[index].reps = Number(e.target.value);
                                            handleUpdateExercise(exercise.id, updatedExercise);
                                        }}
                                        min="0"
                                    />
                                    <span>reps</span>
                                    <input
                                        type="number"
                                        value={serie.weight}
                                        onChange={(e) => {
                                            const updatedExercise = { ...exercise };
                                            updatedExercise.series[index].weight = Number(e.target.value);
                                            handleUpdateExercise(exercise.id, updatedExercise);
                                        }}
                                        min="0"
                                    />
                                    <span>kg</span>
                                </div>
                            ))}
                        </div>
                        <button
                            onClick={() => handleDeleteExercise(exercise.id)}
                            style={{
                                backgroundColor: '#ff4444',
                                color: 'white',
                                padding: '5px 10px',
                                border: 'none',
                                borderRadius: '3px',
                                cursor: 'pointer',
                                marginTop: '10px'
                            }}
                        >
                            Eliminar
                        </button>
                    </div>
                ))}
            </div>

            <button
                onClick={() => setIsAddingExercise(true)}
                style={{
                    backgroundColor: '#4CAF50',
                    color: 'white',
                    padding: '10px 20px',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginTop: '20px'
                }}
            >
                Agregar Ejercicio
            </button>
        </div>
    );
};

export default TrainingPage;
