import { 
    collection, 
    addDoc, 
    getDocs, 
    doc, 
    getDoc, 
    updateDoc,
    query,
    where,
    arrayUnion,
    setDoc,
    writeBatch
} from 'firebase/firestore';
import { db } from '../firebase';

// Funciones para usuarios
export const createUser = async (userData) => {
    try {
        const docRef = await addDoc(collection(db, "users"), {
            ...userData,
            level: 1,
            xp: 0,
            createdAt: new Date(),
            lastLogin: new Date()
        });
        return docRef.id;
    } catch (error) {
        console.error("Error al crear usuario:", error);
        throw error;
    }
};

export const getUser = async (userId) => {
    try {
        const docRef = doc(db, "users", userId);
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
            return { id: docSnap.id, ...docSnap.data() };
        }
        return null;
    } catch (error) {
        console.error("Error al obtener usuario:", error);
        throw error;
    }
};

// Funciones para rutinas
export const saveWorkout = async (userId, workoutData) => {
    try {
        const docRef = await addDoc(collection(db, "workouts"), {
            userId,
            ...workoutData,
            completedAt: new Date()
        });
        return docRef.id;
    } catch (error) {
        console.error("Error al guardar rutina:", error);
        throw error;
    }
};

export const getUserWorkouts = async (userId) => {
    try {
        const q = query(collection(db, "workouts"), where("userId", "==", userId));
        const querySnapshot = await getDocs(q);
        return querySnapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data()
        }));
    } catch (error) {
        console.error("Error al obtener rutinas:", error);
        throw error;
    }
};

// Funciones para progreso
export const updateProgress = async (userId, progressData) => {
    try {
        const userRef = doc(db, "users", userId);
        await updateDoc(userRef, {
            xp: progressData.xp,
            level: progressData.level,
            lastUpdated: new Date()
        });
    } catch (error) {
        console.error("Error al actualizar progreso:", error);
        throw error;
    }
};

// Función para obtener todos los usuarios
export const getUsers = async () => {
    try {
        const querySnapshot = await getDocs(collection(db, 'users'));
        return querySnapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data()
        }));
    } catch (error) {
        console.error("Error al obtener usuarios:", error);
        throw error;
    }
};

// Funciones para ejercicios
export const getAllExercises = async () => {
    try {
        const querySnapshot = await getDocs(collection(db, "exercises"));
        return querySnapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data()
        }));
    } catch (error) {
        console.error("Error al obtener ejercicios:", error);
        throw error;
    }
};

// Funciones para rutinas personalizadas
export const createUserRoutine = async (userId, day, exercises) => {
    try {
        const routineRef = doc(db, "user_routines", userId);
        await setDoc(routineRef, {
            [day]: exercises
        }, { merge: true });
        return true;
    } catch (error) {
        console.error("Error al crear rutina:", error);
        throw error;
    }
};

export const getUserRoutine = async (userId) => {
    try {
        const routineRef = doc(db, "user_routines", userId);
        const routineSnap = await getDoc(routineRef);
        if (routineSnap.exists()) {
            return routineSnap.data();
        }
        return null;
    } catch (error) {
        console.error("Error al obtener rutina:", error);
        throw error;
    }
};

export const updateExerciseInRoutine = async (userId, day, exerciseId, newData) => {
    try {
        const routineRef = doc(db, "user_routines", userId);
        const routineSnap = await getDoc(routineRef);
        if (routineSnap.exists()) {
            const routine = routineSnap.data();
            const dayExercises = routine[day] || [];
            const updatedExercises = dayExercises.map(ex => 
                ex.id === exerciseId ? { ...ex, ...newData } : ex
            );
            await updateDoc(routineRef, {
                [day]: updatedExercises
            });
            return true;
        }
        return false;
    } catch (error) {
        console.error("Error al actualizar ejercicio:", error);
        throw error;
    }
};

