import React, { useState, useEffect, useRef } from 'react';
import { getDocs, collection } from 'firebase/firestore';
import { db } from '../firebase';
import Header from './Header';
import ParticlesBackground from './ParticlesBackground';
import '../styles/global.css';

const DAYS = ['Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado', 'Domingo'];

function getTodayIndex() {
  const jsDay = new Date().getDay(); // 0=Domingo, 1=Lunes...
  return jsDay === 0 ? 6 : jsDay - 1;
}

const defaultSet = () => ({ weight: '', reps: '', timer: 60, completed: false });

function useSetTimers(routine, today) {
  // Estructura: timers[exIdx][setIdx] = { running, timeLeft, intervalId }
  const [timers, setTimers] = useState({});
  const audioRef = useRef(null);

  useEffect(() => {
    // Reset timers si cambia el d√≠a o la rutina
    setTimers({});
  }, [routine, today]);

  const startTimer = (exIdx, setIdx, initial) => {
    setTimers(prev => {
      const key = `${exIdx}-${setIdx}`;
      if (prev[key]?.running) return prev;
      const timeLeft = prev[key]?.timeLeft ?? initial;
      let lastTick = Date.now();
      const intervalId = setInterval(() => {
        setTimers(current => {
          const now = current[key]?.timeLeft ?? initial;
          // Calcular el tiempo real transcurrido para evitar saltos dobles
          const nowTick = Date.now();
          const elapsed = Math.floor((nowTick - lastTick) / 1000);
          if (elapsed >= 1) {
            lastTick = nowTick;
            if (now > 0) {
              return { ...current, [key]: { ...current[key], timeLeft: now - 1, running: true, intervalId } };
            } else {
              clearInterval(intervalId);
              if (window.navigator.vibrate) {
                window.navigator.vibrate(200);
              }
              return { ...current, [key]: { ...current[key], timeLeft: 0, running: false, intervalId: null } };
            }
          }
          return current;
        });
      }, 250); // Intervalo m√°s corto para mayor precisi√≥n
      return { ...prev, [key]: { running: true, timeLeft, intervalId } };
    });
  };

  const pauseTimer = (exIdx, setIdx) => {
    setTimers(prev => {
      const key = `${exIdx}-${setIdx}`;
      if (prev[key]?.intervalId) clearInterval(prev[key].intervalId);
      return { ...prev, [key]: { ...prev[key], running: false, intervalId: null } };
    });
  };

  const resetTimer = (exIdx, setIdx, value) => {
    setTimers(prev => {
      const key = `${exIdx}-${setIdx}`;
      if (prev[key]?.intervalId) clearInterval(prev[key].intervalId);
      return { ...prev, [key]: { running: false, timeLeft: value, intervalId: null } };
    });
  };

  return { timers, startTimer, pauseTimer, resetTimer, audioRef };
}

export default function TrainingPage() {
  const [categories, setCategories] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [filteredExercises, setFilteredExercises] = useState([]);
  const [selectedExercise, setSelectedExercise] = useState('');
  const [routine, setRoutine] = useState({});
  const [setsCount, setSetsCount] = useState(1);
  const [today, setToday] = useState(getTodayIndex());

  // Timers para sets
  const { timers, startTimer, pauseTimer, resetTimer, audioRef } = useSetTimers(routine, today);

  // Cargar categor√≠as y ejercicios
  useEffect(() => {
    async function fetchData() {
      const catSnap = await getDocs(collection(db, 'categories'));
      const cats = catSnap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCategories(cats);
      const exSnap = await getDocs(collection(db, 'exercises'));
      const exs = exSnap.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setExercises(exs);
    }
    fetchData();
  }, []);

  // Filtrar ejercicios por categor√≠a
  useEffect(() => {
    if (selectedCategory) {
      setFilteredExercises(exercises.filter(e => e.categoryId === selectedCategory));
    } else {
      setFilteredExercises([]);
    }
    setSelectedExercise('');
  }, [selectedCategory, exercises]);

  // Cargar rutina del d√≠a desde localStorage
  useEffect(() => {
    const saved = localStorage.getItem('training_routine');
    if (saved) setRoutine(JSON.parse(saved));
  }, []);

  // Guardar rutina en localStorage
  useEffect(() => {
    localStorage.setItem('training_routine', JSON.stringify(routine));
  }, [routine]);

  // Agrupar ejercicios por categor√≠a para el select
  const exercisesByCategory = categories.reduce((acc, cat) => {
    acc[cat.id] = exercises.filter(ex => ex.categoryId === cat.id);
    return acc;
  }, {});

  const handleAddExercise = () => {
    if (!selectedExercise) return;
    const ex = exercises.find(e => e.id === selectedExercise);
    const newSets = Array.from({ length: setsCount }, defaultSet);
    setRoutine(prev => {
      const day = DAYS[today];
      const prevDay = prev[day] || [];
      return {
        ...prev,
        [day]: [
          ...prevDay,
          {
            id: ex.id,
            name: ex.name,
            category: categories.find(c => c.id === ex.categoryId)?.name || '',
            series: newSets
          }
        ]
      };
    });
    setSelectedExercise('');
    setSetsCount(1);
  };

  const handleSetChange = (exIdx, setIdx, field, value) => {
    const day = DAYS[today];
    setRoutine(prev => {
      const updated = [...(prev[day] || [])];
      const ex = { ...updated[exIdx] };
      const series = [...ex.series];
      series[setIdx] = { ...series[setIdx], [field]: value };
      ex.series = series;
      updated[exIdx] = ex;
      return { ...prev, [day]: updated };
    });
    if (field === 'timer') resetTimer(exIdx, setIdx, Number(value));
  };

  const handleRemoveExercise = (exIdx) => {
    const day = DAYS[today];
    setRoutine(prev => {
      const updated = [...(prev[day] || [])];
      updated.splice(exIdx, 1);
      return { ...prev, [day]: updated };
    });
  };

  const handleDayChange = (idx) => setToday(idx);

  return (
    <div className="min-h-screen text-white p-4 pb-24 neon-luxury">
      <div className="neon-glow-luxury"></div>
      <ParticlesBackground />
      <Header />

      <div className="max-w-4xl mx-auto space-y-6">
        {/* Selector de d√≠as */}
        <div className="bg-black/40 backdrop-blur-sm rounded-xl p-4 border border-neon-gold/30 shadow-neon-sm">
          <div className="flex justify-between gap-2">
            {DAYS.map((d, i) => (
              <button
                key={d}
                onClick={() => handleDayChange(i)}
                className={`flex-1 px-2 py-2 rounded-lg text-sm font-medium transition-all duration-300 ${
                  today === i 
                    ? 'bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black shadow-neon' 
                    : 'bg-black/30 text-gray-300 border border-neon-gold/20 hover:border-neon-gold/40'
                }`}
              >
                {d.slice(0,3)}
              </button>
            ))}
          </div>
        </div>

        {/* Panel de agregar ejercicio */}
        <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
          <div className="space-y-4">
            <select
              value={selectedCategory}
              onChange={e => setSelectedCategory(e.target.value)}
              className="w-full px-4 py-2 rounded-lg bg-black/30 border border-neon-gold/20 text-white neon-input-luxury"
            >
              <option value="">Selecciona categor√≠a</option>
              {categories.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
            </select>

            <select
              value={selectedExercise}
              onChange={e => setSelectedExercise(e.target.value)}
              className="w-full px-4 py-2 rounded-lg bg-black/30 border border-neon-gold/20 text-white neon-input-luxury"
              disabled={!selectedCategory}
            >
              <option value="">Selecciona ejercicio</option>
              {categories.map(cat => (
                exercisesByCategory[cat.id]?.length > 0 && (
                  <optgroup key={cat.id} label={cat.name}>
                    {exercisesByCategory[cat.id].map(ex => (
                      <option key={ex.id} value={ex.id}>{ex.name} ({ex.difficulty})</option>
                    ))}
                  </optgroup>
                )
              ))}
            </select>

            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <span className="text-neon-gold">Sets:</span>
                <input
                  type="number"
                  min={1}
                  max={10}
                  value={setsCount}
                  onChange={e => setSetsCount(Number(e.target.value))}
                  className="w-16 px-3 py-2 rounded-lg bg-black/30 border border-neon-gold/20 text-white neon-input-luxury"
                />
              </div>
              <button
                onClick={handleAddExercise}
                className="flex-1 px-4 py-2 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={!selectedExercise}
              >
                Agregar Ejercicio
              </button>
            </div>
          </div>
        </div>

        {/* Lista de ejercicios */}
        <div className="space-y-4">
          {(routine[DAYS[today]] || []).map((ex, exIdx) => (
            <div key={exIdx} className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-bold text-neon-gold">{ex.name}</h3>
                <button 
                  onClick={() => handleRemoveExercise(exIdx)} 
                  className="p-2 text-red-400 hover:text-red-300 transition-colors duration-300"
                >
                  üóëÔ∏è
                </button>
              </div>

              <div className="space-y-3">
                {ex.series.map((set, setIdx) => {
                  const key = `${exIdx}-${setIdx}`;
                  const timerState = timers[key] || { running: false, timeLeft: set.timer };
                  return (
                    <div key={setIdx} className="flex items-center gap-4 p-3 bg-black/20 rounded-lg border border-neon-gold/10">
                      <span className="text-neon-gold font-medium">Set {setIdx + 1}</span>
                      <input
                        type="number"
                        placeholder="Kg"
                        value={set.weight}
                        onChange={e => handleSetChange(exIdx, setIdx, 'weight', e.target.value)}
                        className="w-20 px-3 py-2 rounded-lg bg-black/30 border border-neon-gold/20 text-white neon-input-luxury"
                      />
                      <input
                        type="number"
                        placeholder="Reps"
                        value={set.reps}
                        onChange={e => handleSetChange(exIdx, setIdx, 'reps', e.target.value)}
                        className="w-20 px-3 py-2 rounded-lg bg-black/30 border border-neon-gold/20 text-white neon-input-luxury"
                      />
                      <div className="flex items-center gap-2">
                        <input
                          type="number"
                          placeholder="Seg"
                          value={set.timer}
                          onChange={e => handleSetChange(exIdx, setIdx, 'timer', e.target.value)}
                          className="w-20 px-3 py-2 rounded-lg bg-black/30 border border-neon-gold/20 text-white neon-input-luxury"
                        />
                        <button
                          onClick={() => timerState.running ? pauseTimer(exIdx, setIdx) : startTimer(exIdx, setIdx, set.timer)}
                          className={`px-4 py-2 rounded-lg ${
                            timerState.running
                              ? 'bg-red-500/20 text-red-400 border border-red-500/30'
                              : 'bg-neon-gold/20 text-neon-gold border border-neon-gold/30'
                          } transition-all duration-300`}
                        >
                          {timerState.running ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
                        </button>
                        <span className={`px-3 py-2 rounded-lg ${
                          timerState.running ? 'bg-red-500/20 text-red-400 border border-red-500/30' : 'bg-black/30 text-white border border-neon-gold/20'
                        }`}>
                          {timerState.timeLeft}s
                        </span>
                      </div>
                      <input
                        type="checkbox"
                        checked={set.completed}
                        onChange={e => handleSetChange(exIdx, setIdx, 'completed', e.target.checked)}
                        className="w-5 h-5 rounded border-neon-gold/30 text-neon-gold focus:ring-neon-gold"
                      />
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
