import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FaGoogle } from 'react-icons/fa';
import './LoginPage.css';

export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { login, loginWithGoogle } = useAuth();
    const navigate = useNavigate();

    async function handleSubmit(e) {
        e.preventDefault();
        try {
            setError('');
            setLoading(true);
            await login(email, password);
            navigate('/');
        } catch (error) {
            console.error('Error en login:', error);
            switch (error.code) {
                case 'auth/invalid-email':
                    setError('El correo electrónico no es válido');
                    break;
                case 'auth/user-disabled':
                    setError('Esta cuenta ha sido deshabilitada');
                    break;
                case 'auth/user-not-found':
                    setError('No existe una cuenta con este correo electrónico');
                    break;
                case 'auth/wrong-password':
                    setError('Contraseña incorrecta');
                    break;
                case 'auth/too-many-requests':
                    setError('Demasiados intentos fallidos. Por favor, intente más tarde');
                    break;
                default:
                    setError('Error al iniciar sesión. Por favor, intente nuevamente');
            }
        } finally {
            setLoading(false);
        }
    }

    async function handleGoogleLogin() {
        try {
            setError('');
            setLoading(true);
            await loginWithGoogle();
            navigate('/');
        } catch (error) {
            console.error('Error en login con Google:', error);
            switch (error.code) {
                case 'auth/popup-closed-by-user':
                    setError('La ventana de inicio de sesión fue cerrada');
                    break;
                case 'auth/popup-blocked':
                    setError('La ventana emergente fue bloqueada. Por favor, permita las ventanas emergentes');
                    break;
                case 'auth/cancelled-popup-request':
                    setError('La solicitud de inicio de sesión fue cancelada');
                    break;
                case 'auth/account-exists-with-different-credential':
                    setError('Ya existe una cuenta con este correo electrónico usando otro método de inicio de sesión');
                    break;
                default:
                    setError('Error al iniciar sesión con Google. Por favor, intente nuevamente');
            }
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Iniciar Sesión</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Correo Electrónico</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            disabled={loading}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Contraseña</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            disabled={loading}
                        />
                    </div>
                    <button 
                        type="submit" 
                        className="login-button"
                        disabled={loading}
                    >
                        {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
                    </button>
                </form>
                <div className="divider">
                    <span>o</span>
                </div>
                <button 
                    onClick={handleGoogleLogin}
                    className="google-button"
                    disabled={loading}
                >
                    <FaGoogle className="google-icon" />
                    Continuar con Google
                </button>
                <div className="register-link">
                    ¿No tienes una cuenta? <Link to="/register">Regístrate aquí</Link>
                </div>
            </div>
        </div>
    );
}
