import React, { useState } from 'react';
import { FaShoppingCart, FaPlus, FaMinus, FaTrash } from 'react-icons/fa';

const ShopPage = () => {
    const [cart, setCart] = useState([]);
    const [showCart, setShowCart] = useState(false);

    const products = [
        {
            id: 1,
            name: "Remera SementalFit",
            price: 15000,
            image: "https://via.placeholder.com/150",
            description: "Remera de alta calidad con logo SementalFit"
        },
        {
            id: 2,
            name: "Buzo SementalFit",
            price: 25000,
            image: "https://via.placeholder.com/150",
            description: "Buzo con capucha y logo bordado"
        },
        {
            id: 3,
            name: "Proteína Whey Gold Standard",
            price: 35000,
            image: "https://via.placeholder.com/150",
            description: "Suplemento de proteína de alta calidad"
        }
    ];

    const addToCart = (product) => {
        const existingItem = cart.find(item => item.id === product.id);
        if (existingItem) {
            setCart(cart.map(item =>
                item.id === product.id
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            ));
        } else {
            setCart([...cart, { ...product, quantity: 1 }]);
        }
    };

    const removeFromCart = (productId) => {
        setCart(cart.filter(item => item.id !== productId));
    };

    const updateQuantity = (productId, change) => {
        setCart(cart.map(item => {
            if (item.id === productId) {
                const newQuantity = item.quantity + change;
                return newQuantity > 0 ? { ...item, quantity: newQuantity } : item;
            }
            return item;
        }));
    };

    const getTotal = () => {
        return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
    };

    return (
        <div className="min-h-screen bg-black text-white p-4 pb-20">
            <div className="max-w-sm mx-auto">
                <div className="flex justify-between items-center mb-6">
                    <h1 className="text-2xl font-bold text-yellow-500">Tienda</h1>
                    <button
                        onClick={() => setShowCart(!showCart)}
                        className="relative p-2 text-yellow-500"
                    >
                        <FaShoppingCart className="text-2xl" />
                        {cart.length > 0 && (
                            <span className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">
                                {cart.reduce((total, item) => total + item.quantity, 0)}
                            </span>
                        )}
                    </button>
                </div>

                {showCart ? (
                    <div className="space-y-4">
                        <h2 className="text-xl font-semibold text-yellow-500 mb-4">Carrito de Compras</h2>
                        {cart.length === 0 ? (
                            <p className="text-gray-400 text-center">El carrito está vacío</p>
                        ) : (
                            <>
                                {cart.map(item => (
                                    <div key={item.id} className="bg-gray-800 rounded-xl p-4">
                                        <div className="flex justify-between items-start">
                                            <div>
                                                <h3 className="text-yellow-500 font-semibold">{item.name}</h3>
                                                <p className="text-gray-400">${item.price.toLocaleString('es-AR')}</p>
                                            </div>
                                            <button
                                                onClick={() => removeFromCart(item.id)}
                                                className="text-red-500"
                                            >
                                                <FaTrash />
                                            </button>
                                        </div>
                                        <div className="flex items-center justify-between mt-2">
                                            <div className="flex items-center space-x-2">
                                                <button
                                                    onClick={() => updateQuantity(item.id, -1)}
                                                    className="text-yellow-500"
                                                >
                                                    <FaMinus />
                                                </button>
                                                <span className="text-gray-300">{item.quantity}</span>
                                                <button
                                                    onClick={() => updateQuantity(item.id, 1)}
                                                    className="text-yellow-500"
                                                >
                                                    <FaPlus />
                                                </button>
                                            </div>
                                            <span className="text-yellow-500">
                                                ${(item.price * item.quantity).toLocaleString('es-AR')}
                                            </span>
                                        </div>
                                    </div>
                                ))}
                                <div className="bg-gray-800 rounded-xl p-4">
                                    <div className="flex justify-between items-center">
                                        <span className="text-gray-300">Total:</span>
                                        <span className="text-yellow-500 text-xl font-bold">
                                            ${getTotal().toLocaleString('es-AR')}
                                        </span>
                                    </div>
                                    <button className="w-full mt-4 bg-yellow-500 text-black font-bold py-2 px-4 rounded-lg">
                                        Finalizar Compra
                                    </button>
                                </div>
                            </>
                        )}
                    </div>
                ) : (
                    <div className="space-y-4">
                        {products.map(product => (
                            <div key={product.id} className="bg-gray-800 rounded-xl p-4">
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="w-full h-40 object-cover rounded-lg mb-4"
                                />
                                <h3 className="text-yellow-500 font-semibold">{product.name}</h3>
                                <p className="text-gray-400 mb-2">{product.description}</p>
                                <div className="flex justify-between items-center">
                                    <span className="text-yellow-500 font-bold">
                                        ${product.price.toLocaleString('es-AR')}
                                    </span>
                                    <button
                                        onClick={() => addToCart(product)}
                                        className="bg-yellow-500 text-black font-bold py-2 px-4 rounded-lg"
                                    >
                                        Comprar
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default ShopPage;
