import React, { useState, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import Header from './Header';
import { FaAppleAlt, FaUtensils, FaChartLine, FaClipboardList } from 'react-icons/fa';
import Particles from 'react-tsparticles';
import { loadSlim } from 'tsparticles-slim';

export default function NutritionPage() {
  const { currentUser } = useAuth();
  const [selectedPlan, setSelectedPlan] = useState(null);

  const particlesInit = useCallback(async (engine) => {
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback(async (container) => {
    console.log("Particles container loaded", container);
  }, []);

  const nutritionPlans = [
    {
      id: 1,
      name: "Plan Básico",
      description: "Plan nutricional para principiantes",
      price: "Gratis",
      features: [
        "Calculadora de calorías",
        "Recetas básicas",
        "Guía de alimentación"
      ]
    },
    {
      id: 2,
      name: "Plan Premium",
      description: "Plan nutricional personalizado",
      price: "$29.99/mes",
      features: [
        "Plan personalizado",
        "Seguimiento de progreso",
        "Recetas exclusivas",
        "Asesoría nutricional"
      ]
    }
  ];

  return (
    <div className="min-h-screen text-white p-4 pb-24 neon-luxury">
      <div className="neon-glow-luxury"></div>

      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          fullScreen: { enable: false },
          background: {
            color: {
              value: "transparent",
            },
          },
          fpsLimit: 60,
          particles: {
            color: {
              value: "#ffd700",
            },
            links: {
              color: "#ffd700",
              distance: 150,
              enable: false,
              opacity: 0.5,
              width: 1,
            },
            move: {
              enable: true,
              outModes: {
                default: "out",
              },
              random: true,
              speed: 1,
              straight: false,
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 20,
            },
            opacity: {
              value: 0.5,
            },
            shape: {
              type: "circle",
            },
            size: {
              value: { min: 1, max: 3 },
            },
          },
          detectRetina: true,
        }}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
          opacity: 0.3,
        }}
      />

      <Header />

      <div className="max-w-4xl mx-auto space-y-8">
        {/* Hero Section */}
        <div className="text-center py-12">
          <div className="inline-block p-3 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-full shadow-neon-sm mb-4">
            <FaAppleAlt className="text-4xl text-black" />
          </div>
          <h1 className="text-4xl md:text-5xl font-bold mb-4 bg-gradient-to-r from-neon-gold via-yellow-300 to-neon-gold bg-clip-text text-transparent">
            Nutrición
          </h1>
          <p className="text-xl text-gray-300">
            Alimenta tu cuerpo, potencia tu rendimiento
          </p>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
            <div className="flex items-center gap-3 mb-2">
              <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
                <FaUtensils className="text-xl text-black" />
              </div>
              <h3 className="text-lg font-semibold text-neon-gold">Calorías Diarias</h3>
            </div>
            <p className="text-3xl font-bold text-white">2,500</p>
          </div>

          <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
            <div className="flex items-center gap-3 mb-2">
              <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
                <FaChartLine className="text-xl text-black" />
              </div>
              <h3 className="text-lg font-semibold text-neon-gold">Progreso</h3>
            </div>
            <p className="text-3xl font-bold text-white">75%</p>
          </div>

          <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
            <div className="flex items-center gap-3 mb-2">
              <div className="p-2 bg-gradient-to-br from-neon-gold to-neon-gold-dark rounded-lg shadow-neon-sm">
                <FaClipboardList className="text-xl text-black" />
              </div>
              <h3 className="text-lg font-semibold text-neon-gold">Recetas</h3>
            </div>
            <p className="text-3xl font-bold text-white">24</p>
          </div>
        </div>

        {/* Nutrition Plans */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {nutritionPlans.map((plan) => (
            <div
              key={plan.id}
              className={`bg-black/40 backdrop-blur-sm rounded-xl p-6 border ${
                selectedPlan === plan.id
                  ? 'border-neon-gold shadow-neon'
                  : 'border-neon-gold/30 shadow-neon-sm'
              } hover:shadow-neon transition-all duration-300`}
              onClick={() => setSelectedPlan(plan.id)}
            >
              <h3 className="text-xl font-bold text-neon-gold mb-2">{plan.name}</h3>
              <p className="text-gray-300 mb-4">{plan.description}</p>
              <div className="text-2xl font-bold text-white mb-4">{plan.price}</div>
              <ul className="space-y-2 mb-6">
                {plan.features.map((feature, index) => (
                  <li key={index} className="flex items-center gap-2 text-gray-300">
                    <span className="text-neon-gold">•</span>
                    {feature}
                  </li>
                ))}
              </ul>
              <button className="w-full py-2 px-4 bg-gradient-to-r from-neon-gold to-neon-gold-dark text-black font-semibold rounded-lg shadow-neon-sm hover:shadow-neon transition-all duration-300">
                Seleccionar Plan
              </button>
            </div>
          ))}
        </div>

        {/* Meal Plan Preview */}
        <div className="bg-black/40 backdrop-blur-sm rounded-xl p-6 border border-neon-gold/30 shadow-neon-sm">
          <h3 className="text-xl font-bold text-neon-gold mb-4">Plan de Comidas</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-black/20 rounded-lg p-4 border border-neon-gold/20">
              <h4 className="text-lg font-semibold text-white mb-2">Desayuno</h4>
              <p className="text-gray-300">Avena con frutas y proteína</p>
            </div>
            <div className="bg-black/20 rounded-lg p-4 border border-neon-gold/20">
              <h4 className="text-lg font-semibold text-white mb-2">Almuerzo</h4>
              <p className="text-gray-300">Pollo con arroz y vegetales</p>
            </div>
            <div className="bg-black/20 rounded-lg p-4 border border-neon-gold/20">
              <h4 className="text-lg font-semibold text-white mb-2">Cena</h4>
              <p className="text-gray-300">Pescado con ensalada</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
