import React, { useState, useEffect, useCallback } from 'react';
import { collection, addDoc, getDocs, updateDoc, deleteDoc, doc, query, where } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from '../firebase';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    categoryId: '',
    description: '',
    difficulty: 'Principiante'
  });

  const [exercises, setExercises] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [editExercise, setEditExercise] = useState(null);
  const [editCategory, setEditCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [error, setError] = useState(null);

  const loadExercises = useCallback(async () => {
    try {
      let exercisesQuery = collection(db, 'exercises');
      
      if (filterCategory) {
        exercisesQuery = query(exercisesQuery, where('categoryId', '==', filterCategory));
      }

      const querySnapshot = await getDocs(exercisesQuery);
      let exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      if (searchTerm) {
        exercisesList = exercisesList.filter(exercise =>
          exercise.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }
      setExercises(exercisesList);
      setError(null);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
      setError('Error al cargar ejercicios: ' + error.message);
    }
  }, [filterCategory, searchTerm]);

  useEffect(() => {
    loadCategories();
    loadExercises();
  }, [loadExercises]);

  const loadCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'categories'));
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categorías:', error);
      alert('Error al cargar categorías: ' + error.message);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) return;

    setIsLoading(true);
    try {
      if (editCategory) {
        const categoryRef = doc(db, 'categories', editCategory.id);
        await updateDoc(categoryRef, {
          name: newCategoryName.trim(),
        });
        alert('Categoría actualizada correctamente');
        setEditCategory(null);
      } else {
        await addDoc(collection(db, 'categories'), {
          name: newCategoryName.trim(),
          createdAt: new Date().toISOString()
        });
        alert('Categoría agregada correctamente');
      }
      setNewCategoryName('');
      loadCategories();
    } catch (error) {
      console.error(`Error al ${editCategory ? 'actualizar' : 'agregar'} categoría:`, error);
      alert(`Error al ${editCategory ? 'actualizar' : 'agregar'} categoría: ` + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleEditCategory = (categoryToEdit) => {
    setEditCategory(categoryToEdit);
    setNewCategoryName(categoryToEdit.name);
  };

  const handleDeleteCategory = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar esta categoría? Esto no eliminará los ejercicios asociados a ella.')) {
      setIsLoading(true);
      try {
        await deleteDoc(doc(db, 'categories', id));
        loadCategories();
        alert('Categoría eliminada correctamente');
      } catch (error) {
        console.error('Error al eliminar categoría:', error);
        alert('Error al eliminar categoría: ' + error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleImageUpload = async () => {
    if (!imageFile) {
      console.log("No se ha seleccionado ningún archivo de imagen para subir.");
      return null;
    }
    console.log("Intentando subir imagen:", imageFile.name);
    const storageRef = ref(storage, `exercise_images/${imageFile.name}_${Date.now()}`);
    try {
      const snapshot = await uploadBytes(storageRef, imageFile);
      const downloadURL = await getDownloadURL(snapshot.ref);
      console.log("Imagen subida exitosamente. URL:", downloadURL);
      return downloadURL;
    } catch (error) {
      console.error("Error al subir imagen a Firebase Storage:", error);
      throw error; // Propagar el error para que handleSubmit lo maneje
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    console.log("Iniciando handleSubmit.");
    try {
      let imageUrl = null;
      if (imageFile) {
        console.log("Se ha detectado un archivo de imagen. Iniciando subida...");
        imageUrl = await handleImageUpload();
      } else if (editExercise && editExercise.imageUrl) {
        console.log("No se ha seleccionado nueva imagen, conservando la existente.");
        imageUrl = editExercise.imageUrl;
      }
      console.log("URL de la imagen a guardar:", imageUrl);

      if (editExercise) {
        console.log("Actualizando ejercicio existente:", exercise.name);
        const exerciseRef = doc(db, 'exercises', editExercise.id);
        await updateDoc(exerciseRef, {
          name: exercise.name,
          categoryId: exercise.categoryId || '',
          imageUrl: imageUrl || null,
          description: exercise.description, 
          difficulty: exercise.difficulty 
        });
        alert('Ejercicio actualizado correctamente');
        setEditExercise(null);
      } else {
        console.log("Agregando nuevo ejercicio:", exercise.name);
        await addDoc(collection(db, 'exercises'), {
          ...exercise,
          imageUrl: imageUrl || null,
          createdAt: new Date().toISOString()
        });
        alert('Ejercicio agregado correctamente');
      }
      
      setExercise({
        name: '',
        categoryId: '',
        description: '',
        difficulty: 'Principiante'
      });
      setImageFile(null); 
      setShowAddForm(false);
      loadExercises();
      console.log("Operación de ejercicio completada.");
    } catch (error) {
      console.error(`Error en handleSubmit al ${editExercise ? 'actualizar' : 'agregar'} ejercicio:`, error);
      alert(`Error al ${editExercise ? 'actualizar' : 'agregar'} ejercicio: ` + error.message);
    } finally {
      setIsLoading(false);
      console.log("Finalizando handleSubmit.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setImageFile(e.target.files[0]);
    }
  };

  const handleEdit = (exerciseToEdit) => {
    setEditExercise(exerciseToEdit);
    setExercise({
      name: exerciseToEdit.name,
      categoryId: exerciseToEdit.categoryId,
      description: exerciseToEdit.description || '',
      difficulty: exerciseToEdit.difficulty || 'Principiante'
    });
    setImageFile(null);
    setShowAddForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este ejercicio?')) {
      setIsLoading(true);
      try {
        await deleteDoc(doc(db, 'exercises', id));
        loadExercises();
        alert('Ejercicio eliminado correctamente');
      } catch (error) {
        console.error('Error al eliminar ejercicio:', error);
        alert('Error al eliminar ejercicio: ' + error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.id === categoryId);
    return category ? category.name : 'Sin categoría';
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4 sm:p-6 pb-20">
      <h1 className="text-2xl sm:text-4xl font-extrabold text-yellow-500 mb-6 sm:mb-8 text-center">Panel de Control Administrativo</h1>
      
      {error && (
        <div className="bg-red-500 text-white p-4 rounded-lg mb-4">
          {error}
        </div>
      )}

      <div className="bg-gray-800 rounded-xl sm:rounded-2xl shadow-lg p-4 sm:p-6 mb-6 sm:mb-8 border border-gray-700">
        <h2 className="text-xl sm:text-2xl font-bold text-yellow-400 mb-4 sm:mb-5">Gestionar Categorías</h2>
        <form onSubmit={handleAddCategory} className="flex flex-col sm:flex-row gap-3 mb-4 sm:mb-5">
          <input
            type="text"
            value={newCategoryName}
            onChange={(e) => setNewCategoryName(e.target.value)}
            placeholder="Nombre de la categoría"
            className="flex-1 px-4 py-2 rounded-lg bg-gray-700 border border-gray-600 focus:border-yellow-500 focus:outline-none"
          />
          <button
            type="submit"
            disabled={isLoading}
            className="px-6 py-2 bg-yellow-500 text-gray-900 font-semibold rounded-lg hover:bg-yellow-400 transition-colors disabled:opacity-50"
          >
            {isLoading ? 'Procesando...' : editCategory ? 'Actualizar' : 'Agregar'}
          </button>
        </form>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {categories.map(category => (
            <div key={category.id} className="bg-gray-700 p-4 rounded-lg flex justify-between items-center">
              <span className="text-white">{category.name}</span>
              <div className="flex gap-2">
                <button
                  onClick={() => handleEditCategory(category)}
                  className="p-2 text-blue-400 hover:text-blue-300"
                >
                  ✏️
                </button>
                <button
                  onClick={() => handleDeleteCategory(category.id)}
                  className="p-2 text-red-400 hover:text-red-300"
                >
                  🗑️
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="bg-gray-800 rounded-xl sm:rounded-2xl shadow-lg p-4 sm:p-6 border border-gray-700">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 sm:mb-6 gap-4">
          <h2 className="text-xl sm:text-2xl font-bold text-yellow-400">Gestionar Ejercicios</h2>
          <button
            onClick={() => setShowAddForm(!showAddForm)}
            className="px-4 py-2 bg-yellow-500 text-gray-900 font-semibold rounded-lg hover:bg-yellow-400 transition-colors"
          >
            {showAddForm ? 'Cancelar' : 'Agregar Ejercicio'}
          </button>
        </div>

        <div className="flex flex-col sm:flex-row gap-4 mb-4">
          <input
            type="text"
            placeholder="Buscar ejercicio..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-1 px-4 py-2 rounded-lg bg-gray-700 border border-gray-600 focus:border-yellow-500 focus:outline-none"
          />
          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="px-4 py-2 rounded-lg bg-gray-700 border border-gray-600 focus:border-yellow-500 focus:outline-none"
          >
            <option value="">Todas las categorías</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>

        {showAddForm && (
          <form onSubmit={handleSubmit} className="bg-gray-700 p-4 rounded-lg mb-6">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
              <input
                type="text"
                name="name"
                value={exercise.name}
                onChange={handleChange}
                placeholder="Nombre del ejercicio"
                className="px-4 py-2 rounded-lg bg-gray-600 border border-gray-500 focus:border-yellow-500 focus:outline-none"
                required
              />
              <select
                name="categoryId"
                value={exercise.categoryId}
                onChange={handleChange}
                className="px-4 py-2 rounded-lg bg-gray-600 border border-gray-500 focus:border-yellow-500 focus:outline-none"
                required
              >
                <option value="">Seleccionar categoría</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>

            <textarea
              name="description"
              value={exercise.description}
              onChange={handleChange}
              placeholder="Descripción del ejercicio"
              className="w-full px-4 py-2 rounded-lg bg-gray-600 border border-gray-500 focus:border-yellow-500 focus:outline-none mb-4"
              rows="3"
              required
            />

            <select
              name="difficulty"
              value={exercise.difficulty}
              onChange={handleChange}
              className="w-full px-4 py-2 rounded-lg bg-gray-600 border border-gray-500 focus:border-yellow-500 focus:outline-none mb-4"
              required
            >
              <option value="Principiante">Principiante</option>
              <option value="Intermedio">Intermedio</option>
              <option value="Avanzado">Avanzado</option>
            </select>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Imagen del ejercicio
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={handleFileChange}
                className="w-full px-4 py-2 rounded-lg bg-gray-600 border border-gray-500 focus:border-yellow-500 focus:outline-none"
              />
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className="w-full px-6 py-2 bg-yellow-500 text-gray-900 font-semibold rounded-lg hover:bg-yellow-400 transition-colors disabled:opacity-50"
            >
              {isLoading ? 'Procesando...' : editExercise ? 'Actualizar Ejercicio' : 'Agregar Ejercicio'}
            </button>
          </form>
        )}

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {exercises.map(exercise => (
            <div key={exercise.id} className="bg-gray-700 p-4 rounded-lg">
              {exercise.imageUrl && (
                <img
                  src={exercise.imageUrl}
                  alt={exercise.name}
                  className="w-full h-48 object-cover rounded-lg mb-4"
                />
              )}
              <h3 className="text-lg font-semibold mb-2">{exercise.name}</h3>
              <p className="text-sm text-gray-300 mb-2">{getCategoryName(exercise.categoryId)}</p>
              <p className="text-sm text-gray-300 mb-2">Dificultad: {exercise.difficulty}</p>
              <p className="text-sm text-gray-300 mb-4">{exercise.description}</p>
              <div className="flex justify-end gap-2">
                <button
                  onClick={() => handleEdit(exercise)}
                  className="p-2 text-blue-400 hover:text-blue-300"
                >
                  ✏️
                </button>
                <button
                  onClick={() => handleDelete(exercise.id)}
                  className="p-2 text-red-400 hover:text-red-300"
                >
                  🗑️
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
