import React, { useState, useEffect, useCallback } from 'react';
import { collection, addDoc, getDocs, updateDoc, deleteDoc, doc, query, where } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from '../firebase';
import { useAuth } from '../contexts/AuthContext';
import Header from './Header';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    categoryId: '',
    description: '',
    difficulty: 'Principiante'
  });

  const [exercises, setExercises] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [editExercise, setEditExercise] = useState(null);
  const [editCategory, setEditCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [error, setError] = useState(null);
  const { register } = useAuth();
  const [ptForm, setPtForm] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [ptLoading, setPtLoading] = useState(false);
  const [ptError, setPtError] = useState('');
  const [ptSuccess, setPtSuccess] = useState('');

  const loadExercises = useCallback(async () => {
    try {
      let exercisesQuery = collection(db, 'exercises');

      if (filterCategory) {
        exercisesQuery = query(exercisesQuery, where('categoryId', '==', filterCategory));
      }

      const querySnapshot = await getDocs(exercisesQuery);
      let exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      if (searchTerm) {
        exercisesList = exercisesList.filter(exercise =>
          exercise.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }
      setExercises(exercisesList);
      setError(null);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
      setError('Error al cargar ejercicios: ' + error.message);
    }
  }, [filterCategory, searchTerm]);

  useEffect(() => {
    loadCategories();
    loadExercises();
  }, [loadExercises]);

  const loadCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'categories'));
      const categoriesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCategories(categoriesList);
    } catch (error) {
      console.error('Error al cargar categor√≠as:', error);
      alert('Error al cargar categor√≠as: ' + error.message);
    }
  };

  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategoryName.trim()) return;

    setIsLoading(true);
    try {
      if (editCategory) {
        const categoryRef = doc(db, 'categories', editCategory.id);
        await updateDoc(categoryRef, {
          name: newCategoryName.trim(),
        });
        alert('Categor√≠a actualizada correctamente');
        setEditCategory(null);
      } else {
        await addDoc(collection(db, 'categories'), {
          name: newCategoryName.trim(),
          createdAt: new Date().toISOString()
        });
        alert('Categor√≠a agregada correctamente');
      }
      setNewCategoryName('');
      loadCategories();
    } catch (error) {
      console.error(`Error al ${editCategory ? 'actualizar' : 'agregar'} categor√≠a:`, error);
      alert(`Error al ${editCategory ? 'actualizar' : 'agregar'} categor√≠a: ` + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleEditCategory = (categoryToEdit) => {
    setEditCategory(categoryToEdit);
    setNewCategoryName(categoryToEdit.name);
  };

  const handleDeleteCategory = async (id) => {
    if (window.confirm('¬øEst√°s seguro de que quieres eliminar esta categor√≠a? Esto no eliminar√° los ejercicios asociados a ella.')) {
      setIsLoading(true);
      try {
        await deleteDoc(doc(db, 'categories', id));
        loadCategories();
        alert('Categor√≠a eliminada correctamente');
      } catch (error) {
        console.error('Error al eliminar categor√≠a:', error);
        alert('Error al eliminar categor√≠a: ' + error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleImageUpload = async () => {
    if (!imageFile) return null;

    try {
      const storageRef = ref(storage, `exercise_images/${imageFile.name}_${Date.now()}`);
      const snapshot = await uploadBytes(storageRef, imageFile);
      return await getDownloadURL(snapshot.ref);
    } catch (error) {
      console.error('Error al subir imagen:', error);
      throw new Error('Error al subir la imagen: ' + error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      let imageUrl = null;
      if (imageFile) {
        imageUrl = await handleImageUpload();
      } else if (editExercise && editExercise.imageUrl) {
        imageUrl = editExercise.imageUrl;
      }

      const exerciseData = {
        name: exercise.name,
        categoryId: exercise.categoryId || '',
        imageUrl: imageUrl || null,
        description: exercise.description,
        difficulty: exercise.difficulty,
        createdAt: new Date().toISOString()
      };

      if (editExercise) {
        const exerciseRef = doc(db, 'exercises', editExercise.id);
        await updateDoc(exerciseRef, exerciseData);
        alert('Ejercicio actualizado correctamente');
        setEditExercise(null);
      } else {
        await addDoc(collection(db, 'exercises'), exerciseData);
        alert('Ejercicio agregado correctamente');
      }

      // Limpiar el formulario
      setExercise({
        name: '',
        categoryId: '',
        description: '',
        difficulty: 'Principiante'
      });
      setImageFile(null);
      setShowAddForm(false);

      // Recargar los ejercicios
      await loadExercises();
    } catch (error) {
      console.error('Error al guardar ejercicio:', error);
      setError('Error al guardar ejercicio: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setImageFile(e.target.files[0]);
    }
  };

  const handleEdit = (exerciseToEdit) => {
    setEditExercise(exerciseToEdit);
    setExercise({
      name: exerciseToEdit.name,
      categoryId: exerciseToEdit.categoryId,
      description: exerciseToEdit.description || '',
      difficulty: exerciseToEdit.difficulty || 'Principiante'
    });
    setImageFile(null);
    setShowAddForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¬øEst√°s seguro de que quieres eliminar este ejercicio?')) {
      setIsLoading(true);
      try {
        await deleteDoc(doc(db, 'exercises', id));
        loadExercises();
        alert('Ejercicio eliminado correctamente');
      } catch (error) {
        console.error('Error al eliminar ejercicio:', error);
        alert('Error al eliminar ejercicio: ' + error.message);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat.id === categoryId);
    return category ? category.name : 'Sin categor√≠a';
  };

  const handlePtChange = (e) => {
    const { name, value } = e.target;
    setPtForm(prev => ({ ...prev, [name]: value }));
  };

  const handlePtSubmit = async (e) => {
    e.preventDefault();
    setPtError('');
    setPtSuccess('');
    setPtLoading(true);
    try {
      await register(ptForm.email, ptForm.password, ptForm.name, 2);
      setPtSuccess('Personal Trainer creado exitosamente.');
      setPtForm({ name: '', email: '', password: '' });
    } catch (error) {
      setPtError(error.message || 'Error al crear Personal Trainer.');
    } finally {
      setPtLoading(false);
    }
  };

  return (
    <div className="admin-page-container">
      <Header />
      <div className="min-h-screen bg-gray-900 text-white p-2 pb-20 max-w-xs mx-auto">
        <h1 className="text-lg font-extrabold text-yellow-500 mb-4 text-center">Panel Admin</h1>
        {error && (
          <div className="bg-red-500 text-white p-2 rounded mb-2 text-xs">{error}</div>
        )}
        <div className="bg-gray-800 rounded-lg shadow p-2 mb-4 border border-gray-700">
          <h2 className="text-base font-bold text-yellow-400 mb-2">Categor√≠a</h2>
          <form onSubmit={handleAddCategory} className="flex gap-2 mb-2">
            <input
              type="text"
              value={newCategoryName}
              onChange={(e) => setNewCategoryName(e.target.value)}
              placeholder="Nueva categor√≠a"
              className="flex-1 px-2 py-1 rounded bg-gray-700 border border-gray-600 text-xs focus:border-yellow-500 focus:outline-none"
            />
            <button
              type="submit"
              disabled={isLoading}
              className="px-2 py-1 bg-yellow-500 text-gray-900 font-semibold rounded text-xs hover:bg-yellow-400 disabled:opacity-50"
            >
              {isLoading ? '...' : editCategory ? 'Editar' : 'Agregar'}
            </button>
          </form>
          {/* Men√∫ desplegable de categor√≠as */}
          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="w-full px-2 py-1 rounded bg-gray-700 border border-gray-600 text-xs focus:border-yellow-500 focus:outline-none mb-2"
          >
            <option value="">Todas las categor√≠as</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>{category.name}</option>
            ))}
          </select>
        </div>
        <div className="bg-gray-800 rounded-lg shadow p-2 border border-gray-700">
          <div className="flex justify-between items-center mb-2">
            <h2 className="text-base font-bold text-yellow-400">Ejercicios</h2>
            <button
              onClick={() => setShowAddForm(!showAddForm)}
              className="px-2 py-1 bg-yellow-500 text-gray-900 font-semibold rounded text-xs hover:bg-yellow-400"
            >
              {showAddForm ? 'Cancelar' : 'Agregar'}
            </button>
          </div>
          <input
            type="text"
            placeholder="Buscar..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full px-2 py-1 rounded bg-gray-700 border border-gray-600 text-xs focus:border-yellow-500 focus:outline-none mb-2"
          />
          {showAddForm && (
            <form onSubmit={handleSubmit} className="bg-gray-700 p-2 rounded mb-2">
              <input
                type="text"
                name="name"
                value={exercise.name}
                onChange={handleChange}
                placeholder="Nombre"
                className="w-full px-2 py-1 rounded bg-gray-600 border border-gray-500 text-xs focus:border-yellow-500 focus:outline-none mb-1"
                required
              />
              <select
                name="categoryId"
                value={exercise.categoryId}
                onChange={handleChange}
                className="w-full px-2 py-1 rounded bg-gray-600 border border-gray-500 text-xs focus:border-yellow-500 focus:outline-none mb-1"
                required
              >
                <option value="">Categor√≠a</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>{category.name}</option>
                ))}
              </select>
              <textarea
                name="description"
                value={exercise.description}
                onChange={handleChange}
                placeholder="Descripci√≥n"
                className="w-full px-2 py-1 rounded bg-gray-600 border border-gray-500 text-xs focus:border-yellow-500 focus:outline-none mb-1"
                rows="2"
                required
              />
              <select
                name="difficulty"
                value={exercise.difficulty}
                onChange={handleChange}
                className="w-full px-2 py-1 rounded bg-gray-600 border border-gray-500 text-xs focus:border-yellow-500 focus:outline-none mb-2"
                required
              >
                <option value="Principiante">Principiante</option>
                <option value="Intermedio">Intermedio</option>
                <option value="Avanzado">Avanzado</option>
              </select>
              <button
                type="submit"
                disabled={isLoading}
                className="w-full px-2 py-1 bg-yellow-500 text-gray-900 font-semibold rounded text-xs hover:bg-yellow-400 disabled:opacity-50"
              >
                {isLoading ? 'Procesando...' : editExercise ? 'Editar' : 'Agregar'}
              </button>
            </form>
          )}
          <div className="flex flex-col gap-2">
            {exercises.map(exercise => (
              <div key={exercise.id} className="bg-gray-700 p-2 rounded flex flex-col text-xs mb-1">
                <span className="font-bold text-yellow-300">{exercise.name}</span>
                <span className="text-gray-300">{getCategoryName(exercise.categoryId)}</span>
                <span className="text-gray-400">{exercise.difficulty}</span>
                <span className="text-gray-200 mb-1">{exercise.description}</span>
                <div className="flex gap-2 self-end">
                  <button
                    onClick={() => handleEdit(exercise)}
                    className="text-blue-400 hover:text-blue-300"
                  >‚úèÔ∏è</button>
                  <button
                    onClick={() => handleDelete(exercise.id)}
                    className="text-red-400 hover:text-red-300"
                  >üóëÔ∏è</button>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="admin-section">
          <h2>Crear Personal Trainer</h2>
          <form onSubmit={handlePtSubmit} className="pt-form">
            <div className="form-group">
              <label>Nombre</label>
              <input type="text" name="name" value={ptForm.name} onChange={handlePtChange} required disabled={ptLoading} />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input type="email" name="email" value={ptForm.email} onChange={handlePtChange} required disabled={ptLoading} />
            </div>
            <div className="form-group">
              <label>Contrase√±a</label>
              <input type="password" name="password" value={ptForm.password} onChange={handlePtChange} required disabled={ptLoading} />
            </div>
            <button type="submit" className="register-button" disabled={ptLoading}>
              {ptLoading ? 'Creando...' : 'Crear Personal Trainer'}
            </button>
            {ptError && <div className="error-message">{ptError}</div>}
            {ptSuccess && <div className="success-message">{ptSuccess}</div>}
          </form>
        </div>
      </div>
    </div>
  );
}
