import React, { useState } from 'react';

export default function RoutinesNew() {
  const [exercises, setExercises] = useState([]);
  const [newExercise, setNewExercise] = useState({ name: '', sets: '', reps: '', notes: '' });

  const addExercise = () => {
    if (newExercise.name.trim() !== '') {
      setExercises([...exercises, newExercise]);
      setNewExercise({ name: '', sets: '', reps: '', notes: '' });
    }
  };

  const removeExercise = (index) => {
    const updatedExercises = exercises.filter((_, i) => i !== index);
    setExercises(updatedExercises);
  };

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold text-yellow-400 mb-4">Crear Nueva Rutina</h3>
      <div className="space-y-4">
        <div className="flex flex-col space-y-2">
          <input
            type="text"
            placeholder="Nombre del ejercicio"
            value={newExercise.name}
            onChange={(e) => setNewExercise({ ...newExercise, name: e.target.value })}
            className="p-2 bg-gray-700 text-white rounded"
          />
          <input
            type="text"
            placeholder="Series"
            value={newExercise.sets}
            onChange={(e) => setNewExercise({ ...newExercise, sets: e.target.value })}
            className="p-2 bg-gray-700 text-white rounded"
          />
          <input
            type="text"
            placeholder="Repeticiones"
            value={newExercise.reps}
            onChange={(e) => setNewExercise({ ...newExercise, reps: e.target.value })}
            className="p-2 bg-gray-700 text-white rounded"
          />
          <input
            type="text"
            placeholder="Notas"
            value={newExercise.notes}
            onChange={(e) => setNewExercise({ ...newExercise, notes: e.target.value })}
            className="p-2 bg-gray-700 text-white rounded"
          />
          <button
            onClick={addExercise}
            className="p-2 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-400"
          >
            Agregar Ejercicio
          </button>
        </div>
        <div className="space-y-2">
          {exercises.map((exercise, index) => (
            <div key={index} className="flex justify-between items-center p-2 bg-gray-700 rounded">
              <span className="text-white">{exercise.name} - {exercise.sets} x {exercise.reps}</span>
              <button
                onClick={() => removeExercise(index)}
                className="p-1 bg-red-500 text-white rounded hover:bg-red-400"
              >
                Eliminar
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
