import React, { useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

export default function HomePage() {
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const frase = 'Bienvenido a Semental Fit. Tu camino hacia una vida más saludable comienza aquí.';
    if ('speechSynthesis' in window) {
      const utter = new window.SpeechSynthesisUtterance(frase);
      utter.lang = 'es-ES';
      utter.onend = () => {
        if (currentUser) {
          navigate('/mirutina');
        } else {
          navigate('/login');
        }
      };
      window.speechSynthesis.cancel();
      window.speechSynthesis.speak(utter);
    } else {
      // Si no hay speechSynthesis, redirigir igual tras 3s
      setTimeout(() => {
        if (currentUser) {
          navigate('/mirutina');
        } else {
          navigate('/login');
        }
      }, 3000);
    }
  }, [currentUser, navigate]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-black">
      <img src="/logo.png" alt="Semental Fit Logo" className="w-40 h-40 mb-8 drop-shadow-lg" />
      <h1 className="text-3xl md:text-4xl font-bold text-gradient-gold text-center max-w-xl">
        Bienvenido a Semental Fit
      </h1>
    </div>
  );
}
