import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { doc, getDoc, updateDoc } from 'firebase/firestore';
import { auth, db } from '../services/firebase';

const TrainingPage = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (user) => {
            if (user) {
                try {
                    const userDoc = await getDoc(doc(db, 'users', user.uid));
                    if (userDoc.exists()) {
                        setUser({
                            ...user,
                            ...userDoc.data()
                        });
                    } else {
                        setUser(user);
                    }
                } catch (error) {
                    console.error('Error getting user data:', error);
                    setUser(user);
                }
            } else {
                navigate('/login');
            }
            setLoading(false);
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogout = async () => {
        try {
            await signOut(auth);
            navigate('/login');
        } catch (error) {
            console.error('Error signing out:', error);
            setError('Error al cerrar sesión');
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-black flex items-center justify-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-black text-white p-4">
            <div className="max-w-4xl mx-auto">
                <div className="flex justify-between items-center mb-8">
                    <h1 className="text-3xl font-bold text-yellow-500">SementalFit</h1>
                    <button
                        onClick={handleLogout}
                        className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                    >
                        Cerrar sesión
                    </button>
                </div>

                {error && (
                    <div className="bg-red-900/20 text-red-500 p-4 rounded-lg mb-6">
                        {error}
                    </div>
                )}

                <div className="bg-gray-900 rounded-xl p-6 mb-6">
                    <h2 className="text-2xl font-semibold mb-4">Bienvenido, {user?.displayName || user?.email}</h2>
                    <p className="text-gray-400">
                        Aquí podrás ver tu rutina de entrenamiento personalizada.
                    </p>
                </div>

                {/* Aquí irá el contenido de la rutina de entrenamiento */}
                <div className="bg-gray-900 rounded-xl p-6">
                    <h3 className="text-xl font-semibold mb-4">Tu Rutina</h3>
                    <p className="text-gray-400">
                        Próximamente: Tu rutina personalizada aparecerá aquí.
                    </p>
                </div>
            </div>
        </div>
    );
};

export default TrainingPage;
