import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs } from 'firebase/firestore';
import { db } from '../firebase';

export default function AdminPage() {
  const [exercise, setExercise] = useState({
    name: '',
    category: ''
  });

  const [exercises, setExercises] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);

  useEffect(() => {
    loadExercises();
  }, []);

  const loadExercises = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, 'exercises'));
      const exercisesList = querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setExercises(exercisesList);
    } catch (error) {
      console.error('Error al cargar ejercicios:', error);
      alert('Error al cargar ejercicios: ' + error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'exercises'), {
        ...exercise,
        createdAt: new Date().toISOString()
      });
      
      setExercise({
        name: '',
        category: ''
      });
      setShowAddForm(false);
      loadExercises();
      alert('Ejercicio agregado correctamente');
    } catch (error) {
      console.error('Error al agregar ejercicio:', error);
      alert('Error al agregar ejercicio: ' + error.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setExercise(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="min-h-screen bg-black text-white p-4 pb-20">
      <h1 className="text-2xl font-bold text-yellow-500 mb-6">Panel de Control</h1>
      
      {/* Lista de Ejercicios Existentes */}
      <div className="bg-gray-800 rounded-xl p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Ejercicios Existentes</h2>
          <button
            onClick={() => setShowAddForm(!showAddForm)}
            className="bg-yellow-500 text-black font-bold py-2 px-4 rounded-lg hover:bg-yellow-600 transition-colors"
          >
            {showAddForm ? 'Cancelar' : 'Agregar Ejercicio'}
          </button>
        </div>

        {showAddForm && (
          <form onSubmit={handleSubmit} className="mb-6 space-y-4">
            <div>
              <label className="block text-gray-300 mb-2">Nombre del Ejercicio</label>
              <input
                type="text"
                name="name"
                value={exercise.name}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
                required
              />
            </div>

            <div>
              <label className="block text-gray-300 mb-2">Categoría</label>
              <input
                type="text"
                name="category"
                value={exercise.category}
                onChange={handleChange}
                className="w-full p-3 bg-gray-700 rounded-lg text-white border border-gray-600 focus:border-yellow-500 focus:outline-none"
                required
              />
            </div>

            <button
              type="submit"
              className="w-full bg-yellow-500 text-black font-bold py-3 rounded-lg hover:bg-yellow-600 transition-colors"
            >
              Agregar Ejercicio
            </button>
          </form>
        )}

        <div className="space-y-2">
          {exercises.map(exercise => (
            <div key={exercise.id} className="bg-gray-700 p-3 rounded-lg">
              <h3 className="font-semibold text-yellow-500">{exercise.name}</h3>
              <p className="text-gray-300">Categoría: {exercise.category}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
