import React, { useState, useEffect, useMemo } from 'react';
import { Routes, Route, useNavigate, NavLink, Navigate } from 'react-router-dom';
import {
    getUsers,
    getUser,
    createUser,
    getAllExercises,
    getUserRoutine,
    createUserRoutine,
    updateExerciseInRoutine,
    initializeExercises,
    initializeExercisesByCategory,
    saveUserRoutine
} from './services/database';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged } from 'firebase/auth';
import { auth } from './services/firebase';

// --- Iconos SVG para la interfaz ---
const DumbbellIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.25278C12 6.25278 10.8333 5 8.5 5C6.16667 5 5 6.25278 5 6.25278C5 6.25278 3.83333 7.50556 3.83333 10C3.83333 12.4944 5 13.7472 5 13.7472C5 13.7472 6.16667 15 8.5 15C10.8333 15 12 13.7472 12 13.7472M12 6.25278L12 17.7472M12 6.25278C12 6.25278 13.1667 5 15.5 5C17.8333 5 19 6.25278 19 6.25278C19 6.25278 20.1667 7.50556 20.1667 10C20.1667 12.4944 19 13.7472 19 13.7472C19 13.7472 17.8333 15 15.5 15C13.1667 15 12 13.7472 12 13.7472" />
    </svg>
);

const NutritionIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5.636 18.364a9 9 0 010-12.728m12.728 0a9 9 0 010 12.728m-9.9-2.828a4 4 0 010-5.656m5.656 0a4 4 0 010 5.656" />
    </svg>
);

const ShopIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
    </svg>
);

const ProfileIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
    </svg>
);

const LogoIcon = ({ className }) => (
    <img 
        src={process.env.PUBLIC_URL + '/logo.png'} 
        alt="Semental Fit Logo" 
        className={className}
        style={{ objectFit: 'contain', maxWidth: '100%', height: 'auto' }}
    />
);

// --- Datos de la Aplicación ---
const LEVELS = {
    1: { name: "Novato", discount: 0 },
    2: { name: "Aprendiz", discount: 0.05 },
    3: { name: "Guerrero", discount: 0.10 },
    4: { name: "Titán", discount: 0.12 },
    5: { name: "Semental Supremo", discount: 0.15 },
};

const QUOTES = [
    "El dolor que sientes hoy será la fuerza que sentirás mañana.",
    "No te rindas cuando estés cansado. Ríndete cuando hayas terminado.",
    "El éxito no es para los débiles. Es para los que se atreven a soñar.",
    "Tu cuerpo puede soportar casi cualquier cosa. Es tu mente la que necesitas convencer.",
    "La diferencia entre lo imposible y lo posible está en la determinación de una persona.",
    "No cuentes los días, haz que los días cuenten.",
    "El único entrenamiento malo es el que no se hace.",
    "La fuerza no viene de la capacidad física. Viene de una voluntad indomable.",
    "Si quieres algo que nunca has tenido, debes estar dispuesto a hacer algo que nunca has hecho.",
    "El dolor es temporal. El orgullo es para siempre."
];

const WORKOUTS = {
    fuerza: [
        { name: "Press de Banca", sets: 4, reps: 6, xp: 20 },
        { name: "Sentadillas", sets: 4, reps: 5, xp: 25 },
        { name: "Peso Muerto", sets: 3, reps: 5, xp: 30 },
    ],
    hipertrofia: [
        { name: "Curl de Bíceps", sets: 3, reps: 12, xp: 15 },
        { name: "Elevaciones Laterales", sets: 4, reps: 15, xp: 15 },
        { name: "Press Francés", sets: 3, reps: 10, xp: 15 },
    ],
    grasa: [
        { name: "Burpees", time: "30s", sets: 5, xp: 20 },
        { name: "Saltos de Cuerda", time: "5min", sets: 1, xp: 25 },
        { name: "Sprints", time: "15s", sets: 8, xp: 30 },
    ],
};

const PRODUCTS = [
    { id: 1, name: "Remera 'Legacy'", price: 25000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Legacy" },
    { id: 2, name: "Proteína 'Titan Whey'", price: 45000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Titan+Whey" },
    { id: 3, name: "Buzo 'Supremo'", price: 38000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Supremo" },
];

const NUTRITION_PLAN = {
    desayuno: "Avena con proteína, frutos rojos y un puñado de almendras.",
    almuerzo: "Pechuga de pollo a la plancha, quinoa y brócoli al vapor.",
    merienda: "Yogur griego con miel y nueces.",
    cena: "Salmón al horno con batatas y espárragos.",
};

// --- Componentes de la Interfaz ---

const HomePage = () => {
    const [quote, setQuote] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Seleccionar una frase aleatoria al cargar
        const randomQuote = QUOTES[Math.floor(Math.random() * QUOTES.length)];
        setQuote(randomQuote);

        // Función para reproducir el audio
        const speakQuote = () => {
            if ('speechSynthesis' in window) {
                // Cancelar cualquier reproducción anterior
                window.speechSynthesis.cancel();

                // Obtener todas las voces disponibles
                const voices = window.speechSynthesis.getVoices();
                
                // Buscar una voz femenina en español
                const femaleSpanishVoice = voices.find(voice => 
                    (voice.lang.includes('es') || voice.name.includes('Spanish') || voice.name.includes('Español')) &&
                    voice.name.toLowerCase().includes('female')
                ) || voices.find(voice => 
                    voice.lang.includes('es') || 
                    voice.name.includes('Spanish') || 
                    voice.name.includes('Español')
                );

                const utterance = new SpeechSynthesisUtterance(randomQuote);
                utterance.lang = 'es-ES';
                
                // Si encontramos una voz femenina en español, la usamos
                if (femaleSpanishVoice) {
                    utterance.voice = femaleSpanishVoice;
                }
                
                // Configurar la voz para que sea más natural y sensual
                utterance.rate = 0.8; // Velocidad más lenta
                utterance.pitch = 1.1; // Tono ligeramente más alto para sonar más femenino
                utterance.volume = 1; // Volumen máximo

                // Agregar pausas naturales
                utterance.text = randomQuote.replace(/\./g, '. ').replace(/,/g, ', ');

                utterance.onend = () => {
                    // Navegar a la página de entrenamiento después del audio
                    navigate('/training');
                };

                window.speechSynthesis.speak(utterance);
            }
        };

        // Esperar a que las voces estén cargadas
        if (window.speechSynthesis.getVoices().length > 0) {
            speakQuote();
        } else {
            window.speechSynthesis.onvoiceschanged = speakQuote;
        }

        // Limpiar al desmontar
        return () => {
            window.speechSynthesis.cancel();
        };
    }, [navigate]);

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-black p-4">
            <div className="text-center">
                <img 
                    src="/logo.png" 
                    alt="SementalFit Logo" 
                    className="w-64 h-64 mx-auto mb-8"
                />
                <div className="max-w-2xl mx-auto">
                    <p className="text-2xl text-yellow-400 mb-4 font-semibold">{quote}</p>
                </div>
            </div>
        </div>
    );
};

const TrainingPage = () => {
    const [selectedDay, setSelectedDay] = useState('Lunes');
    const [exercises, setExercises] = useState([]);
    const [userRoutine, setUserRoutine] = useState({});
    const [selectedCategory, setSelectedCategory] = useState('Pecho');
    const [selectedExercise, setSelectedExercise] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [series, setSeries] = useState(3);
    const [reps, setReps] = useState(12);
    const [weight, setWeight] = useState(0);

    useEffect(() => {
        const loadData = async () => {
            try {
                setLoading(true);
                setError(null);
                
                // Cargar ejercicios primero
                const exercisesData = await getAllExercises();
                if (!exercisesData || exercisesData.length === 0) {
                    throw new Error('No se pudieron cargar los ejercicios');
                }
                setExercises(exercisesData);
                
                // Luego cargar la rutina
                const routineData = await getUserRoutine();
                setUserRoutine(routineData || {});
            } catch (err) {
                console.error('Error al cargar datos:', err);
                setError(err.message || 'Error al cargar los datos. Por favor, intenta de nuevo.');
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, []);

    const filteredExercises = exercises.filter(ex => ex.category === selectedCategory);

    const addExerciseToRoutine = async (exercise) => {
        try {
            if (!selectedDay) {
                setError('Por favor, selecciona un día primero');
                return;
            }

            const newRoutine = { ...userRoutine };
            if (!newRoutine[selectedDay]) {
                newRoutine[selectedDay] = [];
            }

            // Verificar si el ejercicio ya existe en el día seleccionado
            const exerciseExists = newRoutine[selectedDay].some(
                ex => ex.id === exercise.id
            );

            if (exerciseExists) {
                setError('Este ejercicio ya está en tu rutina para este día');
                return;
            }

            // Agregar el ejercicio con series por defecto
            newRoutine[selectedDay].push({
                ...exercise,
                series: [
                    { reps: 12, weight: 0 },
                    { reps: 12, weight: 0 },
                    { reps: 12, weight: 0 }
                ]
            });

            setUserRoutine(newRoutine);
            await saveUserRoutine(user.uid, newRoutine);
            setSelectedExercise(null);
            setError(null);
        } catch (error) {
            console.error('Error al agregar ejercicio:', error);
            setError('Error al agregar ejercicio a la rutina');
        }
    };

    const removeExerciseFromRoutine = async (exerciseId) => {
        try {
            const newRoutine = { ...userRoutine };
            newRoutine[selectedDay] = newRoutine[selectedDay].filter(
                ex => ex.id !== exerciseId
            );
            setUserRoutine(newRoutine);
            await saveUserRoutine(user.uid, newRoutine);
        } catch (error) {
            console.error('Error al eliminar ejercicio:', error);
            setError('Error al eliminar ejercicio de la rutina');
        }
    };

    const updateExerciseInRoutine = async (exerciseId, updatedExercise) => {
        try {
            const newRoutine = { ...userRoutine };
            const exerciseIndex = newRoutine[selectedDay].findIndex(
                ex => ex.id === exerciseId
            );
            if (exerciseIndex !== -1) {
                newRoutine[selectedDay][exerciseIndex] = updatedExercise;
                setUserRoutine(newRoutine);
                await saveUserRoutine(user.uid, newRoutine);
            }
        } catch (error) {
            console.error('Error al actualizar ejercicio:', error);
            setError('Error al actualizar ejercicio en la rutina');
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-black text-white p-4">
                <div className="max-w-4xl mx-auto">
                    <div className="bg-gray-900 rounded-lg shadow-md p-6">
                        <div className="flex justify-center items-center h-64">
                            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
                            <p className="ml-3 text-gray-300">Cargando ejercicios...</p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-black text-white p-4">
                <div className="max-w-4xl mx-auto">
                    <div className="bg-gray-900 rounded-lg shadow-md p-6">
                        <div className="text-center">
                            <p className="text-lg font-semibold text-red-400 mb-4">{error}</p>
                            <button 
                                onClick={() => window.location.reload()}
                                className="px-4 py-2 bg-yellow-500 text-black rounded hover:bg-yellow-600 transition-colors"
                            >
                                Reintentar
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-black text-white p-4">
            <div className="max-w-4xl mx-auto">
                <div className="bg-gray-900 rounded-lg shadow-md p-6">
                    <h2 className="text-2xl font-bold mb-6 text-yellow-500">Entrenamiento</h2>
                    
                    {/* Selector de día */}
                    <div className="mb-6">
                        <label className="block text-sm font-medium text-gray-300 mb-2">Día de entrenamiento</label>
                        <select
                            value={selectedDay}
                            onChange={(e) => setSelectedDay(e.target.value)}
                            className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                        >
                            {['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'].map(day => (
                                <option key={day} value={day}>{day}</option>
                            ))}
                        </select>
                    </div>

                    {/* Selector de categoría */}
                    <div className="mb-6">
                        <label className="block text-sm font-medium text-gray-300 mb-2">Categoría</label>
                        <select
                            value={selectedCategory}
                            onChange={(e) => setSelectedCategory(e.target.value)}
                            className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                        >
                            {['Pecho', 'Espalda', 'Hombros', 'Piernas', 'Gemelos', 'Bíceps', 'Tríceps', 'Abdominales'].map(category => (
                                <option key={category} value={category}>{category}</option>
                            ))}
                        </select>
                    </div>

                    {/* Lista de ejercicios */}
                    <div className="mb-6">
                        <label className="block text-sm font-medium text-gray-300 mb-2">Ejercicios disponibles</label>
                        <select
                            value={selectedExercise?.id || ''}
                            onChange={(e) => {
                                const exercise = filteredExercises.find(ex => ex.id === e.target.value);
                                setSelectedExercise(exercise);
                            }}
                            className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                        >
                            <option value="">Selecciona un ejercicio</option>
                            {filteredExercises.map(exercise => (
                                <option key={exercise.id} value={exercise.id}>
                                    {exercise.name}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* Configuración de series, repeticiones y peso */}
                    {selectedExercise && (
                        <div className="mb-6 grid grid-cols-3 gap-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-300 mb-2">Series</label>
                                <input
                                    type="number"
                                    value={series}
                                    onChange={(e) => setSeries(parseInt(e.target.value) || 0)}
                                    min="1"
                                    className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium text-gray-300 mb-2">Repeticiones</label>
                                <input
                                    type="number"
                                    value={reps}
                                    onChange={(e) => setReps(parseInt(e.target.value) || 0)}
                                    min="1"
                                    className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium text-gray-300 mb-2">Peso (kg)</label>
                                <input
                                    type="number"
                                    value={weight}
                                    onChange={(e) => setWeight(parseInt(e.target.value) || 0)}
                                    min="0"
                                    className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                                />
                            </div>
                        </div>
                    )}

                    {/* Botón para agregar ejercicio */}
                    <button
                        onClick={() => addExerciseToRoutine(selectedExercise)}
                        disabled={!selectedExercise}
                        className="w-full bg-yellow-500 text-black py-2 px-4 rounded hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400"
                    >
                        Agregar ejercicio
                    </button>

                    {/* Lista de ejercicios del día */}
                    <div className="mt-6">
                        <h3 className="text-lg font-semibold mb-3 text-yellow-500">Ejercicios del día</h3>
                        {userRoutine[selectedDay]?.map((exercise, index) => (
                            <div key={index} className="bg-gray-800 rounded mb-4 p-4">
                                <div className="flex justify-between items-center mb-2">
                                    <span className="font-medium">{exercise.name}</span>
                                    <div className="flex space-x-2">
                                        <button
                                            onClick={() => removeExerciseFromRoutine(exercise.id)}
                                            className="text-red-400 hover:text-red-300"
                                        >
                                            Eliminar
                                        </button>
                                        <button
                                            onClick={() => {
                                                const updatedExercise = { ...exercise, series: [{ reps: series, weight: weight }] };
                                                updateExerciseInRoutine(exercise.id, updatedExercise);
                                            }}
                                            className="text-yellow-400 hover:text-yellow-300"
                                        >
                                            Actualizar
                                        </button>
                                    </div>
                                </div>
                                <div className="space-y-2">
                                    {Array.isArray(exercise.series) && exercise.series.map((serie, serieIndex) => (
                                        <div key={serieIndex} className="flex items-center space-x-4">
                                            <span className="text-sm text-gray-400">Serie {serieIndex + 1}:</span>
                                            <div className="flex flex-col">
                                                <span className="text-xs text-gray-400 mb-1">Reps</span>
                                                <input
                                                    type="number"
                                                    value={serie.reps}
                                                    onChange={(e) => {
                                                        const updatedExercise = { ...exercise, series: exercise.series.map((s, i) => i === serieIndex ? { ...s, reps: parseInt(e.target.value) || 0 } : s) };
                                                        updateExerciseInRoutine(exercise.id, updatedExercise);
                                                    }}
                                                    className="w-20 bg-gray-700 text-white p-1 rounded"
                                                    placeholder="Reps"
                                                />
                                            </div>
                                            <div className="flex flex-col">
                                                <span className="text-xs text-gray-400 mb-1">Kg</span>
                                                <input
                                                    type="number"
                                                    value={serie.weight}
                                                    onChange={(e) => {
                                                        const updatedExercise = { ...exercise, series: exercise.series.map((s, i) => i === serieIndex ? { ...s, weight: parseInt(e.target.value) || 0 } : s) };
                                                        updateExerciseInRoutine(exercise.id, updatedExercise);
                                                    }}
                                                    className="w-20 bg-gray-700 text-white p-1 rounded"
                                                    placeholder="Peso"
                                                />
                                            </div>
                                            <input
                                                type="checkbox"
                                                checked={serie.completed}
                                                onChange={(e) => {
                                                    const updatedExercise = { ...exercise, series: exercise.series.map((s, i) => i === serieIndex ? { ...s, completed: e.target.checked } : s) };
                                                    updateExerciseInRoutine(exercise.id, updatedExercise);
                                                }}
                                                className="form-checkbox h-5 w-5 text-yellow-500"
                                            />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

