const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [isRegistering, setIsRegistering] = useState(false);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        try {
            if (isRegistering) {
                // Registro de nuevo usuario
                const userCredential = await createUserWithEmailAndPassword(auth, email, password);
                const user = userCredential.user;
                
                // Crear perfil de usuario
                await createUser({
                    id: user.uid,
                    name: name,
                    email: email,
                    level: 'Principiante',
                    experience: 0,
                    goals: ['Ganar masa muscular', 'Mejorar la fuerza'],
                    levelName: 'Novato',
                    discount: 0.1,
                    xp: 0
                });

                // Inicializar rutina vacía
                await createUserRoutine(user.uid, {});
            } else {
                // Inicio de sesión
                await signInWithEmailAndPassword(auth, email, password);
            }
        } catch (err) {
            console.error('Error:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white flex items-center justify-center p-4">
            <div className="w-full max-w-md">
                <div className="bg-gray-900 rounded-lg shadow-md p-8">
                    <h2 className="text-2xl font-bold mb-6 text-yellow-500 text-center">
                        {isRegistering ? 'Crear cuenta' : 'Iniciar sesión'}
                    </h2>

                    {error && (
                        <div className="bg-red-900/50 p-4 rounded-lg mb-6">
                            <p className="text-red-400">{error}</p>
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-4">
                        {isRegistering && (
                            <div>
                                <label className="block text-sm font-medium text-gray-300 mb-2">
                                    Nombre
                                </label>
                                <input
                                    type="text"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    required
                                    className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                                    placeholder="Tu nombre"
                                />
                            </div>
                        )}

                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">
                                Correo electrónico
                            </label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                                className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                                placeholder="tu@email.com"
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">
                                Contraseña
                            </label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                                className="w-full p-2 bg-gray-800 text-white border border-gray-700 rounded-md"
                                placeholder="••••••••"
                            />
                        </div>

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full bg-yellow-500 text-black py-2 px-4 rounded hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400"
                        >
                            {loading ? (
                                <div className="flex items-center justify-center">
                                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-black"></div>
                                    <span className="ml-2">Cargando...</span>
                                </div>
                            ) : (
                                isRegistering ? 'Registrarse' : 'Iniciar sesión'
                            )}
                        </button>
                    </form>

                    <div className="mt-4 text-center">
                        <button
                            onClick={() => {
                                setIsRegistering(!isRegistering);
                                setError(null);
                            }}
                            className="text-yellow-500 hover:text-yellow-400"
                        >
                            {isRegistering
                                ? '¿Ya tienes cuenta? Inicia sesión'
                                : '¿No tienes cuenta? Regístrate'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

const Navigation = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            setUser(user);
            setLoading(false);
        });

        return () => unsubscribe();
    }, []);

    if (loading) {
        return (
            <div className="min-h-screen bg-black text-white flex items-center justify-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
            </div>
        );
    }

    if (!user) {
        return <Navigate to="/login" />;
    }

    return (
        <div className="fixed bottom-0 left-0 right-0 bg-gray-900 border-t border-gray-800">
            <div className="max-w-4xl mx-auto px-4">
                <div className="flex justify-around py-2">
                    <NavLink to="/" className={({ isActive }) =>
                        `flex flex-col items-center ${isActive ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'}`
