import React, { useState, useEffect, useMemo } from 'react';
import { getUsers, getUser, createUser, getAllExercises, createUserRoutine, getUserRoutine, updateExerciseInRoutine, initializeExercises, initializeExercisesByCategory } from './services/database';

// --- Iconos SVG para la interfaz ---
const DumbbellIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.25278C12 6.25278 10.8333 5 8.5 5C6.16667 5 5 6.25278 5 6.25278C5 6.25278 3.83333 7.50556 3.83333 10C3.83333 12.4944 5 13.7472 5 13.7472C5 13.7472 6.16667 15 8.5 15C10.8333 15 12 13.7472 12 13.7472M12 6.25278L12 17.7472M12 6.25278C12 6.25278 13.1667 5 15.5 5C17.8333 5 19 6.25278 19 6.25278C19 6.25278 20.1667 7.50556 20.1667 10C20.1667 12.4944 19 13.7472 19 13.7472C19 13.7472 17.8333 15 15.5 15C13.1667 15 12 13.7472 12 13.7472" />
    </svg>
);

const NutritionIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5.636 18.364a9 9 0 010-12.728m12.728 0a9 9 0 010 12.728m-9.9-2.828a4 4 0 010-5.656m5.656 0a4 4 0 010 5.656" />
    </svg>
);

const ShopIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
    </svg>
);

const ProfileIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
    </svg>
);

const LogoIcon = ({ className }) => (
    <img 
        src={process.env.PUBLIC_URL + '/logo.png'} 
        alt="Semental Fit Logo" 
        className={className}
        style={{ objectFit: 'contain', maxWidth: '100%', height: 'auto' }}
    />
);

// --- Datos de la Aplicación ---
const LEVELS = {
    1: { name: "Novato", discount: 0 },
    2: { name: "Aprendiz", discount: 0.05 },
    3: { name: "Guerrero", discount: 0.10 },
    4: { name: "Titán", discount: 0.12 },
    5: { name: "Semental Supremo", discount: 0.15 },
};

const QUOTES = [
    "El dolor que sientes hoy será la fuerza que sentirás mañana.",
    "No te rindas cuando estés cansado. Ríndete cuando hayas terminado.",
    "El éxito no es para los débiles. Es para los que se atreven a soñar.",
    "Tu cuerpo puede soportar casi cualquier cosa. Es tu mente la que necesitas convencer.",
    "La diferencia entre lo imposible y lo posible está en la determinación de una persona.",
    "No cuentes los días, haz que los días cuenten.",
    "El único entrenamiento malo es el que no se hace.",
    "La fuerza no viene de la capacidad física. Viene de una voluntad indomable.",
    "Si quieres algo que nunca has tenido, debes estar dispuesto a hacer algo que nunca has hecho.",
    "El dolor es temporal. El orgullo es para siempre."
];

const WORKOUTS = {
    fuerza: [
        { name: "Press de Banca", sets: 4, reps: 6, xp: 20 },
        { name: "Sentadillas", sets: 4, reps: 5, xp: 25 },
        { name: "Peso Muerto", sets: 3, reps: 5, xp: 30 },
    ],
    hipertrofia: [
        { name: "Curl de Bíceps", sets: 3, reps: 12, xp: 15 },
        { name: "Elevaciones Laterales", sets: 4, reps: 15, xp: 15 },
        { name: "Press Francés", sets: 3, reps: 10, xp: 15 },
    ],
    grasa: [
        { name: "Burpees", time: "30s", sets: 5, xp: 20 },
        { name: "Saltos de Cuerda", time: "5min", sets: 1, xp: 25 },
        { name: "Sprints", time: "15s", sets: 8, xp: 30 },
    ],
};

const PRODUCTS = [
    { id: 1, name: "Remera 'Legacy'", price: 25000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Legacy" },
    { id: 2, name: "Proteína 'Titan Whey'", price: 45000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Titan+Whey" },
    { id: 3, name: "Buzo 'Supremo'", price: 38000, img: "https://placehold.co/400x400/1a1a1a/FFD700?text=Supremo" },
];

const NUTRITION_PLAN = {
    desayuno: "Avena con proteína, frutos rojos y un puñado de almendras.",
    almuerzo: "Pechuga de pollo a la plancha, quinoa y brócoli al vapor.",
    merienda: "Yogur griego con miel y nueces.",
    cena: "Salmón al horno con batatas y espárragos.",
};

// --- Componentes de la Interfaz ---

const HomePage = () => {
    const [quote, setQuote] = useState('');
    const [isSpeaking, setIsSpeaking] = useState(false);

    useEffect(() => {
        // Seleccionar una frase aleatoria al cargar
        const randomQuote = QUOTES[Math.floor(Math.random() * QUOTES.length)];
        setQuote(randomQuote);
    }, []);

    const speakQuote = () => {
        if ('speechSynthesis' in window) {
            if (isSpeaking) {
                window.speechSynthesis.cancel();
                setIsSpeaking(false);
                return;
            }

            const utterance = new SpeechSynthesisUtterance(quote);
            utterance.lang = 'es-ES';
            utterance.onend = () => setIsSpeaking(false);
            window.speechSynthesis.speak(utterance);
            setIsSpeaking(true);
        }
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 p-4">
            <div className="text-center">
                <img 
                    src="/logo.png" 
                    alt="SementalFit Logo" 
                    className="w-64 h-64 mx-auto mb-8"
                />
                <div className="max-w-2xl mx-auto">
                    <p className="text-2xl text-yellow-400 mb-4 font-semibold">{quote}</p>
                    <button
                        onClick={speakQuote}
                        className="bg-yellow-500 text-black px-6 py-2 rounded-full hover:bg-yellow-600 transition-colors flex items-center gap-2 mx-auto"
                    >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M9.383 3.076A1 1 0 0110 4v12a1 1 0 01-1.707.707L4.586 13H2a1 1 0 01-1-1V8a1 1 0 011-1h2.586l3.707-3.707a1 1 0 011.09-.217zM12.293 7.293a1 1 0 011.414 0L15 8.586l1.293-1.293a1 1 0 111.414 1.414L16.414 10l1.293 1.293a1 1 0 01-1.414 1.414L15 11.414l-1.293 1.293a1 1 0 01-1.414-1.414L13.586 10l-1.293-1.293a1 1 0 010-1.414z" clipRule="evenodd" />
                        </svg>
                        {isSpeaking ? 'Detener' : 'Escuchar'}
                    </button>
                </div>
            </div>
        </div>
    );
};

const TrainingPage = () => {
    const [selectedDay, setSelectedDay] = useState('Lunes');
    const [exercises, setExercises] = useState([]);
    const [userRoutine, setUserRoutine] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedExercise, setSelectedExercise] = useState('');
    const [newExercise, setNewExercise] = useState({
        name: '',
        sets: 1,
        weight: '',
        reps: 0
    });

    const DAYS = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

    useEffect(() => {
        loadExercises();
        loadUserRoutine();
    }, []);

    const loadExercises = async () => {
        try {
            const allExercises = await getAllExercises();
            setExercises(allExercises);
        } catch (error) {
            console.error('Error al cargar ejercicios:', error);
        }
    };

    const loadUserRoutine = async () => {
        try {
            const routine = await getUserRoutine('user1'); // Por ahora usamos un ID fijo
            setUserRoutine(routine);
        } catch (error) {
            console.error('Error al cargar rutina:', error);
        }
    };

    const handleAddExercise = async () => {
        if (!selectedExercise) return;
        
        const exercise = exercises.find(e => e.name === selectedExercise);
        if (!exercise) return;

        const updatedRoutine = {
            ...userRoutine,
            [selectedDay]: [
                ...(userRoutine?.[selectedDay] || []),
                {
                    id: Date.now().toString(),
                    name: exercise.name,
                    category: exercise.category,
                    series: [
                        {
                            id: 1,
                            weight: 0,
                            reps: 0
                        }
                    ]
                }
            ]
        };

        try {
            await createUserRoutine('user1', selectedDay, updatedRoutine[selectedDay]);
            setUserRoutine(updatedRoutine);
            setSelectedExercise('');
        } catch (error) {
            console.error('Error al agregar ejercicio:', error);
        }
    };

    const handleUpdateSeries = async (exerciseId, seriesId, field, value) => {
        if (!userRoutine?.[selectedDay]) return;

        const updatedExercises = userRoutine[selectedDay].map(ex => {
            if (ex.id === exerciseId) {
                const currentSeries = ex.series || [];
                const updatedSeries = currentSeries.map(s => {
                    if (s.id === seriesId) {
                        return { ...s, [field]: value };
                    }
                    return s;
                });
                return { ...ex, series: updatedSeries };
            }
            return ex;
        });

        const updatedRoutine = {
            ...userRoutine,
            [selectedDay]: updatedExercises
        };

        try {
            await createUserRoutine('user1', selectedDay, updatedExercises);
            setUserRoutine(updatedRoutine);
        } catch (error) {
            console.error('Error al actualizar serie:', error);
        }
    };

    const handleAddSeries = async (exerciseId) => {
        if (!userRoutine?.[selectedDay]) return;

        const updatedExercises = userRoutine[selectedDay].map(ex => {
            if (ex.id === exerciseId) {
                const currentSeries = ex.series || [];
                const newSeriesId = currentSeries.length + 1;
                return {
                    ...ex,
                    series: [
                        ...currentSeries,
                        {
                            id: newSeriesId,
                            weight: 0,
                            reps: 0
                        }
                    ]
                };
            }
            return ex;
        });

        const updatedRoutine = {
            ...userRoutine,
            [selectedDay]: updatedExercises
        };

        try {
            await createUserRoutine('user1', selectedDay, updatedExercises);
            setUserRoutine(updatedRoutine);
        } catch (error) {
            console.error('Error al agregar serie:', error);
        }
    };

    const handleRemoveSeries = async (exerciseId, seriesId) => {
        if (!userRoutine?.[selectedDay]) return;

        const updatedExercises = userRoutine[selectedDay].map(ex => {
            if (ex.id === exerciseId) {
                const currentSeries = ex.series || [];
                const updatedSeries = currentSeries
                    .filter(s => s.id !== seriesId)
                    .map((s, index) => ({ ...s, id: index + 1 }));
                return { ...ex, series: updatedSeries };
            }
            return ex;
        });

        const updatedRoutine = {
            ...userRoutine,
            [selectedDay]: updatedExercises
        };

        try {
            await createUserRoutine('user1', selectedDay, updatedExercises);
            setUserRoutine(updatedRoutine);
        } catch (error) {
            console.error('Error al eliminar serie:', error);
        }
    };

    const handleRemoveExercise = async (exerciseId) => {
        if (!userRoutine?.[selectedDay]) return;

        const updatedExercises = userRoutine[selectedDay].filter(ex => ex.id !== exerciseId);

        const updatedRoutine = {
            ...userRoutine,
            [selectedDay]: updatedExercises
        };

        try {
            await createUserRoutine('user1', selectedDay, updatedExercises);
            setUserRoutine(updatedRoutine);
        } catch (error) {
            console.error('Error al eliminar ejercicio:', error);
        }
    };

    // Agrupar ejercicios por categoría
    const exercisesByCategory = exercises.reduce((acc, exercise) => {
        if (!acc[exercise.category]) {
            acc[exercise.category] = [];
        }
        acc[exercise.category].push(exercise);
        return acc;
    }, {});

    return (
        <div className="p-4 text-white">
            <h2 className="text-2xl font-bold mb-4 text-yellow-400">Entrenamiento</h2>
            
            <div className="mb-4">
                <label className="block text-sm font-medium text-yellow-400 mb-2">
                    Día de la semana
                </label>
                <select
                    value={selectedDay}
                    onChange={(e) => setSelectedDay(e.target.value)}
                    className="w-full p-2 border rounded bg-gray-800 text-yellow-400 border-gray-700"
                >
                    {['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'].map(day => (
                        <option key={day} value={day}>{day}</option>
                    ))}
                </select>
            </div>

            <div className="mb-4">
                <label className="block text-sm font-medium text-yellow-400 mb-2">
                    Agregar ejercicio
                </label>
                <div className="flex gap-2">
                    <select
                        value={selectedExercise}
                        onChange={(e) => setSelectedExercise(e.target.value)}
                        className="flex-1 p-2 border rounded bg-gray-800 text-yellow-400 border-gray-700"
                    >
                        <option value="">Seleccionar ejercicio</option>
                        {Object.entries(exercisesByCategory).map(([category, exercises]) => (
                            <optgroup key={category} label={category.toUpperCase()}>
                                {exercises.map(exercise => (
                                    <option key={exercise.id} value={exercise.name}>
                                        {exercise.name}
                                    </option>
                                ))}
                            </optgroup>
                        ))}
                    </select>
                    <button
                        onClick={handleAddExercise}
                        className="bg-yellow-500 text-black px-4 py-2 rounded hover:bg-yellow-600"
                    >
                        Agregar
                    </button>
                </div>
            </div>

            <div className="space-y-6 overflow-y-auto max-h-[calc(100vh-300px)]">
                {userRoutine?.[selectedDay]?.map(exercise => (
                    <div key={exercise.id} className="border border-gray-700 p-4 rounded bg-gray-800">
                        <div className="flex justify-between items-center mb-4">
                            <h3 className="font-semibold text-yellow-400">{exercise.name}</h3>
                            <button
                                onClick={() => handleRemoveExercise(exercise.id)}
                                className="p-2 text-red-500 hover:text-red-600"
                                title="Eliminar ejercicio"
                            >
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                                </svg>
                            </button>
                        </div>
                        <div className="space-y-4">
                            {(exercise.series || []).map((serie) => (
                                <div key={serie.id} className="flex items-center gap-4 p-3 bg-gray-700 rounded">
                                    <div className="w-16 text-center">
                                        <span className="text-yellow-400 font-bold">Serie {serie.id}</span>
                                    </div>
                                    <div className="flex-1">
                                        <label className="block text-sm text-gray-400 mb-1">Peso</label>
                                        <input
                                            type="number"
                                            min="0"
                                            value={serie.weight}
                                            onChange={(e) => handleUpdateSeries(exercise.id, serie.id, 'weight', parseInt(e.target.value) || 0)}
                                            className="w-full p-2 border rounded bg-gray-800 text-white border-gray-600"
                                            placeholder="Ej: 20, 30, 50"
                                        />
                                    </div>
                                    <div className="flex-1">
                                        <label className="block text-sm text-gray-400 mb-1">Repeticiones</label>
                                        <input
                                            type="number"
                                            min="0"
                                            value={serie.reps}
                                            onChange={(e) => handleUpdateSeries(exercise.id, serie.id, 'reps', parseInt(e.target.value) || 0)}
                                            className="w-full p-2 border rounded bg-gray-800 text-white border-gray-600"
                                        />
                                    </div>
                                    {(exercise.series || []).length > 1 && (
                                        <button
                                            onClick={() => handleRemoveSeries(exercise.id, serie.id)}
                                            className="p-2 text-red-500 hover:text-red-600"
                                            title="Eliminar serie"
                                        >
                                            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                                <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                                            </svg>
                                        </button>
                                    )}
                                </div>
                            ))}
                            <button
                                onClick={() => handleAddSeries(exercise.id)}
                                className="w-full p-2 bg-gray-700 text-yellow-400 rounded hover:bg-gray-600 flex items-center justify-center gap-2"
                            >
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                                    <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd" />
                                </svg>
                                Agregar Serie
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

const NutritionPage = ({ user }) => (
    <div className="p-4 text-white animate-slideInUp">
        <h1 className="text-4xl font-bold mb-2">Nutrición</h1>
        <p className="text-gray-400 mb-6">El combustible para tu máquina.</p>

        <div className="bg-gray-800 p-4 rounded-lg">
            <h2 className="text-xl font-bold text-yellow-400 mb-4">Plan Sugerido para ti</h2>
            <p className="text-sm text-gray-500 mb-4">Adaptado para tu nivel <span className="font-bold">{user.levelName}</span> y objetivos.</p>
            <div className="space-y-3">
                <p><strong className="text-yellow-500">Desayuno:</strong> {NUTRITION_PLAN.desayuno}</p>
                <p><strong className="text-yellow-500">Almuerzo:</strong> {NUTRITION_PLAN.almuerzo}</p>
                <p><strong className="text-yellow-500">Merienda:</strong> {NUTRITION_PLAN.merienda}</p>
                <p><strong className="text-yellow-500">Cena:</strong> {NUTRITION_PLAN.cena}</p>
            </div>
        </div>
    </div>
);

const ShopPage = ({ user }) => {
    const formatPrice = (price) => {
        return new Intl.NumberFormat('es-AR', { style: 'currency', currency: 'ARS' }).format(price);
    };

    const getDiscountedPrice = (price) => {
        return price * (1 - user.discount);
    };
    
    const handleWhatsAppBuy = (product) => {
        const message = encodeURIComponent(`¡Hola! Estoy interesado en el producto "${product.name}" de la tienda SE...MENTAL.`);
        window.open(`https://wa.me/?text=${message}`, '_blank');
    }

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-2">Tienda</h1>
            <p className="text-yellow-400 mb-6">
                Tu nivel <span className="font-bold">{user.levelName}</span> te da un <span className="font-bold">{(user.discount * 100).toFixed(0)}% de descuento</span>.
            </p>

            <div className="grid grid-cols-2 gap-4">
                {PRODUCTS.map(product => {
                    const discountedPrice = getDiscountedPrice(product.price);
                    return (
                        <div key={product.id} className="bg-gray-800 rounded-lg overflow-hidden">
                            <img src={product.img} alt={product.name} className="w-full h-32 object-cover"/>
                            <div className="p-3">
                                <h3 className="font-bold">{product.name}</h3>
                                {user.discount > 0 && (
                                     <p className="text-sm text-gray-500 line-through">{formatPrice(product.price)}</p>
                                )}
                                <p className="text-lg font-bold text-yellow-400">{formatPrice(discountedPrice)}</p>
                                <button onClick={() => handleWhatsAppBuy(product)} className="w-full bg-green-500 text-white font-bold py-1 mt-2 text-sm rounded-lg hover:bg-green-400 transition-colors">
                                    Comprar
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

const ProfilePage = ({ user }) => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [debugInfo, setDebugInfo] = useState('');
    const [initStatus, setInitStatus] = useState({});

    const testDatabase = async () => {
        setLoading(true);
        setError(null);
        setDebugInfo('');
        try {
            console.log('Intentando conectar a la base de datos...');
            setDebugInfo(prev => prev + '\nIntentando conectar a la base de datos...');
            
            const usersList = await getUsers();
            console.log('Respuesta de getUsers:', usersList);
            setDebugInfo(prev => prev + `\nRespuesta de getUsers: ${JSON.stringify(usersList, null, 2)}`);
            
            setUsers(usersList);
            setDebugInfo(prev => prev + '\nConexión exitosa');
        } catch (error) {
            console.error('Error en testDatabase:', error);
            setError(`Error al conectar: ${error.message}`);
            setDebugInfo(prev => prev + `\nError: ${error.message}\nStack: ${error.stack}`);
        } finally {
            setLoading(false);
        }
    };

    const handleInitializeCategory = async (category) => {
        setLoading(true);
        setError(null);
        try {
            await initializeExercisesByCategory(category);
            setInitStatus(prev => ({ ...prev, [category]: 'success' }));
        } catch (error) {
            console.error(`Error al inicializar ${category}:`, error);
            setError(`Error al inicializar ${category}: ${error.message}`);
            setInitStatus(prev => ({ ...prev, [category]: 'error' }));
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="p-4 text-white animate-slideInUp">
            <h1 className="text-4xl font-bold mb-2">Perfil</h1>
            <p className="text-gray-400 mb-6">Tu progreso y estadísticas.</p>

            <div className="bg-gray-800 p-4 rounded-lg mb-4">
                <h2 className="text-xl font-bold text-yellow-400 mb-4">Nivel {user.level}</h2>
                <p className="text-sm text-gray-500 mb-2">XP: {user.xp}</p>
                <p className="text-sm text-gray-500 mb-2">Rango: {user.levelName}</p>
                <p className="text-sm text-gray-500">Descuento en tienda: {user.discount * 100}%</p>
            </div>

            <div className="bg-gray-800 p-4 rounded-lg mb-4">
                <h2 className="text-xl font-bold text-yellow-400 mb-4">Prueba de Base de Datos</h2>
                <button 
                    onClick={testDatabase}
                    disabled={loading}
                    className="w-full bg-yellow-400 text-black font-bold py-3 rounded-lg hover:bg-yellow-300 transition-colors disabled:opacity-50 mb-2"
                >
                    {loading ? 'Probando...' : 'Probar Conexión'}
                </button>
                <button 
                    onClick={() => handleInitializeCategory('Pecho')}
                    disabled={loading}
                    className={`w-full bg-blue-500 text-white font-bold py-3 rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50 mb-2`}
                >
                    {loading ? 'Inicializando...' : 'Inicializar Pecho'}
                </button>
                <button 
                    onClick={() => handleInitializeCategory('Espalda')}
                    disabled={loading}
                    className={`w-full bg-blue-500 text-white font-bold py-3 rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50`}
                >
                    {loading ? 'Inicializando...' : 'Inicializar Espalda'}
                </button>
                {error && (
                    <p className={`mt-4 text-sm text-red-400`}>
                        {error}
                    </p>
                )}
            </div>

            {users.length > 0 && (
                <div className="mt-4">
                    <h3 className="text-lg font-semibold mb-2">Usuarios encontrados:</h3>
                    <ul className="list-disc pl-5">
                        {users.map(user => (
                            <li key={user.id}>
                                {user.name} - Nivel {user.level} (XP: {user.xp})
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {debugInfo && (
                <div className="mt-4">
                    <h3 className="text-lg font-semibold mb-2">Información de Depuración:</h3>
                    <pre className="bg-gray-100 p-4 rounded overflow-auto max-h-60">
                        {debugInfo}
                    </pre>
                </div>
            )}
        </div>
    );
};

const App = () => {
    const [currentPage, setCurrentPage] = useState('home');
    const [user, setUser] = useState({
        level: 1,
        xp: 0,
        levelName: LEVELS[1].name,
        discount: LEVELS[1].discount
    });

    useEffect(() => {
        // Simular carga inicial
        setTimeout(() => {
            setCurrentPage('training');
        }, 2000);

        // Cargar datos del usuario desde Firebase
        const loadUserData = async () => {
            try {
                const users = await getUsers();
                if (users.length > 0) {
                    setUser(users[0]);
                } else {
                    // Crear usuario por defecto si no existe
                    const newUserId = await createUser({
                        level: 1,
                        xp: 0,
                        levelName: LEVELS[1].name,
                        discount: LEVELS[1].discount
                    });
                    const newUser = await getUser(newUserId);
                    setUser(newUser);
                }
            } catch (error) {
                console.error("Error al cargar datos del usuario:", error);
            }
        };

        loadUserData();
    }, []);

    const handleWorkoutComplete = (xpGained) => {
        const newXp = user.xp + xpGained;
        const newLevel = Math.floor(newXp / 100) + 1;
        const levelData = LEVELS[newLevel] || LEVELS[5];
        
        setUser(prev => ({
            ...prev,
            xp: newXp,
            level: newLevel,
            levelName: levelData.name,
            discount: levelData.discount
        }));
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'home':
                return <HomePage />;
            case 'training':
                return <TrainingPage />;
            case 'nutrition':
                return <NutritionPage user={user} />;
            case 'shop':
                return <ShopPage user={user} />;
            case 'profile':
                return <ProfilePage user={user} />;
            default:
                return <HomePage />;
        }
    };
    
    // Cargar la fuente de Google Fonts
    useEffect(() => {
        const link = document.createElement('link');
        link.href = "https://fonts.googleapis.com/css2?family=Cinzel:wght@700&family=Inter:wght@400;700&display=swap";
        link.rel = 'stylesheet';
        document.head.appendChild(link);
    }, []);

    if (currentPage === 'splash') {
        return <div className="h-screen w-screen bg-black"><SplashScreen /></div>
    }

    return (
        <div className="min-h-screen bg-black text-white">
            <div className="max-w-md mx-auto relative min-h-screen">
                {renderPage()}
                <nav className="absolute bottom-0 left-0 right-0 bg-gray-800/80 backdrop-blur-sm border-t border-gray-700">
                    <div className="flex justify-around items-center h-16">
                        <button onClick={() => setCurrentPage('training')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${currentPage === 'training' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <DumbbellIcon />
                            <span className="text-xs mt-1">Entrenar</span>
                        </button>
                        <button onClick={() => setCurrentPage('nutrition')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${currentPage === 'nutrition' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <NutritionIcon />
                            <span className="text-xs mt-1">Nutrición</span>
                        </button>
                        <button onClick={() => setCurrentPage('shop')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${currentPage === 'shop' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <ShopIcon />
                            <span className="text-xs mt-1">Tienda</span>
                        </button>
                        <button onClick={() => setCurrentPage('profile')} className={`flex flex-col items-center justify-center p-2 rounded-lg transition-colors ${currentPage === 'profile' ? 'text-yellow-400' : 'text-gray-400 hover:text-white'}`}>
                            <ProfileIcon />
                            <span className="text-xs mt-1">Perfil</span>
                        </button>
                    </div>
                </nav>
            </div>
            
            <style jsx global>{`
                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }
                @keyframes slideInUp {
                    from { transform: translateY(20px); opacity: 0; }
                    to { transform: translateY(0); opacity: 1; }
                }
                .animate-fadeIn { animation: fadeIn 1.5s ease-in-out; }
                .animate-slideInUp { animation: slideInUp 0.5s ease-out; }
            `}</style>
        </div>
    );
};

export default App;
