import { initializeApp } from 'firebase/app';
import { getAuth, connectAuthEmulator } from 'firebase/auth';
import { getFirestore, collection, doc, getDoc, setDoc, getDocs, connectFirestoreEmulator } from 'firebase/firestore';

const firebaseConfig = {
    apiKey: "AIzaSyBXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",
    authDomain: "sementalfit.firebaseapp.com",
    projectId: "sementalfit",
    storageBucket: "sementalfit.appspot.com",
    messagingSenderId: "XXXXXXXXXXXX",
    appId: "1:XXXXXXXXXXXX:web:XXXXXXXXXXXXXXXX"
};

// Inicializar Firebase
const app = initializeApp(firebaseConfig);

// Inicializar Auth
const auth = getAuth(app);
auth.useDeviceLanguage();

// Inicializar Firestore
const db = getFirestore(app);

// Conectar a los emuladores en desarrollo
if (process.env.NODE_ENV === 'development') {
    connectAuthEmulator(auth, 'http://localhost:9099');
    connectFirestoreEmulator(db, 'localhost', 8080);
}

export const getUser = async (userId) => {
    try {
        const userDoc = await getDoc(doc(db, 'users', userId));
        return userDoc.exists() ? userDoc.data() : null;
    } catch (error) {
        console.error('Error getting user:', error);
        return null;
    }
};

export const getUserRoutine = async (userId) => {
    try {
        const routineDoc = await getDoc(doc(db, 'routines', userId));
        return routineDoc.exists() ? routineDoc.data() : {};
    } catch (error) {
        console.error('Error getting user routine:', error);
        return {};
    }
};

export const saveUserRoutine = async (userId, routine) => {
    try {
        await setDoc(doc(db, 'routines', userId), routine);
        return true;
    } catch (error) {
        console.error('Error saving user routine:', error);
        return false;
    }
};

export const getAllExercises = async () => {
    try {
        const exercisesSnapshot = await getDocs(collection(db, 'exercises'));
        return exercisesSnapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data()
        }));
    } catch (error) {
        console.error('Error getting exercises:', error);
        return [];
    }
};

export { app, auth, db };
