import React, { createContext, useContext, useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { 
    onAuthStateChanged, 
    signInWithEmailAndPassword,
    createUserWithEmailAndPassword,
    signOut,
    updateProfile,
    signInWithPopup
} from 'firebase/auth';
import { 
    doc, 
    getDoc, 
    collection, 
    query, 
    where, 
    getDocs, 
    addDoc, 
    setDoc,
    serverTimestamp 
} from 'firebase/firestore';
import { googleProvider } from '../firebase';

const AuthContext = createContext();

export function useAuth() {
    return useContext(AuthContext);
}

export function AuthProvider({ children }) {
    const [currentUser, setCurrentUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Función para traducir errores de Firebase a mensajes amigables
    function getErrorMessage(error) {
        switch (error.code) {
            case 'auth/email-already-in-use':
                return 'Este correo electrónico ya está registrado. Por favor, inicia sesión o usa otro correo.';
            case 'auth/invalid-email':
                return 'El correo electrónico no es válido.';
            case 'auth/operation-not-allowed':
                return 'El registro con correo y contraseña no está habilitado.';
            case 'auth/weak-password':
                return 'La contraseña es demasiado débil. Debe tener al menos 6 caracteres.';
            case 'auth/user-disabled':
                return 'Esta cuenta ha sido deshabilitada.';
            case 'auth/user-not-found':
                return 'No existe una cuenta con este correo electrónico.';
            case 'auth/wrong-password':
                return 'Contraseña incorrecta.';
            case 'auth/too-many-requests':
                return 'Demasiados intentos fallidos. Por favor, intente más tarde.';
            case 'auth/popup-closed-by-user':
                return 'La ventana de inicio de sesión fue cerrada.';
            case 'auth/popup-blocked':
                return 'La ventana emergente fue bloqueada. Por favor, permita las ventanas emergentes.';
            case 'auth/cancelled-popup-request':
                return 'La solicitud de inicio de sesión fue cancelada.';
            case 'auth/account-exists-with-different-credential':
                return 'Ya existe una cuenta con este correo electrónico usando otro método de inicio de sesión.';
            default:
                return 'Ocurrió un error. Por favor, intente nuevamente.';
        }
    }

    async function login(email, password) {
        try {
            // Primero buscar el usuario en Firestore
            const usersRef = collection(db, 'users');
            const q = query(usersRef, where('email', '==', email));
            const querySnapshot = await getDocs(q);
            
            if (querySnapshot.empty) {
                throw new Error('Usuario no encontrado');
            }

            const userDoc = querySnapshot.docs[0];
            const userData = userDoc.data();

            // Verificar la contraseña
            if (userData.password !== password) {
                throw new Error('Contraseña incorrecta');
            }

            // Actualizar último inicio de sesión
            await setDoc(doc(db, 'users', userDoc.id), {
                lastLogin: serverTimestamp()
            }, { merge: true });

            // Establecer el usuario actual con todos sus datos
            const user = {
                uid: userDoc.id,
                ...userData
            };
            setCurrentUser(user);

            return user;
        } catch (error) {
            console.error('Error en login:', error);
            throw error;
        }
    }

    async function register(email, password, name, role = 1) {
        try {
            // Crear el usuario en Firestore
            const userRef = await addDoc(collection(db, 'users'), {
                email,
                password,
                name,
                role,
                createdAt: serverTimestamp(),
                lastLogin: serverTimestamp()
            });

            // Establecer el usuario actual
            const user = {
                uid: userRef.id,
                email,
                name,
                role
            };
            setCurrentUser(user);

            return userRef.id;
        } catch (error) {
            console.error('Error en registro:', error);
            throw error;
        }
    }

    async function loginWithGoogle() {
        try {
            setError(null);
            const result = await signInWithPopup(auth, googleProvider);
            const user = result.user;
            
            // Verificar si el usuario ya existe en Firestore
            const userDoc = await getDoc(doc(db, 'users', user.uid));
            
            if (!userDoc.exists()) {
                // Si es un nuevo usuario, crear su documento en Firestore
                await setDoc(doc(db, 'users', user.uid), {
                    email: user.email,
                    name: user.displayName,
                    role: 1, // Guardar como número
                    createdAt: serverTimestamp(),
                    lastLogin: serverTimestamp(),
                    status: 'active',
                    photoURL: user.photoURL
                });
            } else {
                // Si el usuario existe, actualizar último inicio de sesión
                await setDoc(doc(db, 'users', user.uid), {
                    lastLogin: serverTimestamp(),
                    photoURL: user.photoURL
                }, { merge: true });
            }

            return result;
        } catch (error) {
            console.error('Error en login con Google:', error);
            setError(getErrorMessage(error));
            throw error;
        }
    }

    function logout() {
        setCurrentUser(null);
    }

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, user => {
            setCurrentUser(user);
            setLoading(false);
        });

        return unsubscribe;
    }, []);

    const value = {
        currentUser,
        login,
        register,
        logout,
        loginWithGoogle,
        loading,
        error
    };

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
}
