const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Verificar si el usuario ya está autenticado
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                navigate('/training');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const userCredential = await signInWithEmailAndPassword(auth, email, password);
            if (userCredential.user) {
                navigate('/training');
            }
        } catch (error) {
            console.error('Error al iniciar sesión:', error);
            switch (error.code) {
                case 'auth/user-not-found':
                    setError('No existe una cuenta con este correo electrónico');
                    break;
                case 'auth/wrong-password':
                    setError('Contraseña incorrecta');
                    break;
                case 'auth/invalid-email':
                    setError('Correo electrónico inválido');
                    break;
                default:
                    setError('Error al iniciar sesión. Por favor, intenta de nuevo');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white flex items-center justify-center p-4">
            <div className="w-full max-w-md">
                <div className="text-center mb-8">
                    <h1 className="text-3xl font-bold text-yellow-500 mb-2">SementalFit</h1>
                    <p className="text-gray-400">Inicia sesión para continuar</p>
                </div>

                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="tu@email.com"
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="••••••••"
                        />
                    </div>

                    {error && (
                        <div className="text-red-500 text-sm text-center">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black py-2 px-4 rounded-lg hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400 transition-colors"
                    >
                        {loading ? (
                            <div className="flex items-center justify-center">
                                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-black"></div>
                            </div>
                        ) : (
                            'Iniciar sesión'
                        )}
                    </button>

                    <div className="text-center">
                        <p className="text-gray-400">
                            ¿No tienes una cuenta?{' '}
                            <button
                                type="button"
                                onClick={() => navigate('/register')}
                                className="text-yellow-500 hover:text-yellow-400"
                            >
                                Regístrate
                            </button>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

const RegisterPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Verificar si el usuario ya está autenticado
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                navigate('/training');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleRegister = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            // Registro de nuevo usuario
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const user = userCredential.user;
            
            // Crear perfil de usuario
            await createUser({
                id: user.uid,
                name: name,
                email: email,
                level: 'Principiante',
                experience: 0,
                goals: ['Ganar masa muscular', 'Mejorar la fuerza'],
                levelName: 'Novato',
                discount: 0.1,
                xp: 0
            });

            // Inicializar rutina vacía
            await createUserRoutine(user.uid, {});

            // Redirigir a la página de entrenamiento
            navigate('/training');
        } catch (error) {
            console.error('Error al registrar:', error);
            switch (error.code) {
                case 'auth/email-already-in-use':
                    setError('Este correo electrónico ya está registrado');
                    break;
                case 'auth/invalid-email':
                    setError('Correo electrónico inválido');
                    break;
                case 'auth/weak-password':
                    setError('La contraseña debe tener al menos 6 caracteres');
                    break;
                default:
                    setError('Error al registrar. Por favor, intenta de nuevo');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white flex items-center justify-center p-4">
            <div className="w-full max-w-md">
                <div className="text-center mb-8">
                    <h1 className="text-3xl font-bold text-yellow-500 mb-2">SementalFit</h1>
                    <p className="text-gray-400">Crea tu cuenta</p>
                </div>

                <form onSubmit={handleRegister} className="space-y-6">
                    <div>
                        <label htmlFor="name" className="block text-sm font-medium text-gray-300 mb-2">
                            Nombre
                        </label>
                        <input
                            type="text"
                            id="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="Tu nombre"
                        />
                    </div>

                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                            Correo electrónico
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="tu@email.com"
                        />
                    </div>

                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-full px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            placeholder="••••••••"
                        />
                    </div>

                    {error && (
                        <div className="text-red-500 text-sm text-center">
                            {error}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-yellow-500 text-black py-2 px-4 rounded-lg hover:bg-yellow-600 disabled:bg-gray-700 disabled:text-gray-400 transition-colors"
                    >
                        {loading ? (
                            <div className="flex items-center justify-center">
                                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-black"></div>
                            </div>
                        ) : (
                            'Registrarse'
                        )}
                    </button>

                    <div className="text-center">
                        <p className="text-gray-400">
                            ¿Ya tienes una cuenta?{' '}
                            <button
                                type="button"
                                onClick={() => navigate('/login')}
                                className="text-yellow-500 hover:text-yellow-400"
                            >
                                Inicia sesión
                            </button>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
};

const Navigation = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            setUser(user);
            setLoading(false);
            if (!user) {
                navigate('/login');
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogout = async () => {
        try {
            await signOut(auth);
            navigate('/login');
        } catch (error) {
            console.error('Error al cerrar sesión:', error);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-black text-white flex items-center justify-center">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-500"></div>
            </div>
        );
    }

    if (!user) {
        return <Navigate to="/login" />;
    }

    return (
        <div className="fixed bottom-0 left-0 right-0 bg-gray-900 border-t border-gray-800">
            <div className="max-w-4xl mx-auto px-4">
                <div className="flex justify-around py-2">
                    <NavLink to="/" className={({ isActive }) =>
                        `flex flex-col items-center ${isActive ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'}`
                    }>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
                        </svg>
                        <span className="text-xs mt-1">Inicio</span>
                    </NavLink>
                    <NavLink to="/training" className={({ isActive }) =>
                        `flex flex-col items-center ${isActive ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'}`
                    }>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                        </svg>
                        <span className="text-xs mt-1">Entrenar</span>
                    </NavLink>
                    <NavLink to="/nutrition" className={({ isActive }) =>
                        `flex flex-col items-center ${isActive ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'}`
                    }>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v13m0-13V6a2 2 0 112 2h-2zm0 0V5.5A2.5 2.5 0 109.5 8H12zm-7 4h14M5 12a2 2 0 110-4h14a2 2 0 110 4M5 12v7a2 2 0 002 2h10a2 2 0 002-2v-7" />
                        </svg>
                        <span className="text-xs mt-1">Nutrición</span>
                    </NavLink>
                    <NavLink to="/profile" className={({ isActive }) =>
                        `flex flex-col items-center ${isActive ? 'text-yellow-500' : 'text-gray-400 hover:text-yellow-500'}`
                    }>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                        </svg>
                        <span className="text-xs mt-1">Perfil</span>
                    </NavLink>
                    <button
                        onClick={handleLogout}
                        className="flex flex-col items-center text-gray-400 hover:text-red-500"
                    >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                        </svg>
                        <span className="text-xs mt-1">Salir</span>
                    </button>
                </div>
            </div>
        </div>
    );
};

const Layout = ({ children }) => {
    return (
        <div className="max-w-md mx-auto min-h-screen flex flex-col">
            <div className="flex-1">
                {children}
            </div>
            <Navigation />
        </div>
    );
};

function App() {
    return (
        <div className="min-h-screen bg-black text-white">
            <Routes>
                <Route path="/login" element={<LoginPage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/" element={<Navigate to="/login" />} />
                <Route path="/training" element={<Layout><TrainingPage /></Layout>} />
                <Route path="/nutrition" element={<Layout><NutritionPage /></Layout>} />
                <Route path="/profile" element={<Layout><ProfilePage /></Layout>} />
            </Routes>
        </div>
    );
}

export default App;
