import React, { useState, useEffect } from 'react';
import { saveUserRoutine, getUserProfile, updateUserProgress, updateUserStats } from '../services/firebaseUtils';

// Grupos musculares y sus ejercicios
const MUSCLE_GROUPS = {
    'Pecho': [
        'Press de Banca',
        'Press Inclinado',
        'Press Declinado',
        'Aperturas con Mancuernas',
        'Fondos en Paralelas',
        'Pullover'
    ],
    'Espalda': [
        'Dominadas',
        'Remo con Barra',
        'Remo con Mancuernas',
        'Pulldown',
        'Pullover',
        'Remo en Polea'
    ],
    'Hombros': [
        'Press Militar',
        'Elevaciones Laterales',
        'Elevaciones Frontales',
        'Elevaciones Posteriores',
        'Press Arnold',
        'Face Pull'
    ],
    'Bíceps': [
        'Curl con Barra',
        'Curl con Mancuernas',
        'Curl Martillo',
        'Curl Concentrado',
        'Curl en Polea',
        'Curl Invertido'
    ],
    'Tríceps': [
        'Extensiones en Polea',
        'Extensiones con Mancuernas',
        'Press Francés',
        'Fondos en Banco',
        'Extensiones Tras Nuca',
        'Kickbacks'
    ],
    'Piernas': [
        'Sentadillas',
        'Peso Muerto',
        'Extensiones de Cuádriceps',
        'Curl de Femoral',
        'Prensa',
        'Zancadas'
    ],
    'Abdominales': [
        'Crunches',
        'Plancha',
        'Elevaciones de Piernas',
        'Russian Twists',
        'Ab Wheel',
        'Leg Raises'
    ]
};

const DAYS_OF_WEEK = [
    'Lunes',
    'Martes',
    'Miércoles',
    'Jueves',
    'Viernes',
    'Sábado',
    'Domingo'
];

const TrainingPage = () => {
    const [error, setError] = useState('');
    const [userRoutine, setUserRoutine] = useState({});
    const [profile, setProfile] = useState(null);
    const [selectedDay, setSelectedDay] = useState(DAYS_OF_WEEK[0]);
    const [selectedMuscleGroup, setSelectedMuscleGroup] = useState('');
    const [selectedExercise, setSelectedExercise] = useState('');
    const [restTimer, setRestTimer] = useState(90); // 90 segundos por defecto
    const [timerActive, setTimerActive] = useState(false);
    const [timerTime, setTimerTime] = useState(0);
    const [timerStates, setTimerStates] = useState({});

    useEffect(() => {
        // Inicializar la rutina para cada día si no existe
        const initializeRoutine = () => {
            const newRoutine = {};
            DAYS_OF_WEEK.forEach(day => {
                if (!userRoutine[day]) {
                    newRoutine[day] = [];
                }
            });
            if (Object.keys(newRoutine).length > 0) {
                setUserRoutine(prev => ({ ...prev, ...newRoutine }));
            }
        };
        initializeRoutine();
    }, []);

    useEffect(() => {
        let intervals = {};
        Object.entries(timerStates).forEach(([key, state]) => {
            if (state.active && state.time > 0) {
                intervals[key] = setInterval(() => {
                    setTimerStates(prev => ({
                        ...prev,
                        [key]: {
                            ...prev[key],
                            time: prev[key].time - 1
                        }
                    }));
                }, 1000);
            } else if (state.time === 0) {
                setTimerStates(prev => ({
                    ...prev,
                    [key]: {
                        ...prev[key],
                        active: false
                    }
                }));
            }
        });

        return () => {
            Object.values(intervals).forEach(interval => clearInterval(interval));
        };
    }, [timerStates]);

    const handleAddExercise = () => {
        if (!selectedMuscleGroup || !selectedExercise) {
            setError('Por favor selecciona un grupo muscular y un ejercicio');
            return;
        }

        const newExercise = {
            id: Date.now(),
            name: selectedExercise,
            muscleGroup: selectedMuscleGroup,
            series: [
                { reps: 0, weight: 0, completed: false, restTime: 90 }
            ]
        };

        const newRoutine = { ...userRoutine };
        newRoutine[selectedDay] = [...(newRoutine[selectedDay] || []), newExercise];
        setUserRoutine(newRoutine);
        saveUserRoutine('default', newRoutine);

        setSelectedMuscleGroup('');
        setSelectedExercise('');
    };

    const addSeries = (exerciseId, day) => {
        const newRoutine = { ...userRoutine };
        const exerciseIndex = newRoutine[day].findIndex(ex => ex.id === exerciseId);
        
        if (exerciseIndex !== -1) {
            const exercise = newRoutine[day][exerciseIndex];
            const lastSeries = exercise.series[exercise.series.length - 1];
            
            newRoutine[day][exerciseIndex] = {
                ...exercise,
                series: [
                    ...exercise.series,
                    {
                        reps: lastSeries.reps,
                        weight: lastSeries.weight,
                        completed: false,
                        restTime: lastSeries.restTime
                    }
                ]
            };
            
            setUserRoutine(newRoutine);
            saveUserRoutine('default', newRoutine);
        }
    };

    const removeSeries = (exerciseId, day, seriesIndex) => {
        const newRoutine = { ...userRoutine };
        const exerciseIndex = newRoutine[day].findIndex(ex => ex.id === exerciseId);
        
        if (exerciseIndex !== -1 && newRoutine[day][exerciseIndex].series.length > 1) {
            newRoutine[day][exerciseIndex] = {
                ...newRoutine[day][exerciseIndex],
                series: newRoutine[day][exerciseIndex].series.filter((_, index) => index !== seriesIndex)
            };
            
            setUserRoutine(newRoutine);
            saveUserRoutine('default', newRoutine);
        }
    };

    const handleDeleteExercise = async (exerciseId, day) => {
        if (window.confirm('¿Estás seguro de que quieres eliminar este ejercicio?')) {
            try {
                const newRoutine = { ...userRoutine };
                newRoutine[day] = newRoutine[day].filter(ex => ex.id !== exerciseId);
                await saveUserRoutine('default', newRoutine);
                setUserRoutine(newRoutine);
            } catch (error) {
                console.error('Error al eliminar ejercicio:', error);
                setError('Error al eliminar ejercicio');
            }
        }
    };

    const updateExerciseInRoutine = async (exerciseId, updatedExercise, day) => {
        try {
            const newRoutine = { ...userRoutine };
            const exerciseIndex = newRoutine[day].findIndex(
                ex => ex.id === exerciseId
            );

            if (exerciseIndex !== -1) {
                const existingExercise = newRoutine[day][exerciseIndex];
                const updatedSeries = existingExercise.series.map((serie, index) => {
                    const updatedSerie = updatedExercise.series[index];
                    if (updatedSerie) {
                        return {
                            ...serie,
                            reps: updatedSerie.reps !== undefined ? Number(updatedSerie.reps) : serie.reps,
                            weight: updatedSerie.weight !== undefined ? Number(updatedSerie.weight) : serie.weight,
                            completed: updatedSerie.completed !== undefined ? updatedSerie.completed : serie.completed,
                            restTime: updatedSerie.restTime !== undefined ? Number(updatedSerie.restTime) : serie.restTime
                        };
                    }
                    return serie;
                });

                newRoutine[day][exerciseIndex] = {
                    ...existingExercise,
                    ...updatedExercise,
                    series: updatedSeries
                };

                await saveUserRoutine('default', newRoutine);
                setUserRoutine(newRoutine);

                const hasWeightChanges = updatedSeries.some((serie, index) => 
                    serie.weight !== existingExercise.series[index].weight ||
                    serie.reps !== existingExercise.series[index].reps
                );

                if (hasWeightChanges) {
                    await updateUserProgress('default', 'weight_update');
                    const userProfile = await getUserProfile('default');
                    setProfile(userProfile);

                    const totalWeight = updatedSeries.reduce((sum, serie) => 
                        sum + (Number(serie.weight) * Number(serie.reps)), 0);
                    
                    await updateUserStats('default', {
                        totalExercises: (userProfile?.stats?.totalExercises || 0) + 1,
                        totalWeightLifted: (userProfile?.stats?.totalWeightLifted || 0) + totalWeight
                    });
                }
            }
        } catch (error) {
            console.error('Error al actualizar ejercicio:', error);
            setError('Error al actualizar ejercicio en la rutina');
        }
    };

    const startTimer = (exerciseId, seriesIndex, restTime) => {
        const timerKey = `${exerciseId}-${seriesIndex}`;
        setTimerStates(prev => ({
            ...prev,
            [timerKey]: {
                time: restTime,
                active: true
            }
        }));
    };

    const formatTime = (seconds) => {
        const mins = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    return (
        <div className="min-h-screen bg-black text-white p-4">
            <div className="max-w-4xl mx-auto">
                <div className="flex justify-between items-center mb-4">
                    <h1 className="text-2xl font-bold text-yellow-500">SementalFit</h1>
                </div>

                {error && (
                    <div className="bg-red-900/20 text-red-500 p-3 rounded-lg mb-4 text-sm">
                        {error}
                    </div>
                )}

                {/* Selector de día */}
                <div className="bg-gray-900 rounded-xl p-4 mb-4 overflow-x-auto">
                    <h2 className="text-xl font-semibold mb-3">Día</h2>
                    <div className="flex space-x-2 min-w-max">
                        {DAYS_OF_WEEK.map(day => (
                            <button
                                key={day}
                                onClick={() => setSelectedDay(day)}
                                className={`px-3 py-2 rounded-lg transition-colors ${
                                    selectedDay === day
                                        ? 'bg-yellow-500 text-black'
                                        : 'bg-gray-800 hover:bg-gray-700'
                                }`}
                            >
                                {day.slice(0, 3)}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Agregar ejercicio */}
                <div className="bg-gray-900 rounded-xl p-4 mb-4">
                    <h2 className="text-xl font-semibold mb-3">Agregar Ejercicio</h2>
                    <div className="space-y-3">
                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-1">
                                Grupo Muscular
                            </label>
                            <select
                                value={selectedMuscleGroup}
                                onChange={(e) => {
                                    setSelectedMuscleGroup(e.target.value);
                                    setSelectedExercise('');
                                }}
                                className="w-full px-3 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                            >
                                <option value="">Selecciona un grupo</option>
                                {Object.keys(MUSCLE_GROUPS).map(group => (
                                    <option key={group} value={group}>{group}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-1">
                                Ejercicio
                            </label>
                            <select
                                value={selectedExercise}
                                onChange={(e) => setSelectedExercise(e.target.value)}
                                className="w-full px-3 py-2 bg-gray-800 border border-gray-700 rounded-lg focus:outline-none focus:border-yellow-500"
                                disabled={!selectedMuscleGroup}
                            >
                                <option value="">Selecciona un ejercicio</option>
                                {selectedMuscleGroup && MUSCLE_GROUPS[selectedMuscleGroup].map(exercise => (
                                    <option key={exercise} value={exercise}>{exercise}</option>
                                ))}
                            </select>
                        </div>
                        <button
                            onClick={handleAddExercise}
                            className="w-full bg-yellow-500 text-black py-2 px-4 rounded-lg hover:bg-yellow-600 transition-colors font-semibold"
                        >
                            Agregar Ejercicio
                        </button>
                    </div>
                </div>

                {/* Lista de ejercicios */}
                <div className="bg-gray-900 rounded-xl p-4">
                    <h3 className="text-xl font-semibold mb-3">Rutina de {selectedDay}</h3>
                    {userRoutine[selectedDay]?.length > 0 ? (
                        <div className="space-y-4">
                            {userRoutine[selectedDay].map((exercise) => (
                                <div key={exercise.id} className="bg-gray-800 p-3 rounded-lg">
                                    <div className="flex justify-between items-start mb-3">
                                        <div>
                                            <h5 className="text-lg font-medium">{exercise.name}</h5>
                                            <p className="text-sm text-gray-400">{exercise.muscleGroup}</p>
                                        </div>
                                        <button
                                            onClick={() => addSeries(exercise.id, selectedDay)}
                                            className="px-3 py-1 bg-green-500 text-black rounded hover:bg-green-600 text-sm"
                                        >
                                            + Serie
                                        </button>
                                    </div>
                                    <div className="space-y-3">
                                        {exercise.series.map((serie, index) => (
                                            <div key={index} className="bg-gray-700 p-2 rounded-lg">
                                                <div className="flex items-center justify-between mb-2">
                                                    <span className="text-gray-300">Serie {index + 1}</span>
                                                    {exercise.series.length > 1 && (
                                                        <button
                                                            onClick={() => removeSeries(exercise.id, selectedDay, index)}
                                                            className="text-red-500 hover:text-red-400"
                                                        >
                                                            ×
                                                        </button>
                                                    )}
                                                </div>
                                                <div className="grid grid-cols-2 gap-2">
                                                    <div>
                                                        <label className="block text-xs text-gray-400 mb-1">Reps</label>
                                                        <input
                                                            type="number"
                                                            value={serie.reps}
                                                            onChange={(e) => {
                                                                const updatedExercise = {
                                                                    ...exercise,
                                                                    series: exercise.series.map((s, i) =>
                                                                        i === index ? { ...s, reps: e.target.value } : s
                                                                    )
                                                                };
                                                                updateExerciseInRoutine(exercise.id, updatedExercise, selectedDay);
                                                            }}
                                                            className="w-full bg-gray-800 rounded px-2 py-1 text-center"
                                                            min="0"
                                                        />
                                                    </div>
                                                    <div>
                                                        <label className="block text-xs text-gray-400 mb-1">Kg</label>
                                                        <input
                                                            type="number"
                                                            value={serie.weight}
                                                            onChange={(e) => {
                                                                const updatedExercise = {
                                                                    ...exercise,
                                                                    series: exercise.series.map((s, i) =>
                                                                        i === index ? { ...s, weight: e.target.value } : s
                                                                    )
                                                                };
                                                                updateExerciseInRoutine(exercise.id, updatedExercise, selectedDay);
                                                            }}
                                                            className="w-full bg-gray-800 rounded px-2 py-1 text-center"
                                                            min="0"
                                                        />
                                                    </div>
                                                </div>
                                                <div className="mt-2 flex items-center justify-between">
                                                    <input
                                                        type="checkbox"
                                                        checked={serie.completed}
                                                        onChange={(e) => {
                                                            const updatedExercise = {
                                                                ...exercise,
                                                                series: exercise.series.map((s, i) =>
                                                                    i === index ? { ...s, completed: e.target.checked } : s
                                                                )
                                                            };
                                                            updateExerciseInRoutine(exercise.id, updatedExercise, selectedDay);
                                                        }}
                                                        className="w-4 h-4"
                                                    />
                                                    <button
                                                        onClick={() => startTimer(exercise.id, index, serie.restTime)}
                                                        className="px-3 py-1 bg-yellow-500 text-black rounded hover:bg-yellow-600 text-sm"
                                                    >
                                                        {timerStates[`${exercise.id}-${index}`]?.active 
                                                            ? formatTime(timerStates[`${exercise.id}-${index}`].time)
                                                            : 'Iniciar'}
                                                    </button>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p className="text-gray-400 text-center py-4">
                            No tienes ejercicios en tu rutina. Agrega algunos ejercicios para comenzar.
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default TrainingPage;
